INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010801, /*original_event_id*/10108, /*title*/"我慢くらべはほどほどに　火の実の湯", /*condition_time*/"2022/12/31 12:00:00", /*condition_quest_id*/10108107, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/106701, /*guest_unit_id*/106601, /*day_num*/1, /*material_text*/"エルピス島原産　火の実、オオトウガラシ", /*effect_text*/"冷え性改善、代謝促進、保温効果\n※長時間の入浴にはご注意ください", /*article_text*/"エルピス島のドラゴンもどきから分けて\nもらった火の実をふんだんにつかった\n情熱的な温泉。ひとたび浸かれば汗が\n滝のように噴き出してくる。その熱さは\n己の限界を極めようとする漢・漢女たちの\n勝負心をがっちりと掴んで離さない。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010802, /*original_event_id*/10108, /*title*/"心華やぐリラクゼーション　アロマの湯", /*condition_time*/"2023/01/01", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/101501, /*guest_unit_id*/101601, /*day_num*/2, /*material_text*/"エルフの森産　かぐわし花のエッセンス", /*effect_text*/"美肌効果、リラックス効果、保香効果\n※甘酸っぱい気持ちになることがあります", /*article_text*/"香り高い花とその花のアロマエッセンスを\n使用した目にも鮮やかな温泉。時間を\n忘れるほどのリラクゼーション効果は\n忙しい都会人のニーズにピッタリ。高い\n美容効果もあいまって、若い女性たちの\n間で大注目のスポットになりつつある。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010803, /*original_event_id*/10108, /*title*/"童心くすぐる微炭酸　メロンソーダの湯", /*condition_time*/"2023/01/02", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/105101, /*guest_unit_id*/103601, /*day_num*/3, /*material_text*/"仲居特製の炭酸泉、メロンエッセンス", /*effect_text*/"血行促進、ピーリング効果\n童心に帰る効果", /*article_text*/"優しい微炭酸と甘いメロンの香りに満ちた\n遊び心をくすぐる温泉。子どもたちが喜ぶ\nような温泉を、というミツキの想いが形に\nなったもの。いつもは大人びている優等生\nさんも、この湯舟に浸かればつい年相応に\nはしゃいでしまうのだとか。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010804, /*original_event_id*/10108, /*title*/"ときめき電撃かけながし☆　ビリビリの湯", /*condition_time*/"2023/01/03", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/106701, /*guest_unit_id*/104501, /*day_num*/4, /*material_text*/"若女将の魔法（電撃）", /*effect_text*/"腰痛・肩こりの解消、血行促進\nビリビリエクスタシートリップ（？）", /*article_text*/"当旅館自慢の源泉に若女将の電撃を加えた\n刺激的な温泉。体中のがんこなコリに効果\nてき面と大好評。今日もデスクワーカー\nから老人たちまで、コリをほぐしに足を\n運んでいる。ちょっぴり痛みを感じるのも\nクセになる理由の一つなのだとか。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010805, /*original_event_id*/10108, /*title*/"明日への活力をあなたに　疲労回復の湯", /*condition_time*/"2023/01/04", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/101501, /*guest_unit_id*/104801, /*day_num*/5, /*material_text*/"エルフの森産　ヒールリーフの根っこ", /*effect_text*/"超・肉体疲労回復、超・神経疲労回復\n超・精神疲労回復", /*article_text*/"あらゆる疲労を回復すると名高い温泉。\nエルフの森自慢の薬草類を調合し、その\n効果を高めた薬湯は、疲れ切った人々の\nいこいの場。世の働き者たちは、この湯\nに浸かっては働き、また浸かっては働く\nのを繰り返して精を出すのだとか。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010806, /*original_event_id*/10108, /*title*/"荒業デトックス！　ヒリヒリの湯", /*condition_time*/"2023/01/05", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/105101, /*guest_unit_id*/118001, /*day_num*/6, /*material_text*/"仲居特製のカンポー、希少なトウガラシ", /*effect_text*/"脂肪燃焼効果、高デトックス効果\n※長時間の入浴には十分にご注意ください", /*article_text*/"あまりの辛さで一般市場では流通できない\nとまで言われるトウガラシをぜいたくに\n使用した高デトックス効果のある温泉。\n湯気も刺激的なため、希望者には目を守る\nゴーグルも貸し出している（お気軽に\nフロントまでお申しつけください）");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010807, /*original_event_id*/10108, /*title*/"高粘度の新感覚温泉　ゼラチナの湯", /*condition_time*/"2023/01/06", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/106701, /*guest_unit_id*/106001, /*day_num*/7, /*material_text*/"若女将特製のポーション、元気なゼラチナ", /*effect_text*/"魔力回復、美肌効果、保湿効果\n※お湯に甘噛みされることがあります", /*article_text*/"魔力を回復するポーションとゼラチナを\nかけ合わせた異色の温泉。ゼラチナたちは\n若女将がスカウトしてきたアルバイトとの\nこと。その不思議な湯ざわりと確かな魔力\n回復効果は、新しもの好きの魔法使いや\n魔族たちの間で話題になっているとか。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010808, /*original_event_id*/10108, /*title*/"心と体に爽やかな癒しを♪　スースーの湯", /*condition_time*/"2023/01/07", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/101501, /*guest_unit_id*/102101, /*day_num*/8, /*material_text*/"エルフの森産　スースー草のシードオイル", /*effect_text*/"清涼効果、のど・鼻の保湿効果\n※鼻歌が旅館内に届く可能性があります", /*article_text*/"清涼効果のある薬草類を調合した薬湯。\n湯気を吸うことでのどや鼻が保湿され、\n声の通りが格段によくなると好評。\n湯上がり後のスッキリ感も相まって\nリピーターが多い。歌手やアイドルたちも\nお忍びでこっそり訪れているとか。");
INSERT INTO `dsb_story_data` VALUES (/*sub_story_id*/1010809, /*original_event_id*/10108, /*title*/"ああバラ色の人生　美肌の湯", /*condition_time*/"2023/01/08", /*condition_quest_id*/0, /*reward_type*/8, /*reward_id*/91002, /*reward_count*/40, /*owner_unit_id*/105101, /*guest_unit_id*/100801, /*day_num*/9, /*material_text*/"仲居特製の超美肌薬、大輪のバラ", /*effect_text*/"美肌効果、保湿効果、保温効果\nバラの香りによるリラックス効果", /*article_text*/"とにもかくにも肌を美しくする効果を追求\nした珠玉の温泉。お湯を軽く浴びただけで\n違いが分かるとウワサになり、このお湯を\n利用した化粧水の発売も模索されている\nとか。ちなみにバラはあくまで香りづけの\nものであり、美肌効果はないとのこと。");
