INSERT INTO `equipment_data` VALUES (/*equipment_id*/101011, /*equipment_name*/"アイアンブレード", /*description*/"どこでも手に入る安価な剣。\n剣を学ぶ者の多くが\n初めて手にする大事な一振り。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/15.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101071, /*equipment_name*/"キラーペンシル", /*description*/"ペンの形をした短剣。\n攻撃力は低いが、\n携帯性に優れている。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101101, /*equipment_name*/"ウッドグリップ", /*description*/"拳を強化する木製の武器。\n簡素な作りではあるが、素手で\n攻撃するよりも、拳にかかる\n負担を軽くすることができる。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101131, /*equipment_name*/"狩人の弓", /*description*/"スタンダードな木製の弓。\n主に狩人が愛用しており、\n射程、威力共に、\n動物を狩るには申し分ない。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101161, /*equipment_name*/"鋼の槍", /*description*/"兵士がよく使用する槍。\nリーチが長く、扱いやすいため、\n戦での採用率が高い。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101191, /*equipment_name*/"片手斧", /*description*/"小型の片手斧。\n本来は投げ斧として作られたが、\n取り回しやすさ故に、\n近接武器としても使われている。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101251, /*equipment_name*/"香木の杖", /*description*/"ミステリオン森林に群生している\nアストライアヒノキという\n香木から削り出した杖。\n香りが怪我人の気持ちを和らげる。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101281, /*equipment_name*/"革のサロペット", /*description*/"もともとは職人のエプロン。\n大きなポケットがその名残。\n冒険初心者は持ち物が多いので\nこのポケットはとても重宝する。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/50.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101371, /*equipment_name*/"旅立ちのローブ", /*description*/"冒険者が最初に選ぶ一着。\n初めの一歩を踏み出すことへの\n祝福を込めて\nこの名で売られるようになった。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/55.0, /*atk*/0.0, /*magic_str*/1.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101401, /*equipment_name*/"木の盾", /*description*/"アストライアンシダーという\n針葉樹で作られた盾。\n加工しやすく安価なので\n学生の模擬戦によく用いられる。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101431, /*equipment_name*/"革のブーツ", /*description*/"冒険者以外にも愛される定番の品。\n軽くて歩きやすく\n長く履くほどに足に馴染む。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101521, /*equipment_name*/"おしゃれなベレー", /*description*/"もとは絵描きが愛用していた帽子。\n日よけや風よけに手頃なため\n駆け出しの冒険者にも人気の品。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101551, /*equipment_name*/"やる気のブレスレット", /*description*/"装備すると、どこからかやる気が\n漲ってくる不思議なブレスレット。\n攻撃力を上げる効果がある。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101581, /*equipment_name*/"御守りペンダント", /*description*/"四つ葉のクローバーを\nあしらった御守り。\n幸運を呼び込み、持つ者の\n身を守ってくれると言われている。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/50.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101611, /*equipment_name*/"気合のブレスレット", /*description*/"魔力を帯びたブレスレット。\nつけると気合いが漲り、\n装備者の魔法攻撃力を\n上げる効果がある。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102011, /*equipment_name*/"ギアブレード", /*description*/"刃と柄を歯車が繋ぐ珍しい剣。\n時計屋と鍛冶屋が\nコラボして開発した武器。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/30.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102013, /*equipment_name*/"ブルーブラッド", /*description*/"とある貴族に献上された名剣を\n模して作られた一品。\n見た目の美しさもさることながら、\n切れ味も良く、実用性も高い。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/60.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102014, /*equipment_name*/"オーロラの剣", /*description*/"七色に光る美しい剣。\n刀身は振るう度に発光し、\nその軌道は、まるで空中に\n光が舞うように見える。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102041, /*equipment_name*/"クレセントソード", /*description*/"美しい曲線を描く刀。\n勇猛で知られた将軍をして\n血に濡らすのは惜しいと言わしめた\n美術品としても一級の一振り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/35.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102071, /*equipment_name*/"コマンドナイフ", /*description*/"戦いより、森や山で遭難した時に\n真価を発揮するナイフ。\n生き残るための様々なギミックが\n詰め込まれている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102101, /*equipment_name*/"レザーナックル", /*description*/"革に鉄を埋め込んだ、\n拳を強化するための武器。\n己の体一つで戦おうとする\n武芸者の為の装備。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102131, /*equipment_name*/"クリスタルボウ", /*description*/"クリスタルマウンテンで採れる\n素材を使って作られた弓。\n水晶が魔法の触媒にもなるため、\n魔力を帯びた矢を放つことが可能。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102162, /*equipment_name*/"トライデント", /*description*/"大陸を沈めるほどの力を持つ、\n海神ポセイドンの三叉鉾に\nあやかった槍。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/12.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102163, /*equipment_name*/"フレイムスピア", /*description*/"火の精霊の力を注いだ槍。\n揺らめく炎のような穂先は\n魔物を倒せば倒すほど\nますます激しく燃え盛っていく。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102191, /*equipment_name*/"メタルアックス", /*description*/"鋼の戦斧。\n木こり用の物と違い、\n武器として作られたそれは、\n確かな殺傷能力を持っている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/16.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102192, /*equipment_name*/"ハイメタルアックス", /*description*/"高度な鋳造技術を用いて\n作り出された戦斧。\nその重量を活かした攻撃で、\n敵を殲滅する。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102221, /*equipment_name*/"グリーンロッド", /*description*/"自然と密接な繋がりを持つ\nエルフが作り上げた杖。\n武器としてだけではなく、\n豊穣への祈りにも使われる。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/0.0, /*magic_str*/16.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102222, /*equipment_name*/"リトルドラゴンロッド", /*description*/"宝玉を抱くドラゴンの\n姿をかたどった杖。\nわずかではあるが竜の力が\n込められている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/34.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102251, /*equipment_name*/"医者いらずの杖", /*description*/"回復魔法との相性が良い杖。\nヒーラーの魔法使いが\n好んで使用する。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102252, /*equipment_name*/"ユニコーンの慈愛", /*description*/"貴重なユニコーンの角を使った杖。\n角には傷を癒す力があり、\n回復魔法の効果が上がる。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102281, /*equipment_name*/"智慧の衣", /*description*/"かつて、僧侶が信仰を広めるため\n大陸各地を旅する時に着た衣。\n動きやすく丈夫なため、\n多くの魔法使いが愛用している。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102282, /*equipment_name*/"ライトプレート", /*description*/"薄板で作られた鎧。\n確かな防御力と、軽さ故の\n動きやすさが特徴。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/320, /*require_level*/14, /*hp*/150.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102311, /*equipment_name*/"スケイルメイル", /*description*/"金属や革を鱗状に貼り付けた鎧。\n普通の鎧よりも動きやすく、\n斬撃や刺突に強い反面、\n鈍器などの打撃には弱い。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/95.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102312, /*equipment_name*/"ハイメタルプレート", /*description*/"高度な鋳造技術を用いて\n作り出された強固な鎧。\n並みの攻撃では、装備者に傷一つ\n負わせることはできない。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102341, /*equipment_name*/"フォークロアクロース", /*description*/"アストライアの伝統的な模様が\n織り込まれた服。\n購入には相応の身分や家格が必要。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102342, /*equipment_name*/"忍び装束", /*description*/"闇に紛れて行動するのに\nこれほど優れた衣装はない。\nどこかの国では、プロのスパイが\n愛用していたといわれている。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/110.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102371, /*equipment_name*/"春色ローブ", /*description*/"春を連想させる可愛らしいローブ。\n丈夫な作りで旅路にも向いており、\nそのデザインと実用性から\n愛用者は多い。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/65.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102372, /*equipment_name*/"ノルディックローブ", /*description*/"寒い北の国で愛用されていた。\n羊の毛から紡がれた糸は暖かく\nそれで織ったケープは肩や腕を\n厳しい風土から守ってくれる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102401, /*equipment_name*/"タワーシールド", /*description*/"身の丈ほどの金属の大盾。\n圧倒的な防御力を持つ反面、\nその重量故に、移動が困難となる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/15, /*hp*/135.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102431, /*equipment_name*/"クラウンブーツ", /*description*/"道化師をイメージして作られた\nおしゃれなブーツ。\n履くとどこか陽気な気分になる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/30.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102432, /*equipment_name*/"ハンティングブーツ", /*description*/"高山に生息する魔物の骨を使って\n作り上げられたブーツ。\nワイルドなデザインが、\n野生の心を呼び覚ます！", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/15, /*hp*/60.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102461, /*equipment_name*/"ウィッチブーツ", /*description*/"魔法薬の素材採取のために、\n各地を旅する魔法使いが履く靴。\n過酷な環境にも耐えられるように、\nとても丈夫に作られている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/9, /*hp*/30.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102491, /*equipment_name*/"かわいいキャスケット", /*description*/"ゴーグルがついたキャスケット。\n風船のようなシルエットが可愛いと\nランドソルのローティーン\n注目のアイテム。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102493, /*equipment_name*/"レギオンヘルム", /*description*/"古代の戦士たちが使用していた兜。\nとても頑丈に作られており、\n現代でも十分に使うことができる。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102521, /*equipment_name*/"修道女のフード", /*description*/"修道女が使用するフード。\n彼女たちは、傷ついた人々の\n身も心も癒したという。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/16, /*hp*/0.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102551, /*equipment_name*/"勇気の宝珠", /*description*/"戦いに必要な『仁・智・勇』の\n勇を与えてくれる珠。\nどんな敵にもひるまず向かっていく\n勇気の御守り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/30.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102552, /*equipment_name*/"スパイクバングル", /*description*/"魔物の持つ棘状の鱗がついた腕輪。\n装備者の力を上げる効果がある。\n凶暴な魔物の鱗で作られているため\nあまり市場には出回らない装備。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102581, /*equipment_name*/"仁愛の宝珠", /*description*/"戦いに必要な『仁・智・勇』の\n仁を与えてくれる珠。\n自分や仲間を守りたいと願う\n愛の心の御守り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/55.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102582, /*equipment_name*/"勝利のミサンガ", /*description*/"騎士が無事に帰還できるようにと、\n祈りを込めて作られたミサンガ。\nその思いが装備者を守り、\n防御力を上げてくれる。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/17, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102611, /*equipment_name*/"マジックグラス", /*description*/"装備者の魔法攻撃力を\n上げてくれる特殊なメガネ。\n魔法石が素材として\n使われている。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/11.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102612, /*equipment_name*/"智慧の宝珠", /*description*/"戦いに必要な『仁・智・勇』の\n智を与えてくれる珠。\n頭が冴え渡って魔法の効果が上がる\n知力の御守り。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102613, /*equipment_name*/"マジックモノクル", /*description*/"高貴な魔法使いが使用する片眼鏡。\n金属部分にはルーン文字が\n刻まれており、\n装備者の魔力を上げる力がある。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/17, /*hp*/0.0, /*atk*/0.0, /*magic_str*/24.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102614, /*equipment_name*/"文様の腕輪", /*description*/"銀の腕輪に、魔法石をあしらい、\n特殊な刻印を掘ったアクセサリー。\n魔法の流れをより活性化させ、\n装備者の魔力を上げる効果がある。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/16, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103011, /*equipment_name*/"煌めきの剣", /*description*/"隕石の欠片を使用した片刃の曲刀。\n刀身は光を浴びることで\n星屑のように煌めく。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/90.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103012, /*equipment_name*/"竜紋剣", /*description*/"竜の息吹が描かれた剣。\n幅広の刃は、突くことよりも\n斬ることに優れており、振るう姿は\n天を舞う竜のように見えるだろう。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/115.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103013, /*equipment_name*/"フェザーブレード", /*description*/"ある魔法使いが近接戦のために\n使用した剣。非力だった彼は、\n大きな鳥の羽に魔法をかけて、\nこの極めて軽量な剣を作り出した。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/37, /*hp*/135.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103041, /*equipment_name*/"月光刀", /*description*/"東国に存在するある剣術流派の\n免許皆伝者に与えられる刀剣。\n月光の力が宿っており、装備者の\n五感と霊感を研ぎ澄ませる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/3000, /*require_level*/30, /*hp*/65.0, /*atk*/58.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103042, /*equipment_name*/"雷神刀", /*description*/"ある凄腕の侍が、雷獣を退治\nしたとされている刀。\n雷獣を斬った刀身には、\n稲妻の力が宿っている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/40, /*hp*/75.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103071, /*equipment_name*/"プラチナナイフ", /*description*/"とても高価なプラチナを\n使用して作られた短剣。\n護身用に懐に忍ばせる貴族も多い。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103072, /*equipment_name*/"ヴァンパイアダガー", /*description*/"意思を持ち、血を求め続ける\n恐ろしい短剣。\n血を浴びると、錆びるどころか、\nより一層妖しく輝く。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103101, /*equipment_name*/"ドラゴンズクロー", /*description*/"凶悪な黒竜の顎をかたどった鉤爪。\n暗器の一種で、かつてこの鉤爪を\n使用していた暗殺者は、爪に毒を\n塗り、殺傷能力を高めたという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103131, /*equipment_name*/"キューピッドの弓", /*description*/"かつてこの弓で仕留めた獲物を\n想い人に贈り恋を叶えた男がいた。\n彼はその出来事にあやかり、\n愛の神の名をこの弓につけた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103132, /*equipment_name*/"デビルズボウ", /*description*/"人から迫害され、地下深くに\n追いやられた魔族の制作した弓。\n彼らは自分たちの領域に立ち入る\n人間を、この弓で葬ってきた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103161, /*equipment_name*/"騎士の誇り", /*description*/"名誉ある騎士に贈られる斧槍。\nその刃は、折れることなく、\n国と民の為に戦う騎士の誇りを\n体現している。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/18, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103162, /*equipment_name*/"アブソリュートスピア", /*description*/"一突一殺の槍。\nその殺傷能力は、\n強固な鎧をいとも簡単に貫き、\n一突きで敵の息の根を止める。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/46.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103191, /*equipment_name*/"ブルースフィアアックス", /*description*/"青き天体をあしらった戦斧。\n世界そのものを象徴する天体は、\n大地のエネルギーを吸収し、\n装備者に力を与える。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103192, /*equipment_name*/"麗花の戦斧", /*description*/"華麗な花を施された\nその斧が振るわれるたびに、\n新たな鮮血の大輪が咲く。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103193, /*equipment_name*/"稲妻の斧", /*description*/"高度な武器の製造を研究する、\nある工房で制作された一挺。\n斧の内部に雷を貯蓄し、\n攻撃に際して放出することが可能。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/66, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/77.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103221, /*equipment_name*/"ソーンブラッドロッド", /*description*/"ある残忍な魔法使いの一団が\n使用していた杖。\nこの杖は、彼らの犠牲となった\n者たちの血で紅く染め上げられた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103222, /*equipment_name*/"デビルズホーン", /*description*/"ある魔法使いが、悪魔との取引で\n手に入れたとされる曰く付きの品。\n倒した敵の魂は、この杖を通して\n悪魔に献上されるという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103251, /*equipment_name*/"天球の見守り", /*description*/"占星術を生業とする、ある魔法使い\n一族が使用していた杖。\n彼らは星を観測し、\n未来を予知したという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/0.0, /*magic_str*/40.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103252, /*equipment_name*/"花蕾の杖", /*description*/"人を救いたいという想いが\nこの蕾を花開かせ、\nその清浄なエネルギーは、\n治癒の力となる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/0.0, /*atk*/0.0, /*magic_str*/53.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103281, /*equipment_name*/"ファッションアーマー", /*description*/"強くてオシャレな騎士をテーマに\n作られた鎧。ただし、どんなに\nオシャレをしても、実力が伴って\nいなければ魅力は半減してしまう。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103282, /*equipment_name*/"バトリングドレス", /*description*/"屈強な男たちを次々と倒し、\n闘技場の女王となった女戦士の鎧。\n対戦相手は彼女の獣のような眼光を\n見ただけで戦意を喪失したという。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/42, /*sale_price*/3000, /*require_level*/32, /*hp*/325.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103283, /*equipment_name*/"ヘビーメタルアーマー", /*description*/"厚めの鉄板を組み合わせた鎧。\n軽快さを捨てて、防御力に\n特化させており、斧や大剣を使う\n戦士たちが好んで使用している。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/380.0, /*atk*/17.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103311, /*equipment_name*/"ゴージャスアーマー", /*description*/"派手好きの騎士が、デザイナーに\n頼んで作らせた鎧。\n派手さ故に戦場で目立った彼は、\n敵にとっては恰好の的となった。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103341, /*equipment_name*/"ミルフィーユクロース", /*description*/"布地の上に革のローブを重ねた服。\n防寒対策に優れ、寒冷地での\n素材採取などに適している。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/180.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/160.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103342, /*equipment_name*/"パレスクロース", /*description*/"王城内に務める女性騎士の服。\n上質な革と、黒金を使用し、\n胸にはナイトの地位を示す装飾。\n気品ある意匠に仕上がっている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/210.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103343, /*equipment_name*/"コスモスクロース", /*description*/"エルフの女王が、他種族との\n和平協定会議の際に着ていた服。\nエルフ族は万が一に備え、女王の\n服に強力な魔法のベールを張った。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/75, /*sale_price*/4000, /*require_level*/44, /*hp*/205.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/370.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103371, /*equipment_name*/"キューピッドのローブ", /*description*/"逸話に登場する愛の神の衣装を\nかたどって作られたローブ。\n背面の羽は、魔法で浮いており、\n本物と見間違えるほどの出来栄え。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103372, /*equipment_name*/"哲学者のローブ", /*description*/"幼くして皇帝の座についた少年を、\n生涯支え続けた哲学者の服。\n彼は皇帝に正しき道と教えを説き、\n国をより良い未来へと導いた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/245.0, /*atk*/0.0, /*magic_str*/14.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103401, /*equipment_name*/"ガーネットシールド", /*description*/"勇猛果敢な将軍の盾。\n彼は前線に立つと、\n自ら敵陣に切り込み、\n軍を勝利に導いた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/290.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103431, /*equipment_name*/"忍び足", /*description*/"履くと足音が消える特殊なブーツ。\n東国のスパイが履いていたらしいが\n彼らにまつわる情報は\nそのほとんどが謎のままだ。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/3.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103432, /*equipment_name*/"フェンサーブーツ", /*description*/"レイピアなどの細剣を使用する\n剣士のために作られたブーツ。\nとても軽く、力よりも機動性を\n重視する細剣士には最適の装備。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/3000, /*require_level*/35, /*hp*/115.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/4.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103461, /*equipment_name*/"ウィッチパレスブーツ", /*description*/"魔法使いが正装として履くブーツ。\nどんな場所を歩こうとも、決して\n汚れない魔法がかかっている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103491, /*equipment_name*/"ドラゴンヘッド", /*description*/"かつて存在した黒龍を信仰する\n騎士団が使用していた兜。\n彼らは龍に捧げる生贄のために、\n罪もない人々の命を奪い続けた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/35, /*hp*/235.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103521, /*equipment_name*/"ウィッチカチューシャ", /*description*/"魔法使いの一家が、修行のために\n家を出る娘に贈るカチューシャ。\n母親が自身の魔力を練り込み、\n愛をこめて作り上げる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103522, /*equipment_name*/"ウィッチハット", /*description*/"伝統的な魔法使いの帽子を、\n魔力を補助するアクセサリーで\n飾ったもの。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/26.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103551, /*equipment_name*/"オパールイヤリング", /*description*/"魔力の籠った\nオパールのイヤリング。\n装備者は体の内から活力が溢れ、\n攻撃力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/33.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/90.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103552, /*equipment_name*/"鷲獅子の羽飾り", /*description*/"鷲の上半身と、獅子の下半身を持つ\nグリフォンの羽で作られた羽飾り。\n獰猛な獣の力が宿っている。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/235.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103553, /*equipment_name*/"スカーレットダイヤ", /*description*/"世にも珍しい緋色のダイヤ。\n竜の炎を封じ込めていると言われ、\n装備すると烈火の如き力を\n手に入れることができる。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/70.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103581, /*equipment_name*/"エメラルドイヤリング", /*description*/"魔力の籠った\nエメラルドのイヤリング。\n装備者は不屈の精神を得て、\n防御力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103582, /*equipment_name*/"オクタグラムペンダント", /*description*/"八芒星は無限の循環を意味し、\n自然に存在する八つのエレメントを\n吸収し、装備者に\n絶え間なくその加護を授ける。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/37, /*hp*/0.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103583, /*equipment_name*/"獅子王の守護", /*description*/"かつて獅子王と呼ばれた王の品。\n生涯の大半を戦の中で\n過ごした彼は、その勇猛さから\n多くの民衆に畏れ敬われた。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/46, /*hp*/270.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103611, /*equipment_name*/"ターコイズイヤリング", /*description*/"魔力の籠った\nターコイズのイヤリング。\n装備者は叡智を得て、\n魔法攻撃力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/30.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103612, /*equipment_name*/"パールイヤリング", /*description*/"魔力の籠った\nパールのイヤリング。\n装備者は精神集中力が増し、\n魔力がアップする。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/36, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103613, /*equipment_name*/"ソーサラーグラス", /*description*/"賢人が龍脈の探索に使用する。\n大地に流れる気を研究し、\n大陸の環境や状態の調査に使う。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/41, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/0.0, /*magic_str*/39.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/35.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103614, /*equipment_name*/"月桂樹の哀しみ", /*description*/"精霊ダフネは自らの姿を月桂樹に\n変えた。彼女を愛したアポロンは\nその葉を永遠の愛の印として、\n身に着け続けたと言う。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104011, /*equipment_name*/"エンジェルブレード", /*description*/"大天使が使っていたとされる\n白銀の剣。\n邪悪な存在を消し去る\n退魔の力を持っている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/165.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104012, /*equipment_name*/"太陽剣", /*description*/"太陽神・アポロンの加護を受けたと\nされる剣。その剣を手にした人物は\n太陽王と呼ばれ、歴史に名を刻むと\n共に永きに渡る繁栄をもたらした。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/70.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104013, /*equipment_name*/"アイスクレイモア", /*description*/"斬撃と同時に氷の魔法が発動し\n敵を討つ魔法剣。\n氷の妖精の魔力を用いているため、\n常に冷気を帯びている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104014, /*equipment_name*/"カオスブレード", /*description*/"幾万人の生き血を吸い続けた妖剣。\n呪われれば血を求める傀儡と\n化すが、制御できれば鋼のごとき\n力を得るといわれる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/140.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104041, /*equipment_name*/"妖蝶の刀", /*description*/"深き森の遺跡に眠るという刀剣。\nこの剣に選ばれし者は、蝶に\n誘われることで、その場所に\n辿り着くことができるという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/80.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104042, /*equipment_name*/"光輝の剣", /*description*/"光の女神・アグライアの神託を受け\nた刀鍛冶が生涯をかけて鍛え上げた\n光の剣。人類が魔神に対抗しうる武\n器として、高名な勇者が手にした。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/192, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/10.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104043, /*equipment_name*/"鷹神の煌剣", /*description*/"天空を支配する気高き鷹神の剣。\n振れば獲物を狙う鷹のごとく、\n魔力で加速した切っ先が\n的確に敵の急所をとらえる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104044, /*equipment_name*/"鳳凰刀", /*description*/"日出処に現れる鳳凰神の力を\n宿した刀。意志の強さで纏う炎が\n変化し、強靭な意志を持つ者\nであれば鉄をも溶かす烈火となる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104071, /*equipment_name*/"プレシャスナイフ", /*description*/"あるドワーフの名工が、人間の王に\n献上したと言われる短剣。\n使用された素材は、現存する\nどの鉱石とも一致しない未知の物。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104072, /*equipment_name*/"プリンセスの懐剣", /*description*/"歴史上最も長く存続した小国の王家\nに伝わる短刀。その刀は代々王女へ\nと受け継がれ、彼女らに降りかかる\n魔を払い、国の安寧を守り続けた。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/50.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/300.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104073, /*equipment_name*/"ダークマターエッジ", /*description*/"禁忌に手を染め、人の道を外れた\n悪しき魔法使いを粛清するために\n作られた短剣。魔法の発動を封じる\n術式が施されている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104074, /*equipment_name*/"ユニコーンナイフ", /*description*/"純真無垢な女性だけが扱える短剣。\nあらゆる穢れを拒絶するため、\n邪な意志を持つ者には触れる\nことすらできない。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/160, /*sale_price*/18000, /*require_level*/59, /*hp*/100.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104101, /*equipment_name*/"火焔のガントレット", /*description*/"煉獄の焔で鍛え上げられた\n深紅の篭手。\n拳から発する業火は、\n眼前の敵を跡形もなく焼き払う。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/50, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104102, /*equipment_name*/"獅子王のガントレット", /*description*/"獅子王と呼ばれた名君が使用した\n黄金の篭手。その篭手は使用者に\n無限の力を与え、一騎当千の戦い\nを約束したといわれている。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/81.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/12.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104103, /*equipment_name*/"ブリザードクロー", /*description*/"たった一人で万の軍勢を滅ぼした\n氷の女神の魔力を封じた鉤爪。\n切り裂く風は吹雪のように凍てつき\n敵の戦意を挫く。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/90.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104104, /*equipment_name*/"蒼雷拳", /*description*/"猛き雷神の加護を受けた籠手。\n放たれる蒼き雷撃は一瞬で敵を\n焦がし、雷雲さえも\n呼び寄せるという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104131, /*equipment_name*/"雷光弓", /*description*/"英雄は人を喰らう海竜を倒すために\nこの弓に雷の力を封じ込めた。\n電光纏いしその矢は、海竜を一撃で\n葬ったといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/145, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104132, /*equipment_name*/"エンジェルボウ", /*description*/"旧時代の終末大戦で大天使が悪魔\nとの戦いに用いた大弓。この放た\nれた矢の一撃は、地獄の最下部コ\nキュートスにまで届いたという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/65.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104133, /*equipment_name*/"アルテミスボウ", /*description*/"狩猟の女神の加護を宿した弓。\n獲物を逃さない百発百中の狙いと、\n破魔の効果を得た矢が\n一射で戦局を塗り替える。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104134, /*equipment_name*/"精霊樹の弓", /*description*/"精霊樹を素材にした弓。\n精霊界の神聖な魔力を内包\nしており、放たれる矢は\nあらゆる敵を貫く威力を持つ。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/60.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104161, /*equipment_name*/"緋竜槍", /*description*/"火山に生息する竜の背に、\n突き刺さっていた槍。\n穂先は長年竜の体内にあったことで\n炎の力を宿すこととなった。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104162, /*equipment_name*/"クイーンビースピア", /*description*/"大陸の全土を支配したといわれる伝\n説の女王が使用していた大槍。英知\nに優れ、戦にも長けていた彼女は、\nこの槍の力で大陸を手中に収めた。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104163, /*equipment_name*/"ストームブリンガー", /*description*/"伝説の槍騎士が所有していた名槍。\n一振りすれば真空の刃が旋風となり\n刃に直接触れることなく\n周囲の敵を一網打尽にする。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/205, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/5.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104164, /*equipment_name*/"ガイアバルディッシュ", /*description*/"大地母神の加護を受けた槍。\n決して折れず欠けず砕けない。\nその強靭さを所持者に宿し、膂力を\n飛躍的に向上させる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104191, /*equipment_name*/"紫龍斧", /*description*/"人の世を脅かした邪龍を、各地の\n英傑たちが集いこの斧に封印した。\n龍の魂が宿ったこの斧は、\n禍々しいエネルギーを発している。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/91.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104192, /*equipment_name*/"ヘルファイアアックス", /*description*/"地獄の業火が封じられた両刃斧。そ\nの身は血のように赤く、ひと振りす\nれば紅蓮の炎が敵に襲いかかり、骨\nまで焼き尽くすといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/97.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104193, /*equipment_name*/"戦女王の烈斧", /*description*/"戦の最前線に立ち続けた女王の斧。\n想像を絶する重量だが、斧が認めた\n所有者には軽々と扱える。その刃は\n堅牢な城門すら一撃で打ち破る。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/114.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104194, /*equipment_name*/"アクアスラッシャー", /*description*/"水の精霊が精霊力を注いだ斧。\n軽量だが、切れ味は鋭く、\n触れるだけで鉱物を寸断することも\n可能。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104221, /*equipment_name*/"フューリーロッド", /*description*/"妻を殺され、復讐の鬼と化した\n魔法使いが所持していた杖。\n彼は自身の怒りをこの杖に籠め、\n妻の死に関わった全員を抹殺した。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/0.0, /*magic_str*/91.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104222, /*equipment_name*/"太陽のロッド", /*description*/"太古の賢人たちが太陽の欠片をより\n集めて作ったとされる神秘的な杖。\nその杖に選ばれた者は全知全能の力\nを身に宿すといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104223, /*equipment_name*/"司法神の錫杖", /*description*/"正義と平和、そして真実を司った\n神が持っていたとされる錫杖。\nこれを手にした者は、神の託宣を\n授かるという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/104.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104224, /*equipment_name*/"ハイデビルズワンド", /*description*/"高位な悪魔の魂を宿した杖。\n敵対者には容赦がなく、\n敵を倒す魔法を使用する\nことで真価を発揮する。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/0.0, /*magic_str*/108.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104251, /*equipment_name*/"月華杖", /*description*/"月の女神が、信仰深い信者の\n病を治す際に使用したとされる杖。\n彼女の力を恐れた死の神は、\n杖を下界に隠したといわれている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/0.0, /*magic_str*/73.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104252, /*equipment_name*/"生命のロッド", /*description*/"生命の樹と呼ばれる巨大樹の枝で作\nられた奇跡の杖。振るえば枯れ木に\n新緑の葉が芽吹き、大地を突けば水\nが湧くといわれている。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/67.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104253, /*equipment_name*/"フェニックスロッド", /*description*/"不死鳥の加護をまとった聖なる杖。\n無尽蔵の魔力供給を得ることができ\n魔力切れを起こすことなく\n魔法を撃ち続けることができる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104254, /*equipment_name*/"ハイエンジェルロッド", /*description*/"慈愛溢れる天使の加護を受けた杖。\n他者を慈しむ思いに呼応するため、\n仲間を助ける魔法の効果が\n著しく向上する。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/0.0, /*magic_str*/86.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104281, /*equipment_name*/"インビジブルドレス", /*description*/"姿を消す魔法がかかった特殊な鎧。\nある国の姫が許されざる恋をし、\n駆け落ちの際に使用したと\n言われている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104282, /*equipment_name*/"ミスリルプレート", /*description*/"銀の輝きと鋼をしのぐ強さを\nあわせ持つ希少な金属と、\n当代随一の名工との出会いが生んだ\n美と強靭さを兼ね備えた至高の鎧。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/172, /*sale_price*/10000, /*require_level*/52, /*hp*/250.0, /*atk*/25.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104283, /*equipment_name*/"クルセイダープレート", /*description*/"神の名のもとに異教徒を打ち払った\n誇り高き勇者がまといし鎧。\n付与された神の加護の恩恵は\n穢れなき魂の者にのみ与えられる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/200, /*sale_price*/15000, /*require_level*/54, /*hp*/150.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104284, /*equipment_name*/"スカーレットメイル", /*description*/"闇の女帝から、国を守る為に\n戦った騎士姫の紅き鎧。\nその神々しい輝きは敵を恐怖させ、\n民には勇気を与えた。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/160.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104311, /*equipment_name*/"エンジェルアーマー", /*description*/"天使たちが戦で使用した鎧。\n低級な悪魔は鎧に近づくことすら\nできず、白銀に反射する光を\n浴びるだけで、消滅したという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104312, /*equipment_name*/"バーミリオンメイル", /*description*/"一騎駆けにその身を捧げた\n狂戦士の魂が宿る鎧。\nこれを身に着けた者は\n戦鬼の力を得る。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/10000, /*require_level*/52, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/400.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104313, /*equipment_name*/"バイオレットアーマー", /*description*/"戦場の死神と恐れられた\n大将軍が身につけた美麗な鎧。\n敵兵はこの菫色の甲冑を見ただけで\n浮足立ち、混乱したという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/252, /*sale_price*/15000, /*require_level*/54, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104314, /*equipment_name*/"ドラゴニックアーマー", /*description*/"竜殺しの英雄が愛用した重鎧。\n竜の意匠が施された分厚い装甲は、\n鉄をも引き裂く爪や、\n灼熱のブレスをも防いだという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/330, /*sale_price*/18000, /*require_level*/58, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104341, /*equipment_name*/"月下の法衣", /*description*/"王の密命を受けた神官が己の命を\n賭して作り上げた美しき法衣。\n月明かりの中で縫い上げたため、\n月光を受けると七色の輝きを放つ。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/190, /*sale_price*/7200, /*require_level*/51, /*hp*/360.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/325.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104342, /*equipment_name*/"ハーミットクロース", /*description*/"隠者と呼ばれる、魔道の深淵を\n覗かんとした者の服。\n魔力が練り込まれた糸で\n縫製されている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/10000, /*require_level*/52, /*hp*/180.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104343, /*equipment_name*/"蒼天のローブ", /*description*/"民のためなら神々にすら刃向かう\n義侠の志に篤き術者のローブ。\n無上の輝きに包まれし天の加護が\n装備者に鋼の意志を与える。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/270, /*sale_price*/15000, /*require_level*/54, /*hp*/200.0, /*atk*/45.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104344, /*equipment_name*/"ダークテラードレス", /*description*/"闇の女帝が身に纏いしドレス。\n蠢く闇の如き漆黒の意匠は、\nあらゆる魔をも魅了し、\n人々には恐怖を与えたという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/330, /*sale_price*/18000, /*require_level*/58, /*hp*/200.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/250.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104371, /*equipment_name*/"大魔導師のローブ", /*description*/"人生を魔道に捧げた大魔導師が\nまとっていたローブ。\nかつての主の強大な力により、\n未だ妖しい魔力を放ち続けている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104372, /*equipment_name*/"聖者のローブ", /*description*/"天使たちと共に、\n悪魔と対峙した聖者が\nまとったとされるローブ。\nその気高さは神話の領域。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/141, /*sale_price*/10000, /*require_level*/52, /*hp*/240.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104373, /*equipment_name*/"翠緑の霊衣", /*description*/"神樹の森に住まうという、偉大なる\n賢者が身につけたローブ。\n神霊の力を宿しており、\nあらゆる魔の力から装備者を守る。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/321, /*sale_price*/18000, /*require_level*/58, /*hp*/200.0, /*atk*/0.0, /*magic_str*/25.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/3.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104401, /*equipment_name*/"エンプレスシールド", /*description*/"女王が絶対の信頼を置く\n近衛兵にのみ与えられる盾。\n絶対的な守護の力を宿しており、\n魔法や呪いすらも跳ね返す。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/166, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/4.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104402, /*equipment_name*/"ガーディアンシールド", /*description*/"乱世の小国において、生涯をかけて\n王女の命を守り通した騎士の盾。\nのちに王女は女王として近隣を統べ\nその騎士を救国の英雄と讃えた。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/246, /*sale_price*/15000, /*require_level*/55, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104431, /*equipment_name*/"パラディングリーブ", /*description*/"神聖な力による加護を受けた\n聖騎士の脚具。\n彼らは民の命を守るため、\n命を懸けて魔物と戦った。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/9.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/6.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104432, /*equipment_name*/"エンジェルブーツ", /*description*/"神話に姿を現す聖なる脚具。\nあらゆる攻撃を寄せ付けず、\n炎の中すら涼しげに\n歩くことができる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/140, /*sale_price*/10000, /*require_level*/52, /*hp*/220.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104433, /*equipment_name*/"フェアリアルブーツ", /*description*/"太古の妖精が加護を与えた脚甲。\n巨大な霊力に加え魔除けの\nミスリルを随所に細工することで、\n破魔の力を増大させている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/290, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/38.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104461, /*equipment_name*/"ウィザードブーツ", /*description*/"王族に認められた魔法使いにのみ\n贈られる特別な品。\nその道百年のエルフの職人にしか、\nこの靴を作ることができない。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104462, /*equipment_name*/"ネクロマンサーブーツ", /*description*/"黄泉を司る者として恐れられた\n稀代のネクロマンサー愛用の靴。\nその靴音を聞いた者は精気を奪われ\n装備者に隷属するという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/220, /*sale_price*/15000, /*require_level*/55, /*hp*/100.0, /*atk*/0.0, /*magic_str*/15.0, /*def*/1.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104463, /*equipment_name*/"アクエリアスブーツ", /*description*/"水を司る四大精霊の\n力を宿した美しいブーツ。\n偉大なる精霊の力により、\n装備者の魔力を引き出すという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/40.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/7.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104491, /*equipment_name*/"フェザーワルツ", /*description*/"空の踊り手と呼ばれた半神半人の\n騎士が使用していた兜。\n彼は空を縦横無尽に舞い、\n敵を翻弄したと言われている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/135, /*sale_price*/7200, /*require_level*/52, /*hp*/490.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104521, /*equipment_name*/"ウィザードフード", /*description*/"国が認める最高位の魔法使いの証。\nこのフードを持つ者は、\n王の傍らに立つことを許される。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/0.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104522, /*equipment_name*/"法王のフード", /*description*/"歴代の法王が\n外征時に身につけた頭具。\n破魔の効果が付与されており、\n幻術を造作もなく見破る。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/10000, /*require_level*/52, /*hp*/90.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104523, /*equipment_name*/"福音のティアラ", /*description*/"とある魔法王国の女王が身につけた\n由緒正しき冠。自身の魔力を\n増大させるその力で、女王は長く\n王国に秩序をもたらした。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/246, /*sale_price*/15000, /*require_level*/55, /*hp*/100.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104551, /*equipment_name*/"エレメンタルハート", /*description*/"四大精霊の魔力を統合し、\n封じ込めた特殊な鉱石。\n稀代の錬金術師が成し遂げた\n一大発明品。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104552, /*equipment_name*/"太陽のアミュレット", /*description*/"太陽神の加護を受けた宝玉を用いた\n魔に抗う者たちのための首飾り。\n強大な敵と対峙しても怯むことなく\n自らを鼓舞して戦うことができる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/0.0, /*atk*/70.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104553, /*equipment_name*/"焔帝の指輪", /*description*/"創世の刻より燃え続いている炎を\n宿すルビーが嵌められた指輪。\n万物を焼き払うほどの炎を\n身に纏うことができるという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/200.0, /*atk*/74.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104554, /*equipment_name*/"猫神のペンダント", /*description*/"猫神が生み出したとされる宝石。\n所持者は驚異的な視力と\n反射神経を得るため、\n何者も見落とさず、逃さない。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/0.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/8.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104581, /*equipment_name*/"身代わりのバングル", /*description*/"長きに渡り君臨した皇帝が\n作らせたという伝説の魔道具。\n装備者に降りかかる厄災を\n肩代わりするという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/240.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/5.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104582, /*equipment_name*/"月のブレスレット", /*description*/"月の魔力を閉じ込めたクリスタルを\nあしらったブレスレット。\n放ち続ける霊妙な光は加護となり、\nいかなる呪詛をも振り払う。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104583, /*equipment_name*/"聖信徒のペンダント", /*description*/"聖霊に認められた者だけが所持を\n許される首飾り。\n聖霊の限りない慈愛により\n所持者は脅威から護られる。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/200.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104611, /*equipment_name*/"三日月の嘆き", /*description*/"謀略により愛する者を弓で害した\n月の女神のアミュレット。\n悲哀に満ちた下弦の月の魔力が\n飛矢を退ける。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/100.0, /*atk*/0.0, /*magic_str*/60.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/6.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104612, /*equipment_name*/"竜の涙", /*description*/"竜の流した涙が悠久の時を超え\n結晶となった、希少極まる鉱石。\n装備者の魂を直接震わせ、\n竜の力を宿すと伝えられている。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/120.0, /*atk*/0.0, /*magic_str*/70.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104613, /*equipment_name*/"大賢者の宝珠", /*description*/"数多の戦を勝利に導いた大賢者が、\n持てる全ての魔力を注ぎ込んだ\n水晶玉。所持する者に、栄光へ導く\n才智を授けてくれるという。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/0.0, /*atk*/0.0, /*magic_str*/80.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/16.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105011, /*equipment_name*/"ライトニングブレード", /*description*/"雷神が地上に落とした怒りの雷から\n生まれた大剣。その一振りは空を裂\nく稲妻を生み、あらゆる敵を薙ぎ払\nうことができるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105012, /*equipment_name*/"天開の聖剣", /*description*/"聖域への鍵の役割を\n果たすとされる、神聖なる大剣。\n剣が纏う神気により、かざすだけで\n邪悪な存在はかき消されるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/72, /*hp*/120.0, /*atk*/115.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105013, /*equipment_name*/"罪過の大剣", /*description*/"人を斬らずに罪を斬り祓うとされる\n大剣。この剣で斬られた罪人は傷一\nつ負わないかわりに、その生涯を贖\n罪に捧げるようになるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/475, /*sale_price*/30000, /*require_level*/80, /*hp*/140.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/8.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105014, /*equipment_name*/"神湊剣アクアルーラー", /*description*/"海神の住まう深海の神殿に祀られて\nいた水の神剣。水そのものの刃は千\n変万化、洪水や大渦、霧など多彩な\n形態で敵を圧倒する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/590, /*sale_price*/32000, /*require_level*/88, /*hp*/160.0, /*atk*/132.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105015, /*equipment_name*/"覇王光龍剣", /*description*/"伝説の存在たる光龍の力を宿した大\n剣。あまりの斬れ味に手にしたもの\nさえ無事ではいられないため黄金の\n文様を刻みその力を制御している。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/565, /*sale_price*/32000, /*require_level*/94, /*hp*/60.0, /*atk*/145.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105016, /*equipment_name*/"星核剣エルツシュナイド", /*description*/"大地を統べる母なる神が、正しき心\nの戦王に与えし鉱石の剣。地に突き\n立てれば硬く鋭い鉱柱が地の果てま\nで屹立し、敵の軍勢を討ち払う。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/465, /*sale_price*/32000, /*require_level*/100, /*hp*/80.0, /*atk*/153.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105017, /*equipment_name*/"神兎乃大剣", /*description*/"大地の遣いである神兎の力が宿りし\n霊剣。その愛らしい刀身から放たれ\nる、兎の群れの如き多重の斬撃は、\n敵を瞬時に葬り去るという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/432, /*sale_price*/32000, /*require_level*/106, /*hp*/60.0, /*atk*/167.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105041, /*equipment_name*/"百華白桜刀", /*description*/"白く輝き、咲き誇る桜の美麗さを備\nえた刀。柄に付けられた鈴が鳴る間\nに、花を散らすような軽やかさで、\n次々と敵を切り刻むことができる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/118.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105042, /*equipment_name*/"流禍の蒼刃", /*description*/"天変地異で水没した帝国の魔法剣。\n刃には国の歴史が刻まれている。\n水精霊の寵愛を受けた刀身は、\n流水の如き変幻自在の斬撃を生む。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105043, /*equipment_name*/"妖刀血鴉", /*description*/"常に血を欲している呪われた刀。\n振るわれるに値しないと刀が判断し\nた使い手は呪い殺され、刀が纏う妖\n気の一部にされるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/592, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/125.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105044, /*equipment_name*/"焔神刀イグニース", /*description*/"火神の炎を鍛え刃に変えた紅蓮の刀\n剣。超高熱の刀身はあらゆるものを\n焼き斬り、炎を解放すれば敵の大軍\nでさえ一瞬にして灰燼と化す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/88, /*hp*/80.0, /*atk*/134.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105045, /*equipment_name*/"翠風神ノ太刀", /*description*/"風神が縦横無尽にふるっていたとさ\nれる太刀。刀身に纏う無数の疾風が\n近づいた敵を触れることなく鋭利に\n切り刻む。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/741, /*sale_price*/32000, /*require_level*/94, /*hp*/100.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105046, /*equipment_name*/"神降りの枝刃刀", /*description*/"かつて神降ろしの儀式に使われてい\nた祭礼の剣。刃には今も神の光が宿\nり、一振りであらゆる邪を断ち祓う。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/544, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/150.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/25.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/2.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105047, /*equipment_name*/"旺鯉乃神刀", /*description*/"荒れ狂う水神を鎮めた巫女が振るい\nし霊刀。天を泳ぐ神魚の如き美しい\n刃と、神楽を舞っているかのような\n剣閃は、神をも魅了するという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/172.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105071, /*equipment_name*/"エメラルドダガー", /*description*/"豊饒神の加護を受けたとされる\nダガー。その鮮やかな刃は大自然の\nエネルギーを吸収し、鮮烈な斬撃を\n放つことができるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105072, /*equipment_name*/"ラヴァエッジ", /*description*/"火の精霊を宿した短剣。\n溶岩にも匹敵する灼熱の刀身より\n繰り出される一撃は、触れた敵を\n一瞬のうちに溶かしてしまう。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/405, /*sale_price*/27000, /*require_level*/72, /*hp*/40.0, /*atk*/115.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105073, /*equipment_name*/"ティルナノーグダガー", /*description*/"妖精の王族に代々伝わる懐剣。\n刃には妖精族秘伝の魔術が刻まれて\nおり持ち主から邪なるものを遠ざけ\nる力を持つ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105074, /*equipment_name*/"天雷の双神剣", /*description*/"雷を支配する神が常に帯刀していた\n短剣。双極の刃が無尽の稲妻を生み\n出し、天を覆いつくすほどの雷霆を\n撃ち放つ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/88, /*hp*/110.0, /*atk*/124.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/6.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105075, /*equipment_name*/"天黒剣オブシウス", /*description*/"かつて堕天使が身に付けていたとさ\nれる短剣。使い手を取り巻く因果を\n思うが儘に歪め、天地を我が物とす\nるかの如く暴威をふるう。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/520, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/146.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105076, /*equipment_name*/"ゲヘナドラグーン", /*description*/"神々さえ恐れ慄いた獄炎竜の爪で作\nられた短刀。三爪から生み出される\n無慈悲な爆炎は、天の頂すら灰に帰\nす。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/440, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/161.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105077, /*equipment_name*/"冥刃・鴉天狗", /*description*/"死を司る黒翼の霊鳥の魔力を宿した\n漆黒の魔剣。神をも葬る黒刃から、\n疾風の如き無慈悲な剣閃が疾走ると\nき、あらゆる生が冥府へと還る。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/153.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/4.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105101, /*equipment_name*/"ムーンセスタス", /*description*/"神々しいオーラを放つ、\n月をかたどった格闘武器。月光の輝\nきを拳に纏い、強烈な一撃を敵に叩\nき込むことができるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/6.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105102, /*equipment_name*/"クリムゾンクロー", /*description*/"魔を宿した真紅の鉤爪。\n苛烈極まる一撃はあらゆる生者に\n絶望を与え、対面した敵に\n絶対なる死を与えるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105103, /*equipment_name*/"コキュートスネイル", /*description*/"冥府の氷を精錬して作られた氷爪。\nこの爪甲で切り裂かれた者は瞬く間\nに凍てつき、物言わぬ氷像として冥\n府の住人に仲間入りする。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/470, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105104, /*equipment_name*/"嵐神テンペストギア", /*description*/"嵐を司る神の力を宿した籠手。暴風\nを纏う籠手が繰り出す打撃は荒ぶる\n竜巻の如し。戦いが終わる頃、辺り\nはただ無人の荒野が広がるのみ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/605, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/124.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105105, /*equipment_name*/"極闇爪ブラッディハウル", /*description*/"大狼の狂爪の欠片から造られたとい\nう籠手。圧倒的な力を与える代償に\n未熟な者が扱えば心身共に蝕まれ、\n呪いつくされると畏怖されている。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/111.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105106, /*equipment_name*/"星核拳エルツロア", /*description*/"大地を統べる母なる神が、圧政に抗\nう不屈の闘士に与えた巌の拳。地の\n底でたぎるマグマのエネルギーを宿\nした拳は、一撃で万物を破壊する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/605, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/154.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105107, /*equipment_name*/"通天手甲・犀打ち", /*description*/"原野の霊獣が姿を変えたと謳われる\n拳武具。武勇に優れた者が扱えば、\n心身ともに錬鉄を超える頑強さを誇\nり、武の境地の入口へ至るという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/133.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/28.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105131, /*equipment_name*/"白銀の大翼弓", /*description*/"白銀の翼を持つ鳥を模した弓。放た\nれた矢は滑空する鳥のように飛び、\nその透き通るような一撃であらゆる\n魔を貫く。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/245, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105132, /*equipment_name*/"アビスボウ", /*description*/"魔族の女王の加護を受けた妖弓。\n射掛けた矢は禍々しい魔力を帯び、\n闇に紛れるかのごとく\nどこからともなく敵を襲う。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/7.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105133, /*equipment_name*/"紅天弓", /*description*/"伝説の弓使いが、弓折れ矢尽きた際\n天から遣わされたという聖なる弓。\nどんな速射にも耐え、使い手の狙っ\nたところに矢を届ける。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/126.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105134, /*equipment_name*/"装號弓デウスフォール", /*description*/"神話の大英雄が使った巨大な弓。常\n人では持つことすらかなわず、放た\nれた一矢は天を穿ち空を支配する龍\nすら撃ち墜とすという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/132.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105135, /*equipment_name*/"氷涙弓フリージングティア", /*description*/"冬の女神が流した涙の結晶で作られ\nた弓。放たれた矢は女神の加護を纏\nい、あらゆる難敵を永久に氷漬けに\nする。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/470, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/137.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105136, /*equipment_name*/"星核弓エルツボーゲン", /*description*/"大地を統べる母なる神が、邪竜退治\nに挑む英雄に与えた頑強な大弓。大\n地のエネルギーが巡る弓から放たれ\nる一矢は天に輝く星をも穿つ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/139.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105137, /*equipment_name*/"天穿乃隼弓", /*description*/"天翔ける精霊を宿せし狩弓。荒ぶる\n風を纏い放たれる一矢に貫けぬ物は\nなく、精霊に認められし使い手とも\nなれば、嵐すら巻き起こすという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/630, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/160.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/22.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105161, /*equipment_name*/"ゼロフロストランス", /*description*/"伝説の氷獄に封印されていたとされ\nる氷の槍。その一突きの餌食になっ\nた者は、悲鳴すらも凍りついてしま\nうという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/98.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105162, /*equipment_name*/"ゲイルスティンガー", /*description*/"風の神からの恩恵を授かった\n由緒正しき大槍。\nその一撃は目にも止まらぬ\n突風となり、全てを吹き飛ばす。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/305, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/92.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105163, /*equipment_name*/"護天の聖槍", /*description*/"聖域を守護する者が振るった神聖な\n槍。切っ先が向く先に敵はなく、た\nだ足元に邪悪なるものの骸が転がり\n続けたという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/102.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105164, /*equipment_name*/"冥神槍ドゥームペイン", /*description*/"幽冥を支配する神が振るう魔槍。紅\n蓮の切っ先からは奈落に幽閉されし\n咎神の憎悪と闇の魔力が迸り、見境\nなしの殺戮を繰り返す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/692, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/113.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105165, /*equipment_name*/"豪炎槍プロミネンス", /*description*/"天にまで名を轟かせる鍛冶師が炎帝\nの名を受け生み出した伝説の槍。そ\nの穂先は灼熱の業火を纏い、神の怒\nりの如く森羅万象を焼き貫く。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/440, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/141.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105166, /*equipment_name*/"星核槍エルツボルグ", /*description*/"大地を統べる母なる神が、禍を司る\n悪しき女神に立ち向かう勇者に与え\nた重槍。迸る大地のパワーを纏いし\n一閃は、この世の一切を塵に返す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/100, /*hp*/150.0, /*atk*/127.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105167, /*equipment_name*/"聖鹿槍アルマベナード", /*description*/"神域を護る聖獣を象った霊槍。自然\nに存在する命脈を司るとされ、その\n一撃は、荒れ果てた死の土地へ新た\nな命を芽吹かせるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/418, /*sale_price*/32000, /*require_level*/106, /*hp*/250.0, /*atk*/139.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105191, /*equipment_name*/"ネクロディムアックス", /*description*/"冥界の力が封じ込められた斧。死を\n司る使者がその役目を執行する時に\n使い、鈍い音を立てながら生者を冥\n界に送るという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/400, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/1.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105192, /*equipment_name*/"神判の聖斧", /*description*/"天界が裁きを下す際に\n用いるとされる、聖なる斧。\n相手が抱える業が大きいほど\n威力は増大し、神の威光を示す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105193, /*equipment_name*/"ガイアアクス", /*description*/"古の女戦士が振るった戦斧。\nこの斧に屠られた者は、神々の住ま\nう肥沃な理想郷へと迎え入れられる\nといわれている。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/430, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105194, /*equipment_name*/"神轟斧テラブレイク", /*description*/"巨神族が愛用した無骨な戦斧。神界\nの巨岩から削り出した刀身は恐るべ\nき硬度と重量を誇り、たった一撃で\n大地の形を変えてしまう。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/585, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/140.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105195, /*equipment_name*/"機操斧コアブレイカー", /*description*/"かつて栄華を誇った文明の遺失技術\nである蒸気による変形機構を備えた\n斧。出力を最大まで振り切ると星の\n核をも砕くと言われている。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/642, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/151.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105196, /*equipment_name*/"ターコイズ・ノア", /*description*/"神代に存在した蒼海の深水を鍛えた\n戦斧。膨大な量の水を凝縮した超重\nの刃は、大波の如き破壊の奔流を生\nみ出し、眼前の敵全てを呑み潰す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/580, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/170.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105197, /*equipment_name*/"炎羆帝の爪戦斧", /*description*/"山の神と呼ばれた火熊の魂を宿す戦\n斧。魂を共鳴させ放つ灼熱の一振り\nは、味方に豊穣と繁栄を、仇なす者\nには破滅と衰退をもたらすという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/585, /*sale_price*/32000, /*require_level*/106, /*hp*/200.0, /*atk*/150.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105221, /*equipment_name*/"ゼノスフィアロッド", /*description*/"異界の魔力を封じ込めたとされる\n杖。爆発的な威力を宿しているが、\n相応の実力者でなければ、その力を\n解放することはできないという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/3.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105222, /*equipment_name*/"ジェネシスロッド", /*description*/"世紀の大魔導士が愛用した杖。\n天地創造の理を宿しているとされ、\n一振りで魔物を\n塵をも残さず消し飛ばすという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/0.0, /*magic_str*/110.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/12.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105223, /*equipment_name*/"ケラウノスワンド", /*description*/"天に轟く稲妻の力が込められた聖な\nる杖。人の手には余る苛烈な力のこ\nもった杖ではあるが、ひとたび使い\nこなせれば如何なる敵も焼き払う。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/525, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/118.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/14.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105224, /*equipment_name*/"黒蛇龍の杖", /*description*/"神々の大戦を生き延びた龍の魂を封\nじた杖。ランタンの放つ黒き魔光が\n敵を葬り、魂を喰らう。喰らった魂\nの数だけ灯りは強く妖しく光る。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/770, /*sale_price*/32000, /*require_level*/88, /*hp*/0.0, /*atk*/0.0, /*magic_str*/128.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/16.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105225, /*equipment_name*/"月淵杖サクリファイス", /*description*/"死の神が月の女神から奪い、深淵に\n貶めた魔法杖。使う者の命運を喰ら\nい、月相の如く生と死を流転させる\nと言い伝えられる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/0.0, /*magic_str*/146.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105226, /*equipment_name*/"清雅なる翠蝶杖", /*description*/"この世で一番美しいとされる霊蝶の\n翅を用いた杖。翠の翅が生み出す風\nと輝く鱗粉が敵の感覚を狂わせ、荒\nぶる魔物も無血で制圧する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/440, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/0.0, /*magic_str*/154.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105227, /*equipment_name*/"黒蛇の魔炎杖", /*description*/"神話に記された蛇たちの覇王の魂を\n刻んだ魔杖。放たれる業炎は敵に巻\nきついて離さずその身を焼き続け、\n魂すら燃やし尽くすという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/477, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/0.0, /*magic_str*/172.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/12.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105251, /*equipment_name*/"フレイアムルロッド", /*description*/"愛に生きた炎の女神による加護を受\nけた杖。燃えるような愛の光は、傷\nついた仲間の傷だけではなく、心ま\nでも癒すことができるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/95.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105252, /*equipment_name*/"神花の聖杖", /*description*/"慈愛の精神によって結晶化した\n霊花があしらわれた聖杖。\nもたらされる無尽蔵の治癒力は、\nもはや女神そのもの。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/300, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/2.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105253, /*equipment_name*/"アトランティスロッド", /*description*/"母なる海の生命を蓄えた聖なる杖。\nこの杖を振るえば、いかなる怪我や\n病気もたちどころに快癒するといわ\nれている。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/460, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105254, /*equipment_name*/"天杖ステラスフィア", /*description*/"稀代の占星術師が知識と技術を結集\nさせた神秘の杖。天球が星の動きを\n読み解いて仲間を導き、星の輝きを\n集め治癒の光に変換し解き放つ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/580, /*sale_price*/32000, /*require_level*/88, /*hp*/60.0, /*atk*/0.0, /*magic_str*/110.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105255, /*equipment_name*/"世界樹の枝杖", /*description*/"様々な世界を内包すると言われる世\n界樹の枝を模した杖。宝玉から発す\nる生命の息吹により、万物に癒しを\n与える。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/1000, /*sale_price*/32000, /*require_level*/94, /*hp*/0.0, /*atk*/0.0, /*magic_str*/127.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105256, /*equipment_name*/"炎鳥杖レイズフレア", /*description*/"永遠を生きる不死鳥の炎を宿す深紅\nの杖。杖から溢れ出す炎は重篤なケ\nガもたちどころに癒し、時に死者す\nら蘇らせるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/32000, /*require_level*/100, /*hp*/0.0, /*atk*/0.0, /*magic_str*/152.0, /*def*/1.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/1.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105257, /*equipment_name*/"白蛇の水聖杖", /*description*/"神話に記された癒しの蛇妃の加護を\n受けた聖杖。杖より絶えず生成され\nる聖水は、あらゆる傷病のみならず\n死の運命すら流し清めるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/432, /*sale_price*/32000, /*require_level*/106, /*hp*/0.0, /*atk*/0.0, /*magic_str*/136.0, /*def*/4.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105281, /*equipment_name*/"ファンタズムメイル", /*description*/"見るものを惑わす魔性の鎧。\n立ち上る紫煙の揺らめきを一度目に\nすると、逃れようのない深い幻惑に\n誘われてしまうという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/349, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/32.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105282, /*equipment_name*/"九天の鎧", /*description*/"失われた太古の技術で作られた鎧。\n身に着けた者は不可視の翼を授かり\n戦場を縦横無尽に駆け抜ける。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/392, /*sale_price*/27000, /*require_level*/77, /*hp*/80.0, /*atk*/42.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105283, /*equipment_name*/"レイジクイーンドレス", /*description*/"暗澹たる深山を支配してきた賊の女\n王の鎧。纏えば魔獣の如き強靭さと\n獰猛さが滾り、血風舞う戦場で輝く\n野蛮な美しさに人々は畏怖する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/362, /*sale_price*/30000, /*require_level*/85, /*hp*/80.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105284, /*equipment_name*/"閃華の舞鎧", /*description*/"伝説の舞踊兵士の一族が戦地で纏っ\nていた軽鎧。花びらが舞うように身\n軽な上に、その強度はいかなる猛攻\nも許さない。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/93, /*hp*/0.0, /*atk*/48.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/12.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105285, /*equipment_name*/"麗装ソウルローゼス", /*description*/"自ら戦場で剣を振るった、伝説の軍\n国の王女が纏いしバラの鎧。纏えば\n気高き魂がその身に宿り、いかなる\n絶望や恐怖にも屈することはない。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/795, /*sale_price*/32000, /*require_level*/99, /*hp*/0.0, /*atk*/70.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/2.0, /*physical_critical*/9.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105286, /*equipment_name*/"極南天の烈蒼鎧", /*description*/"南天を司る守護天使の加護を受けた\n紺碧の鎧。正義を示す神の使徒を率\nいる強者の証であり、守護天使の大\nいなる力を恩恵に授かるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/452, /*sale_price*/32000, /*require_level*/105, /*hp*/0.0, /*atk*/81.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105287, /*equipment_name*/"聖光のリリープレート", /*description*/"純潔と威厳を司る天界の白百合に加\n護された白花の鎧。装着者を包む優\n雅な香りは一切の不浄を退け何事に\nも動じぬ凛とした気風を纏わせる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/511, /*sale_price*/32000, /*require_level*/111, /*hp*/0.0, /*atk*/92.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105311, /*equipment_name*/"蒼輝の鎧", /*description*/"青く透き通る結晶から作られた鎧。\nこれまで発見されたことのない結晶\nは、加工さえも困難な強度を誇り、\n表面を曇らせることさえできない。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/511, /*sale_price*/24000, /*require_level*/70, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105312, /*equipment_name*/"聖櫻の鎧", /*description*/"危機に瀕した祖国を救うため立ち上\nがった勇猛な乙女が身に着けていた\n鎧。不思議な加護により、あらゆる\n攻撃を弾く。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/401, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/11.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105313, /*equipment_name*/"煌金の王鎧", /*description*/"かつて繁栄を極めた古代王朝の遺跡\nから発掘された金色の鎧。数千年の\n時を経てもその神々しい輝きと、絶\n対的強度は失われることはない。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/587, /*sale_price*/30000, /*require_level*/85, /*hp*/50.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/1.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105314, /*equipment_name*/"烈風の俊鎧", /*description*/"天と地の境界に潜みし風の守護神が常用したとされる重鎧。纏いし者の周囲に鋭い烈風を巻き起こし、触れた者を慄かせる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/595, /*sale_price*/32000, /*require_level*/93, /*hp*/70.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105315, /*equipment_name*/"熾白銀の鏡鎧", /*description*/"神界の銀を鍛冶神の御業で鍛えた鏡\nの鎧。磨き抜かれた胸部の鏡はあら\nゆる魔法を捻じ曲げ吸収し、凄まじ\nい光の奔流に変え撃ち放つ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/652, /*sale_price*/32000, /*require_level*/99, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/13.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/50.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105316, /*equipment_name*/"極北天の覇焔鎧", /*description*/"北天を司る守護天使の加護を受けた\n真紅の鎧。罪を清める審判の炎で鍛\nえられており、守護天使の深き洞察\nと鋭き見識を恩恵に授かるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/605, /*sale_price*/32000, /*require_level*/105, /*hp*/75.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/16.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105317, /*equipment_name*/"覇王樹の棘針鎧", /*description*/"枯れた大地を万年耐え抜く覇王樹で\n作られた碧翠の鎧。いかなる渇きも\n撥ね退ける加護を持ち、終わらぬ戦\n火から永久に主人を守り抜く。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/580, /*sale_price*/32000, /*require_level*/111, /*hp*/210.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/15.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105341, /*equipment_name*/"イージスコート", /*description*/"生涯で一度の敗北もなかった女騎士\nが身に着けていた戦衣。ひとたび\n纏えば、如何なる戦局も覆す不倒の\n戦果を挙げることができるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/509, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105342, /*equipment_name*/"メイデンクロース", /*description*/"穢れを知らぬ乙女だけが身に着ける\nことができる神聖な服。乙女が無垢\nな祈りを捧げることにより発揮され\nる破邪の力は絶大。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/355, /*sale_price*/27000, /*require_level*/77, /*hp*/160.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105343, /*equipment_name*/"鬼焔の闘衣", /*description*/"炎鬼神の魔力を宿した戦装束。ひと\nたび身に着ければ守護の業炎を全身\nに纏い、降りかかる災いをことごと\nく滅却する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/405, /*sale_price*/30000, /*require_level*/85, /*hp*/0.0, /*atk*/65.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105344, /*equipment_name*/"極黒の冥衣", /*description*/"冥府の王に仕えし密偵の戦闘衣装。\n着衣者の殺傷能力を極限まで引き出\nし、新たな影の歴史を彩ることを強\nいるだろう。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/600, /*sale_price*/32000, /*require_level*/93, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105345, /*equipment_name*/"流蒼装アズールロンド", /*description*/"伝説の舞姫が着ていた衣装。身に着\nけることで体は水と同質に変化し、\n流水の如き変幻自在の動きで敵を翻\n弄する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/910, /*sale_price*/32000, /*require_level*/99, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105346, /*equipment_name*/"極東天の碧風装", /*description*/"東天を司る守護天使の加護を受けた\n深緑の装束。病を遠ざける治癒の風\nを纏っており、守護天使の健やかな\n慈愛と献身を恩恵に授かるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/500, /*sale_price*/32000, /*require_level*/105, /*hp*/0.0, /*atk*/62.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105347, /*equipment_name*/"白霞の純花装", /*description*/"白霞の花妖精が切なる祈りを込めた\n薄染めの聖衣。その幸福の加護と聖\n性は遍く災厄や穢れを寄せ付けず、\n着用者の心身に安寧をもたらす。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/471, /*sale_price*/32000, /*require_level*/111, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/5.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105371, /*equipment_name*/"純潔の巫女装束", /*description*/"神に全てを捧げた巫女が着ていた\n装束。着ている者は神の寵愛を一身\nに受け、魔に屈することのない護り\nの力を授けられるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/365, /*sale_price*/24000, /*require_level*/70, /*hp*/120.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105372, /*equipment_name*/"エンプレスローブ", /*description*/"かつて偉大な女帝が身に着けていた\nローブ。紡糸の一本一本に女帝の魔\n力が込められており、身に着けた者\nをあらゆる魔術から守る。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/43.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105373, /*equipment_name*/"エクゾマギアローブ", /*description*/"幾千もの魔術を操り、魔術王と呼ば\nれた者が纏ったローブ。纏うに値す\nる術者には、膨大な王の知識と魔力\nが授けられるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/440, /*sale_price*/30000, /*require_level*/85, /*hp*/0.0, /*atk*/0.0, /*magic_str*/48.0, /*def*/4.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105374, /*equipment_name*/"爽氷の天衣", /*description*/"氷上を統べし歴代の仙女が着ていた\nとされる神秘のローブ。ひとたび身\nに纏えば、天衣のオーラであらゆる\n衝撃を無効化する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/782, /*sale_price*/32000, /*require_level*/93, /*hp*/80.0, /*atk*/0.0, /*magic_str*/43.0, /*def*/6.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105375, /*equipment_name*/"星詠みの聖霊衣", /*description*/"星を詠み全てを見通した預言者が着\nていたローブ。袖を通せば星の軌道\nから万物を詠み解き、あらゆる厄災\nを事前に察知することができる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/99, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/5.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/13.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105376, /*equipment_name*/"極西天の聖導衣", /*description*/"西天を司る守護天使の加護を受けた\n月白の法衣。混迷の世界を導く光に\n満ちており、守護天使の清き祝福と\n啓示を恩恵に授かるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/507, /*sale_price*/32000, /*require_level*/105, /*hp*/0.0, /*atk*/0.0, /*magic_str*/52.0, /*def*/4.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/15.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105377, /*equipment_name*/"緋天のチューリップドレス", /*description*/"緋天に咲く牡丹百合の花弁で編んだ\n深紅のドレス。情熱を司る花の加護\nは、着用者に不撓不屈の精神を授け\nいかなる危局をも撥ね退ける。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/430, /*sale_price*/32000, /*require_level*/111, /*hp*/0.0, /*atk*/0.0, /*magic_str*/60.0, /*def*/6.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105401, /*equipment_name*/"星詠みの円盾", /*description*/"天体図が刻まれた円盾。\n天に浮かぶ星々の力が宿っており、\nどんな攻撃も受け流すその力は星の\n導きと呼ぶに相応しい。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/446, /*sale_price*/24000, /*require_level*/70, /*hp*/0.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105402, /*equipment_name*/"熾白銀の鏡盾", /*description*/"神界の銀と幾千もの魔術を組み合わ\nせた鏡の盾。傷ひとつ付かない曇り\nなき鏡面は、禁呪すら反射する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/726, /*sale_price*/32000, /*require_level*/99, /*hp*/80.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/14.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105403, /*equipment_name*/"聖円環ヘブンリーロータス", /*description*/"混沌の世界に清浄をもたらす聖蓮華\nの円盾。破邪の祈念が込められた紋\n様は、時にその大輪の花弁を賭して\nまでも己が主の窮地を護るという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/111, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/17.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105431, /*equipment_name*/"インファナルグリーブ", /*description*/"覇道を征った戦姫が敵の刃に倒れる\nまで身に着けていたとされる軍靴。\nあと一歩世界に手が届かなかった無\n念が絶大な魔力として宿っている。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/77, /*hp*/60.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/6.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105432, /*equipment_name*/"天翔の金靴", /*description*/"天使の羽根で作られた黄金に輝く羽\n靴。扱いには熟練を要するが、ひと\nたび足に纏えば自在に天翔けること\nができる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/560, /*sale_price*/32000, /*require_level*/93, /*hp*/0.0, /*atk*/70.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105433, /*equipment_name*/"極翼天の聖靴", /*description*/"天空の守護天使たちの加護を賜りし\n戦靴。四方の天使に授かった祝福の\n羽根をあしらうことで、万象の呪詛\nと苦難を退ける無比の聖性を宿す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/620, /*sale_price*/32000, /*require_level*/105, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/18.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105462, /*equipment_name*/"ブラッドルビーヒール", /*description*/"享楽的な人格と妖艶な美貌で、いく\nつもの国を混乱に陥れた魔女が愛用\nした靴。履いて歩くだけで幻影が発\n生し、見る者を惑い狂わせる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/510, /*sale_price*/30000, /*require_level*/85, /*hp*/60.0, /*atk*/0.0, /*magic_str*/24.0, /*def*/6.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/4.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105463, /*equipment_name*/"熾白銀の鏡脚", /*description*/"神界の銀と錬金術の粋を集め作られ\nた鏡の脚具。歩を刻むごとに実体を\n持った鏡像を生み出し、無尽の軍勢\nを形成して敵を威圧する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/881, /*sale_price*/32000, /*require_level*/99, /*hp*/70.0, /*atk*/0.0, /*magic_str*/26.0, /*def*/7.0, /*magic_def*/11.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105464, /*equipment_name*/"プライドオブローゼス", /*description*/"薔薇王女と謳われた姫君が愛用した\nドレスヒール。ひとたび履けば妖艶\nなる趣で、たとえ愛の天使や神であ\nっても魅了し堕落せしめたという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/580, /*sale_price*/32000, /*require_level*/111, /*hp*/0.0, /*atk*/0.0, /*magic_str*/28.0, /*def*/9.0, /*magic_def*/14.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105491, /*equipment_name*/"ロイヤルガードハット", /*description*/"命を賭して祖国を救った伝説の親衛\n隊長が身に着けていた瀟洒な帽子。\n大きな羽飾りは装備者のみならず目\nにした者の勇気を奮い立たせる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/305, /*sale_price*/30000, /*require_level*/85, /*hp*/0.0, /*atk*/60.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105521, /*equipment_name*/"忘哭の冠", /*description*/"過去に滅び、忘れ去られた国の王女\nが身に着けていた冠。次の持ち主に\nは、深い哀しみを伝える代わりに、\n膨大な魔力を授ける。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/0.0, /*magic_str*/48.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105522, /*equipment_name*/"白祈の聖冠", /*description*/"民草から絶大な尊敬を集めていた聖\n女に贈られた冠。心清らかな者が身\nに着ければ、邪な者を遠ざける力を\n発揮する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/22.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/12.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105523, /*equipment_name*/"プロスペリティベール", /*description*/"人類にその身を捧げた聖女が愛用し\nた繁栄のベール。この聖遺物を手に\nした者は、僥倖と災厄のすべてを見\n定めることができるとされる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/830, /*sale_price*/32000, /*require_level*/93, /*hp*/0.0, /*atk*/0.0, /*magic_str*/23.0, /*def*/8.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105524, /*equipment_name*/"極翼天の賢聖帽", /*description*/"大地の守護天使たちの加護を賜りし\n聖帽。真の求道者へのみ授けられ、\n身に着ける者は天上の理である奇蹟\nに触れることが叶うとされる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/32000, /*require_level*/105, /*hp*/0.0, /*atk*/0.0, /*magic_str*/36.0, /*def*/7.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105551, /*equipment_name*/"ミレニアムイヤリング", /*description*/"数千年も昔の地層から発掘された魔\n石の耳飾り。膨大な自然の力を蓄え\nており、身に着けた者に大いなる大\n地の加護を与える。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/70.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105552, /*equipment_name*/"赫怒竜のペンダント", /*description*/"竜の怒りを封じ込めたと伝承される\n神秘のペンダント。憤怒の化身とし\nて、身につけた者に山を震わせ大地\nを砕く力を与える。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/13.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105553, /*equipment_name*/"闘神の荒腕", /*description*/"闘神の荒ぶる魂を宿した腕輪。大気\n中の魔力を力に変換し、山を砕くほ\nどの膂力を与えるが、使うに値する\n武力が不可欠。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/82, /*hp*/0.0, /*atk*/102.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/24.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105554, /*equipment_name*/"花簪焔火牡丹", /*description*/"陰謀渦巻く戦国時代を生き抜いた伝\n説のくノ一が身に着けていた簪。\n簪に掛けられた魔法は、どんな華奢\nな身にも鬼神の如き力を与える。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/90, /*hp*/0.0, /*atk*/104.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/28.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105555, /*equipment_name*/"緋竜の爪火輪", /*description*/"闇の世界の伝説的暗殺者が愛用した\n指輪型の暗器。火竜の爪を模した棘\nには業火が宿り、一刺しで骨も残さ\nず焼き尽くす。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/96, /*hp*/0.0, /*atk*/98.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/29.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105556, /*equipment_name*/"ルビーローズチョーカー", /*description*/"闘いの女神が唯一寵愛したとされる\n真紅の薔薇を象った首飾り。装着者\nの闘争心に呼応し紅き輝きと共に女\n神の加護をもたらす。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/102, /*hp*/50.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/29.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105557, /*equipment_name*/"紅鳥のクラリオンブローチ", /*description*/"戦勝をもたらすラッパのブローチ。\n象られた霊鳥は戦場に赴くと生きて\nいるかのように飛び回り、軽快なリ\nズムを奏で、装着者を勝利に導く。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/108, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/38.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105581, /*equipment_name*/"精霊王の護石", /*description*/"精霊王に祝福された耳飾り。\n森一つ覆いつくすほどの守護の力は\nありとあらゆる災厄を寄せ付けない\nという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/200.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105582, /*equipment_name*/"人魚姫の霊涙", /*description*/"結晶化した人魚姫の落涙を宝玉とし\nてあしらった首飾り。\n永遠へ紡がれる想いは青い輝きを放\nち続け、あらゆる呪詛を拒絶する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/60.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/2.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105583, /*equipment_name*/"英盾王の腕輪", /*description*/"戦乱から国を護りぬき、英盾王と呼\nばれた君主愛用の腕輪。周囲の魔力\nを吸収して守護の力を展開し、絶対\n的防御で同胞の盾となる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/82, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105584, /*equipment_name*/"海龍神の髪飾り", /*description*/"母なる大海を司る神のウロコから生\nまれ出た髪留め。身に着けた者は大\n海の加護を受け、如何なる攻撃をも\n受け止める。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/90, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105585, /*equipment_name*/"神盾輪ウォールブレス", /*description*/"盾の女神の加護を宿した指輪。指を\n通せば万里に渡る魔力の巨盾を展開\nし、降りかかる苦痛、邪悪、厄災を\n断つ。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/96, /*hp*/50.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/14.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105586, /*equipment_name*/"エルフィンチョーカー", /*description*/"天空の花園にのみ生息するという霊\n蝶の翅を魔術的に再現した首飾り。\nその羽ばたきは因果を歪め致命の一\n撃すらも静かに受け止める。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/102, /*hp*/60.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/11.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/3.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105587, /*equipment_name*/"翆奏のハープブローチ", /*description*/"聖樹の力を宿すと言い伝えられる、\nハープを象ったブローチ。緑豊かな\n天界の庭園を思わせる癒しに満ちた\n音色は、いかなる厄災をも退ける。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/108, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/13.0, /*magic_def*/13.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105611, /*equipment_name*/"海神の耳飾り", /*description*/"海神が持つとされる槍をかたどった\n耳飾り。海のように深い色合いをし\nた碧い宝玉は、荒れ狂う海を鎮める\nほどの魔力を授けるという。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/0.0, /*atk*/0.0, /*magic_str*/78.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/3.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105612, /*equipment_name*/"聖獣の祈念", /*description*/"穢れなき聖獣、ユニコーンの加護を\n宿した首飾り。\n角に宿りし聖なる力は装備者の魔力\nを高め、霊妙な才覚を授ける。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/140.0, /*atk*/0.0, /*magic_str*/84.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105613, /*equipment_name*/"ソフォスブレスレット", /*description*/"叡智を極め、神の領域に到達した賢\n人が遺した装飾品。秀でた知恵者に\nのみ装着を許され、周囲の魔力を奪\nい無限に供給する。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/82, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/14.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105614, /*equipment_name*/"細氷姫の結び紐", /*description*/"氷の心を持った姫君が愛を知り、自\n然と零れ出た涙が結晶化したものを\nあしらったリボン。身に着けると氷\n雪を自在に操れるようになる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/90, /*hp*/30.0, /*atk*/0.0, /*magic_str*/98.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/16.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105615, /*equipment_name*/"深結晶ゼノクリスタル", /*description*/"太古の地層より出土した、膨大な魔\n力の結晶体を用いた指輪。魔法を何\n百倍にも増幅させるが扱いが困難な\nため、優れた術者しか扱えない。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/96, /*hp*/0.0, /*atk*/0.0, /*magic_str*/101.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/20.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105616, /*equipment_name*/"ケイオティックチョーカー", /*description*/"混沌の神が地上へもたらしたとされ\nる魔装具。宝石内部に描かれた緻密\nな立体魔法陣により異界からの魔力\nを無尽蔵に取り出せる。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/102, /*hp*/0.0, /*atk*/0.0, /*magic_str*/103.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/15.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105617, /*equipment_name*/"蒼夜のノクターンブローチ", /*description*/"蒼き夜の輝きに満ちたブローチ。優\n美なる夜想の旋律は邪念を祓い、精\n神を純化させることで、身につけた\n者の更なる潜在能力を引き出す。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/480, /*sale_price*/32000, /*require_level*/108, /*hp*/0.0, /*atk*/0.0, /*magic_str*/105.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/25.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106011, /*equipment_name*/"輝光剣アダマス", /*description*/"原初の金剛石を削り鍛えられたと伝\n説に伝わる宝石剣。幾星霜の時を経\nても変わらぬ光を放ち続け、永世折\nれずにその輝きで魔を祓う。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/155.0, /*atk*/191.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/21.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106041, /*equipment_name*/"紅玉刀スカーレットムーン", /*description*/"紅玉の結晶を研ぎ作られた魔性を宿\nす妖刀。赤い三日月状の刀身は血に\n飢えており、生命を持つ全ての生物\nを斬り裂く抜群の切れ味を誇る。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/206.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/26.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106071, /*equipment_name*/"翠風刃エメラルドエッジ", /*description*/"風を司る妖精王の力を宿した翠玉の\n宝石剣。風の妖精たちを従えられる\n権能を持ち、彼らが巻き起こす疾風\nはあらゆる邪悪を吹き飛ばす。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/720, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/198.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/42.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106101, /*equipment_name*/"オパルスパージフィスト", /*description*/"神の力を宿すという宝石、希望を司\nるオパールがあしらわれた拳具。希\n望の光を放つ拳は、あらゆる魔を葬\nり、人々を絶望から救うという。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/174.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/30.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106131, /*equipment_name*/"蒼神弓マーシーサピロス", /*description*/"神が人に授けた霊石、サファイアで\n作られし神弓。慈愛を司るとされ、\n放たれる蒼き輝きを纏いし矢は人々\nを邪へと堕とす魔神をも退ける。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/880, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/144.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106161, /*equipment_name*/"クリスタロススパイク", /*description*/"神の力が結晶化した水晶の槍。神聖\nなる力を宿しており、聖光を放つ一\n撃は不死の王をも消滅させたことか\nら、浄化を司る槍とも呼ばれる。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/520, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/199.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106191, /*equipment_name*/"黄銅の裂戦斧", /*description*/"英雄譚に名を刻む豪胆な戦神が生涯\nの相棒とした大戦斧。黄金に似て非\nなる雷光の輝きは、畏怖の象徴とし\nて戦場にその名を轟かせたという。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/720, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/219.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106221, /*equipment_name*/"黒真珠の闇獄杖", /*description*/"冥府の黒き女王が死者の霊魂を導く\nために用いたとされる杖。黒き宝珠\nから吹き荒れる冥界の風は禁呪や神\nの奇跡でさえも等しく虚無へ帰す。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/0.0, /*atk*/0.0, /*magic_str*/190.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/29.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/106251, /*equipment_name*/"白真珠の光天杖", /*description*/"天上の白き女神が生命の始まりを祝\n福して授けたとされる杖。宝珠に満\nちる聖光は万病を癒し、悪鬼邪神の\n呪いすら打ち払うとされる。", /*promotion_level*/6, /*craft_flg*/1, /*equipment_enhance_point*/680, /*sale_price*/34000, /*require_level*/112, /*hp*/345.0, /*atk*/0.0, /*magic_str*/145.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0, /*display_item*/0, /*item_type*/0);
