INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1001, /*glossary_category_id*/1, /*title*/"アクドーイ商会", /*description*/"悪人「アクドーイ」を会長とする、悪徳商会。高利貸し、人さらい、違法な武器の密輸入、悪いことはなんでもやる。アクドーイは丁寧な口調だが極悪人である。本当の悪人とはしばしばそういうものなのである。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/1);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1002, /*glossary_category_id*/1, /*title*/"アストライア大陸", /*description*/"世界最大の大陸。大陸の中心には巨大都市・ランドソルがあり、その上空にはソルの塔が天高くそびえ立っている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/2);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1003, /*glossary_category_id*/1, /*title*/"アストルム", /*description*/"この世界全体を示す単語。アストライア大陸の語源ともなっている。ただ、この「アストルム」の語源は定かになっていない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/3);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1004, /*glossary_category_id*/1, /*title*/"アメス", /*description*/"主人公の夢に現れる謎の少女。主人公の過去を知っているような口ぶりで話す。シニカルな口調と、破損した翼が特徴。コッコロはアメスを神とあがめ、さまざまな託宣を受けている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/4);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1005, /*glossary_category_id*/1, /*title*/"歩く竜巻", /*description*/"シュテッヒパルムの二つ名。文字通り街を歩いているだけでも次から次へ竜巻のように何かを起こす彼女を、そう呼ぶ者が自然と増えていった。竜巻という単語が気に入ったらしく、二つ名で呼ぶと楽しげに名乗ってくれる。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/5);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1006, /*glossary_category_id*/1, /*title*/"アンネローゼ・フォン・シュテッヒパルム", /*description*/"アンナの本名。どんな相手にもとりあえず名乗るのがアンナのポリシー。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/6);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1007, /*glossary_category_id*/1, /*title*/"魔瞳（イーヴィル・アイ）", /*description*/"この私、疾風の冥姫は左目に大量の魔力を溜め込むことが出来るぞ！　極めて強力な魔法を使う際に、我が瞳はこの世ならざる輝きを放つのだ！", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/8);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1008, /*glossary_category_id*/1, /*title*/"ヴァイスフリューゲル", /*description*/"モニカが祖国で所属している部隊の名前。ヴァイス＝白、フリューゲル＝翼という意味を持つ。この名前を広く知らしめるためにモニカは日々活動している。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/9);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1009, /*glossary_category_id*/1, /*title*/"ヴァイスフリューゲル　ランドソル支部", /*description*/"モニカをギルドマスターとして結成されたギルド。メンバーはモニカ、ニノン、ユキ、クウカ、アユミの５人。結成「された」というよりも、うっかり結成「されてしまった」と言うニュアンスの方が正しいかもしれない。メンバーはいつも間の抜けた行動でピンチを呼び込んでは、自分たちで華麗に解決している。そのため、このギルドにはポンコツしかいないのか、潜在能力は高いのか、まだ判然としていない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/10);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1010, /*glossary_category_id*/1, /*title*/"ウィスタリア家", /*description*/"アストライア大陸全土に名をはせる、ランドソル一の商家と言われている名家。中でも、数々のパワフルな逸話によりお嬢様「アキノ・ウィスタリア」の名前は特にランドソルに広く知れ渡っている。いい意味でも、悪い意味でも。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/11);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1011, /*glossary_category_id*/1, /*title*/"ヴェリオ火山", /*description*/"アストライア大陸で最も巨大な火山。現在も頻繁に噴火を繰り返しており、加えて魔物も数多く生息している危険な地域。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/12);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1012, /*glossary_category_id*/1, /*title*/"エステレラ街道", /*description*/"アストライア大陸に存在する緑の多い街道。気候のよい土地柄のため、生息する植物、動物、昆虫の種類も多い。歩いているとさまざまな生き物と出会うことができる。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/13);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1013, /*glossary_category_id*/1, /*title*/"深淵悠久＝翠翔裂空斬（エターナルアビス＝ディヴァイダー）", /*description*/"剣に宿した魔力を斬撃として解き放つ魔法。高密度に圧縮された魔力はすさまじい威力を誇り、触れるだけでひとたまりもない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/14);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1014, /*glossary_category_id*/1, /*title*/"エルフ族", /*description*/"アストルムに住む四種族の一つ。尖った長い耳が外見的特徴。一般的に他種族よりも知性が高く、魔法を使えるものも多い。自然を愛し、森で暮らす者が多いが、近年ではランドソルに定住するものも増えてきている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/16);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1015, /*glossary_category_id*/1, /*title*/"エルフの森", /*description*/"アストライア大陸に存在する自然豊かな森。大陸にいるエルフの中には、この地で生まれた者が多く存在し、生まれたときから植物や動物などのあらゆる生命と触れ合ってきた。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/17);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1016, /*glossary_category_id*/1, /*title*/"縁結びの花", /*description*/"ヴェリオ火山でわずかに生息している植物の中でも、最も珍しい種類の一つ。その希少性ゆえに「意中の相手に渡すと恋愛が成就する」といううわさがある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/18);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1017, /*glossary_category_id*/1, /*title*/"オラル高山", /*description*/"アストライア大陸に存在する険しい高山。ヴェリオ火山へと続く山道があるが、その道は極めて過酷で人を寄せ付けない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/19);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1018, /*glossary_category_id*/1, /*title*/"音響盤", /*description*/"音声を記録した、特殊な魔石。魔力を通すと記録された音声が再生される仕組みで、円盤状に加工されたものが流通している。大きさに比例して再生できる時間が長くなる。以前は長時間音声を聞くには魔力を通し続ける者が必要だったので、貴族や富豪しか持っていなかった。しかし数十年前に、魔力を貯められる石から音響盤に魔力が流れる仕組みを搭載した再生機器が生み出されてからは、爆発的に普及していった。今では装置が安価になり、誰でも手軽に音声を楽しめるようになっている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/20);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1019, /*glossary_category_id*/1, /*title*/"自警団（カォン）", /*description*/"【動物苑】の下部組織。同じ【動物苑】の仲間を、主に魔物の脅威から守る武闘派ギルド。「シャドウ」などの、異変の調査や解決もする。【王宮騎士団（NIGHTMARE）】とは犬猿の仲で、常に対立しており、小競り合いが絶えない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/21);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1020, /*glossary_category_id*/1, /*title*/"カジノ", /*description*/"ランドソル内にある娯楽施設。ルーレットや、カードゲームなどで遊ぶことができる。暇つぶしから一攫千金まで……人々の希望と絶望で日々にぎわっている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/22);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1021, /*glossary_category_id*/1, /*title*/"カルミナ", /*description*/"アイドルとしてソロで活動していたノゾミがギルドマスターのアイドルギルド。歌って踊って（悲しい気持ちと）戦うギルドで、ライブなどの興行が主な活動内容。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/23);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1022, /*glossary_category_id*/1, /*title*/"盟約外典術式第666章（ギアスアポクリフォ・タイラントコード）", /*description*/"かつて神によって世界の裏側に封じられた、この世の法則を操る術が記された書物。シュテッヒパルムはその中に記された術式の一節を魂に刻み、魔瞳に貯めた魔力が溢れ出ないようにしている。大規模な魔法を用いるときのみ、その術式を用いて魔力を全身へ奏填する。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/24);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1023, /*glossary_category_id*/1, /*title*/"ギルド", /*description*/"特定の目的を持って活動する人たちの集合体。ランドソルでは、国民に「ギルド」に所属することが義務付けられている。所属していることで受けられる公共サービスもあり、逆に未加入の人間は社会的信用が得られないなどのデメリットも多い。ギルドの目的は多種多様で、商業、軍事、文化事業など、ランドソルのさまざまな産業を支えている。また、一人で複数のギルドに加入することも可能である。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/25);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1024, /*glossary_category_id*/1, /*title*/"ギルド管理協会", /*description*/"ランドソルのギルドを管理する公的機関。ギルドは管理協会によって厳しく管理されており、活動実態のないものは解体処分される。ただし、犯罪集団のように反社会的な非公認ギルドも存在する。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/26);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1025, /*glossary_category_id*/1, /*title*/"ギルドハウス", /*description*/"ギルドを結成したランドソル国民たちに与えられる活動拠点。ギルドの活動規模、功績に応じて施設の拡充や建て替えが可能。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/27);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1026, /*glossary_category_id*/1, /*title*/"クリスタルマウンテン", /*description*/"かつては水晶の発掘により栄えた土地であったが、現在は見る影もなく当時を思わす古城が一つ、山の中にそびえたっている。その歴史を知る者は少ない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/28);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1027, /*glossary_category_id*/1, /*title*/"効率厨", /*description*/"効率最優先で動く人のこと。要はミフユのような人間のこと。ゲームのような遊戯があれば、攻略本を手元で読みながら攻略するタイプ。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/30);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1028, /*glossary_category_id*/1, /*title*/"ゴーレム", /*description*/"魔法使いが創造する疑似生命体。創造主の命令を忠実に実行する。泥土や鉱石など、さまざまな物質で生成され、サイズや能力も多種多様である。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/31);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1029, /*glossary_category_id*/1, /*title*/"蒐集家（コレクター）", /*description*/"魔法などに関する書物だけでなく、小説などの文芸書や面白そうな古代の遺産など、さまざまな物を集めて各地を渡り歩くナナカにつけられた二つ名。本人はまだその名に相応しくないと思っている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/32);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1030, /*glossary_category_id*/1, /*title*/"サラマンダーの血", /*description*/"火山に住む魔物・サラマンダーの血液で、高い溶解能力を持つ。腐食能力が高く、人体にとって極めて有害。サラマンダー自身も己の血液で腐食され、死に至るらしい。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/33);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1031, /*glossary_category_id*/1, /*title*/"サレンディア救護院", /*description*/"もともと【王宮騎士団（NIGHTMARE）】の副団長だったサレンが統括、経営するギルド。孤児院であるが、手広く商売なども行っている。王宮の警護で暇を持て余す毎日に、価値を見いだせずにいたサレンが、人の役に立つことをしたいと思い運営を始めた。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/34);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1032, /*glossary_category_id*/1, /*title*/"熾炎戦鬼煉獄血盟暗黒団(ジ・オーダー・オブ・ゲヘナ・イモータルズ)", /*description*/"シュテッヒパルムが前世において所属していた暗黒騎士団。「暗黒騎士たちはかつて悪に墜ちていた。しかし世界を覆う闇の存在、《星の裁定者》を滅するために、同じ闇の力を使って彼らに抗うと決意した。やがて星の裁定者との戦いの中で、暗黒騎士たちは全員がその命を散らした……」（『冥風戦記』概要より抜粋）", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/35);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1033, /*glossary_category_id*/1, /*title*/"シャドウ", /*description*/"人の姿をした特殊な魔物。人語を発するが、それは文脈のない単語の羅列で、知能はないと推測されている。ランドソルで急激に目撃される頻度が増え、問題視されている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/36);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1034, /*glossary_category_id*/1, /*title*/"ソルオーブ", /*description*/"ソルの塔の頂上へたどり着くために必要だといわれている半透明の球体。現在ではその伝承を信じている者は少なく、おとぎ話の中の存在とされている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/37);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1035, /*glossary_category_id*/1, /*title*/"ソルの塔", /*description*/"ランドソルの中心にそびえ立つ巨塔。アストライア大陸における最大の建造物であり、最大の謎でもある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/38);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1036, /*glossary_category_id*/1, /*title*/"あんパン", /*description*/"リンの大好物。時には給料のほとんどをあんパンにつぎ込むこともある。こしあんとつぶあんがあり、各派閥の戦いは長年続いている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/7);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/1037, /*glossary_category_id*/1, /*title*/"ゲーム", /*description*/"近年になって作られた玩具。ハードという魔法道具に、特殊な鉱石を使用したソフトという記憶媒体をセットすることで遊ぶことができる。高度な技術で作られているため、値段は非常に高価。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/29);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2001, /*glossary_category_id*/2, /*title*/"だいじょぶマイフレンドくん１号", /*description*/"アオイがエルフの森に落ちているブリキを拾い集めて作った人形。アオイの友達作りの練習台として、日々話し相手になっている。通称「マイフレンドくん」。マイフレンドくんとの会話は、基本的にアオイが一方的に話すだけだが、アオイの想像の中では会話が完璧に成立している。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/39);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2002, /*glossary_category_id*/2, /*title*/"黄昏の都", /*description*/"この世界のどこかにあると言い伝えられている場所。いにしえに栄えたといわれる都市で、さまざまな技術が発達していたと語り継がれている。黄昏の都の伝説は幾つもあり、「決して折れない武器を作る刀鍛冶」、「星を行き交う飛空艇」などが有名。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/40);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2003, /*glossary_category_id*/2, /*title*/"超能力", /*description*/"通常の人間にはできないことを実現できる超自然で特殊な身体能力。系統的な学習や、呪文の詠唱が必要ないことが、魔法とは異なる。現在まで、超能力を持った人間は確認されていない。しかし空中を浮遊する人間の目撃情報があり、魔法か超能力かと議論されている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/41);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2004, /*glossary_category_id*/2, /*title*/"通信魔法", /*description*/"アストライア大陸で使用される、遠距離伝達用の魔法。公的機関、または私設団体に所属する魔術師が、インフラ整備された魔法網で通信するものと、個人の魔術で行うものの二種類がある。いずれも高位魔法であることは変わらないため、精度は高くなく、障害も起きやすい。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/42);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2005, /*glossary_category_id*/2, /*title*/"悪魔偽王国軍（ディアボロス）", /*description*/"イリヤ・オーンスタインが魔族の少女たちと結成したギルド。その名前はかつてイリヤが率い、神々を相手に闘争を繰り広げた、魔族の最強軍と同じである。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/43);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2007, /*glossary_category_id*/2, /*title*/"壊し屋（デストロイヤー）", /*description*/"時折用心棒として腕を振るうことがあるエリコに付けられた二つ名。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/44);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2008, /*glossary_category_id*/2, /*title*/"伝説の吸血鬼", /*description*/"太古の昔、魔族を率いて神々と闘ったという魔族の女王。魔族の間では有名なおとぎ話だが、現実に存在したと考える者も少なくない。「伝説の大魔王」「伝説の魔女」などとも呼ばれる。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/45);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2009, /*glossary_category_id*/2, /*title*/"トゥインクルウィッシュ", /*description*/"ヒヨリ、ユイ、レイの３人によって結成された人助けギルド。ギルドマスターはヒヨリで、困った人を助けるための仕事が主な活動内容になっている。しかし本当の目的は、これまで同じ夢を見てきた３人がともに願う「ソルの塔の頂上への到達」である。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/46);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2010, /*glossary_category_id*/2, /*title*/"洞窟", /*description*/"アストライア大陸の地中に生まれた洞穴。魔物の巣になっている場合も多いため、一般には危険区域とみなされている。また、違法な商取引や、盗賊などの隠れ家となることもある。死後の世界への入口と見なされ、墓地として利用されていた例もある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/47);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2011, /*glossary_category_id*/2, /*title*/"動物苑", /*description*/"獣人（ビースト）の互助組織ギルド。ランドソルで暮らす獣人（ビースト）はほとんどが【動物苑】に所属し、支援を受けている。【プリンセスナイト】と匹敵する大規模ギルドだが、おのおのの参加意識が希薄なため、ギルドの全体像は誰にも把握できていない。無数のギルドの連合体で、【自警団（カォン）】【牧場（エリザベスパーク）】などの下部組織がある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/48);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2012, /*glossary_category_id*/2, /*title*/"トーゴク", /*description*/"ニノンが憧れている、ランドソルから遥か東の方にあるという国。ニンジャ、サムライ、ゲイシャ、フジヤマ……など数々のエキゾチックなものがあるといわれている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/49);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2013, /*glossary_category_id*/2, /*title*/"トワイライトキャラバン", /*description*/"黄昏の都を探す者たちが情報収集のために集まったギルド。ギルドマスターはルカ。ルカ以外のメンバーは二つ名で呼ばれるほど有名で、結成されてからはランドソルで最も危険なギルドが誕生したともうわさされている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/50);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2014, /*glossary_category_id*/2, /*title*/"王宮騎士団（NIGHTMARE）", /*description*/"【プリンセスナイト】の本隊というべき騎士団ギルド。ギルドマスターは「団長」と呼称される。いわばランドソルの国軍であり、「王宮」および王族の護衛、および勅命を受けての任務達成が活動目的。同時に、街の治安を守る警察組織としての側面も強い。王侯貴族が要職に就くことが伝統となっており、出自が市民階級の騎士は王宮内に入れないなど制約も多い。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/52);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2015, /*glossary_category_id*/2, /*title*/"馬車", /*description*/"アストライア大陸での主な交通手段。飛空艇のような、高度な魔法移動装置も存在するが、非常に高価であるため、陸路では主に馬車が使用される。ランドソルと外部集落を結ぶ、個人所有の定期便も多く存在する。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/53);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2016, /*glossary_category_id*/2, /*title*/"獣人（ビースト）族", /*description*/"アストルムに住む四種族の一つ。ヒューマン族に次いで人口が多い。さまざまな獣の特徴を併せ持ち、一般的に他種族よりも身体能力が高い。外見的特徴としては獣の耳と尻尾がある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/54);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2017, /*glossary_category_id*/2, /*title*/"美食殿", /*description*/"ギルド所属が条件の飲食店などに入るためにペコリーヌが結成したギルド。目的は食べ歩き。出来たばかりで構成人員も少ない弱小ギルド。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/56);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2018, /*glossary_category_id*/2, /*title*/"ヒューマン族", /*description*/"アストルムに住む四種族の一つ。最も人口が多い種族である。ランドソルの施政はヒューマンの貴族を中心に行われているため、ヒューマンであることに特権意識を持つものもおり、他種族への迫害につながった事例もある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/57);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2019, /*glossary_category_id*/2, /*title*/"ファントムキャッツ", /*description*/"最近ランドソルで話題の、謎の美少女義賊。がめつい悪人の家に侵入しては、貧しい庶民に盗んだお宝を配って回る。その正体は謎に包まれているが、タマキいわく「ファ、ファントムキャッツの正体？　し、知らないにゃ。でも、とっても可愛くて猫を愛する義賊だって聞いたにゃ。これからも、ファントムキャッツをよろしくにゃ！」とのこと。ファントムキャッツ……一体何者なんだ……", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/58);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2020, /*glossary_category_id*/2, /*title*/"フォレスティエ", /*description*/"エルフ族の住む森を管理、守護している歴史の長いギルド。現在のギルドマスターはミサト。エルフの森でしか取れない薬草などの交易品の管理や、他の種族が森へ立ち入る際のガイドと、活動内容は多岐に渡っている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/59);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2021, /*glossary_category_id*/2, /*title*/"プラノ平原", /*description*/"アストライア大陸に広がる平原。街道から外れ、人の手が入っていない自然な土地のため、魔物も多く生息している。街から近いこともあり、腕試しに訪れる武芸者は後を絶たない。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/60);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2022, /*glossary_category_id*/2, /*title*/"ブリザ渓谷", /*description*/"アストライア大陸に存在する渓谷。そよ風の吹く特殊な地形をしており、訪れる冒険者たちを心地よい風が迎える。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/61);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2023, /*glossary_category_id*/2, /*title*/"プリンセスナイト", /*description*/"ランドソルを統治する「王宮」直属のギルド。軍事、郵便、警察、消防など公的事業を担うギルドの総称が【プリンセスナイト】であり、いくつもの下部組織がある。王族、特に君主である姫（プリンセス）を守護する騎士（ナイト）であるため、この名が付いたと言われている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/62);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2024, /*glossary_category_id*/2, /*title*/"ＢＢ団", /*description*/"正式名称は「バイバイぼっち団」。アオイと主人公が友達を作る方法を考えるために二人で結成したチーム。活動内容はさまざまであるが、総じて「友達とはなにか」という哲学にも似た深遠な議題を探究している。そのさまはある意味、知的集団と言えなくもない。やっていることはともかく。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/55);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2025, /*glossary_category_id*/2, /*title*/"防犯魔石", /*description*/"危険な状況になった際にスイッチを押すと、大音量の警告音を発する護身アイテム。主に子供が外出時に携帯する。その効果はさまざまで、騒音を鳴らす程度のものから、超音波を発生させ相手の聴力を破壊するものまである。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/63);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2026, /*glossary_category_id*/1, /*title*/"牧場（エリザベスパーク）", /*description*/"山の上にある牧場のギルド。活動内容は、いわゆる牧場で、牛や馬などの家畜を飼育し、畜産業を営んでいるが、獣人（ビースト）たちが休暇を過ごすピクニック場や病人の静養地としての側面もある。普段はあまり町の事情に関わらないが、牛乳や肉などの取引があるため商人などの出入りは活発である。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/15);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/2027, /*glossary_category_id*/2, /*title*/"ドングリ", /*description*/"あんパンに次ぐリンの好物で、品種によって味はさまざま。リン曰く、どこでもつまめるように常に持ち歩いているという。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/51);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3001, /*glossary_category_id*/3, /*title*/"魔族", /*description*/"アストルムに住む四種族の一つで、もっとも人口が少ない。二本の角を頭部に持つのが外見的特徴。魔力、知力、運動、感覚など、特定の能力に秀でた能力を持っているものが多い。かつては神々と世界の覇権を争ったという伝説もある、高潔な一族。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/64);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3002, /*glossary_category_id*/3, /*title*/"魔法具", /*description*/"制作過程に魔法を加えることで、その効果を恒常的に発揮出来る道具。衝撃吸収の魔法を加えた防具や、腕力を上昇させる腕輪が流通している。なお魔法の効果は、本来のものよりも低くなってしまう。魔法に疎くても魔力があれば使えることもあって、魔族やヒューマン族に人気。稀に副作用が起きる場合もあるため、使用には注意が必要。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/65);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3003, /*glossary_category_id*/3, /*title*/"魔法少女ベルルちゃん", /*description*/"ランドソルに伝わる児童文学。かつて存在した伝説の英雄をモデルにした物語で、大衆演劇の演目とされることも多い。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/66);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3004, /*glossary_category_id*/3, /*title*/"魔物", /*description*/"アストライア大陸に生息する謎の生き物。高い凶暴性を持ち、人類や動物達の生活を脅かしている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/67);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3005, /*glossary_category_id*/3, /*title*/"魔物討伐", /*description*/"凶暴で人々の生活を脅かす魔物を排除するための、駆除、討伐行為。その際に採取される肉や、皮革・油脂・骨・角・羽毛などは、生活品としても利用されており、狩猟自体を生業とするものもいる。反面、時折発見される凶暴性のない魔物は、変異体として、調査のために保護対象となっているものもいる。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/68);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3006, /*glossary_category_id*/3, /*title*/"魔除けのアミュレット", /*description*/"霊や魔物、災厄などから所持者を守る護符。神の癒しを受けた聖石などのマジックアイテムが使われる。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/69);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3007, /*glossary_category_id*/3, /*title*/"マルジェン海岸", /*description*/"アストライア大陸の海岸。近隣には小さな街があり、時折催される祭りは夜の街を夜光花が美しく照らす。海に魔物が出現することもあるため、魔物退治の仕事を請け負った武芸者たちが訪れることがある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/70);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3008, /*glossary_category_id*/3, /*title*/"メルクリウス財団", /*description*/"アキノが大商人である父から言われた、「一人前の商人になれ」という目標を達成するために結成されたギルド。莫大な資金を元手にさまざまな事業への融資や買収を行っている。義賊として活動しているタマキとともに、悪どい稼ぎ方をしている商売人たちをこらしめるのが裏の活動目的。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/71);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3009, /*glossary_category_id*/3, /*title*/"幽霊", /*description*/"人間や動物が死んだ後、成仏できずに非物質的な精神体となった存在。実在するかどうかはランドソル内でも議論されているが決着を見ない。最近ではプリンなどを販売する洋菓子店で目撃情報が多いが、シミュラクラ現象であるという意見もある。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/72);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3010, /*glossary_category_id*/3, /*title*/"ラビリンス", /*description*/"謎の人物・ラビリスタが結成した、世界の謎を探究するギルド。信頼できるごく少数の人数で構成されている。表向きはクレープ屋を経営して、平凡なギルドを装っている、いわば秘密結社。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/73);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3011, /*glossary_category_id*/3, /*title*/"ランドソル", /*description*/"アストライア大陸最大の国家。また、その首都である城塞都市。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/74);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3012, /*glossary_category_id*/3, /*title*/"ランドソル王家", /*description*/"ランドソルの施政を司る君主とその一族。ランドソルは伝統的に、王の長女が君主となる女系国家である。王族は王城に居住しており、【王宮騎士団（NIGHTMARE）】によって、厳しく警護されている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/75);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3013, /*glossary_category_id*/3, /*title*/"リア充", /*description*/"本義的には、リアルが充実している人を指す言葉。のはずなのだが、ユカリいわく「最近どいつもこいつも、『恋人がいる人』の意味でしか使ってないでしょうが！　私だって、独り身だけどリアルは充実してますよーだ！　うう……とにかく爆発しろ！」とのこと。どちらにしても、爆発を強く望んでいるらしい。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/76);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3014, /*glossary_category_id*/3, /*title*/"リトルリリカル", /*description*/"ミミ、ミソギ、キョウカの仲良し３人組が結成したギルド。ギルドの目的は、ただ遊ぶこと。ギルド管理協会に認可された正式な団体ではなく、単なる３人のギルドごっこ遊びである。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/77);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3015, /*glossary_category_id*/3, /*title*/"ルーセント学院", /*description*/"ランドソルに存在する学舎の一つ。ギルド管理協会が運営する公立の教育機関。共学で、通学している生徒の年齢、種族、学力もさまざま。主に学力別にクラス分けされている。なおランドソルは義務教育ではないが、教育関連ギルドの発達により、知識水準は高い。識字率はほぼ100％となっている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/78);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3016, /*glossary_category_id*/3, /*title*/"ルピ", /*description*/"アストライア大陸で流通している通貨の単位。紙幣も硬貨も存在している。価値基準としては、1000ルピで一回の食事を済ますことができる程度。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/79);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3017, /*glossary_category_id*/3, /*title*/"ロスト", /*description*/"存在するはずの人物の記録や記憶が消失している、という怪現象。たとえば子供には必ず両親がいるはずだが、資料を調べてもその存在が確認できないし、子供も両親の存在について何も記憶していない。この怪現象のために保護者が存在せず、行き場のない児童がいて社会問題になっている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/80);
INSERT INTO `glossary_detail` VALUES (/*glossary_id*/3018, /*glossary_category_id*/3, /*title*/"隻眼の悪魔（ワンアイドデビル）", /*description*/"かつてはその名を口にしただけで、大陸中の悪人たちが震え上がったといわれる存在。その正体はミツキ。研究の邪魔をしてきた者たちを退けるうち、この名で呼ばれるようになった。普段は眼帯の下に隠れている左眼が開く時、地獄が生まれるとも言われている。", /*unlock_story_id*/0, /*category_type*/1, /*disp_order*/81);
