INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24016, /*where_type*/24, /*character_id*/110501, /*face_type*/4, /*character_name*/"カオリ（サマー）", /*description*/"今日は踊りのキレも悪いよ～\nゴーヤーっぽいもの食べて、元気回復さ～！", /*voice_id*/2, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.0, /*change_face_type*/1, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24017, /*where_type*/24, /*character_id*/110501, /*face_type*/4, /*character_name*/"カオリ（サマー）", /*description*/"マジムンだらけで目が回るさ～！\nふらふらさ～！", /*voice_id*/3, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/4, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24018, /*where_type*/24, /*character_id*/110501, /*face_type*/1, /*character_name*/"カオリ（サマー）", /*description*/"なんくるない、なんくるない\n次、頑張ればいいんだよ～！\n海で泳いで\nリフレッシュするさー！", /*voice_id*/4, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/5.2, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24019, /*where_type*/24, /*character_id*/110501, /*face_type*/5, /*character_name*/"カオリ（サマー）", /*description*/"恥ずかしいところ\n見られちゃったね～\n水着？　水着のことじゃないよー？", /*voice_id*/5, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.2, /*change_face_type*/6, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24020, /*where_type*/24, /*character_id*/110501, /*face_type*/2, /*character_name*/"カオリ（サマー）", /*description*/"絶好調さ～！　この調子で\n海の平和を取り戻すよ～！\n楽しい夏が待ってるさ～！", /*voice_id*/6, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24021, /*where_type*/24, /*character_id*/110501, /*face_type*/1, /*character_name*/"カオリ（サマー）", /*description*/"何匹で襲ってきたって\n私はマブイを落っことさないよ～！\nくしゅん！　あ……\n今のくしゃみで落っこちたかも～？", /*voice_id*/7, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/6.4, /*change_face_type*/6, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24022, /*where_type*/24, /*character_id*/110501, /*face_type*/2, /*character_name*/"カオリ（サマー）", /*description*/"ひーやーさーさー♪\n戦いの後は、踊りを踊って\n気分スッキリさ～！", /*voice_id*/8, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10021);
INSERT INTO `kmk_navi_comment` VALUES (/*comment_id*/24023, /*where_type*/24, /*character_id*/110501, /*face_type*/6, /*character_name*/"カオリ（サマー）", /*description*/"あれ～？　戦ってるうちに\n水着がどっか行っちゃった～？\nねぇキミ、どっかで見なかった～？\nなんで離れるさ～？", /*voice_id*/9, /*start_time*/"2019/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/10.08, /*change_face_type*/3, /*event_id*/10021);
