INSERT INTO `navi_comment` VALUES (/*comment_id*/7001, /*where_type*/7, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"たまには\n協力するのもいいんじゃない？\nあたしも手を貸してあげる", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/7002, /*where_type*/7, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"多くの方と協力して\n戦えるようです\n主さまなら、さぞ\nご活躍なさることでしょう", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/7003, /*where_type*/7, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"たくさんの人と一緒に\n協力バトルです！\nがんばりましょう☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10001, /*where_type*/10, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"ジュエルはよく考えて\n使うことね\n無駄遣い厳禁！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10002, /*where_type*/10, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さまのジュエルで\n貴重な品々を購入できます\nですが、無駄遣いは\n『めっ』でございますよ", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10003, /*where_type*/10, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"ジュエルを食べ物…\nいえ、貴重品と交換できます！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/12001, /*where_type*/12, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"特定の条件を達成すると\n報酬がもらえますよ！\nがんばりましょう♪　", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13001, /*where_type*/13, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"思い出が詰まってるって\nいろいろあったけど\n振り返るのもいいんじゃない？", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13002, /*where_type*/13, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さまとの楽しい日々…\nこちらで何度でも\n思い返しましょう♪", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13003, /*where_type*/13, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"女の子との思い出が\nたくさん見られますよ！\nヒューヒュー♪\nモテモテですねー☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14001, /*where_type*/14, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"クエストを攻略すると、\nアイテムが手に入るんだって\nもらえるものは、\nもらっておきましょ！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14002, /*where_type*/14, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"育成に必要なアイテムが\n獲得できるクエストです\nわたくし、主さまのために、\n強くなりますよ", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14003, /*where_type*/14, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"このクエストを攻略すれば、\nアイテムがもらえますよ\nたくさん集めて、どんどん\n強くなっちゃいましょう！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/15001, /*where_type*/15, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さま、旅の準備を整えて\nより充実した冒険をいたし\nましょう", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16001, /*where_type*/16, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"クランに入れるらしいわよ\nせっかくだから入ってみたら？\nけっこう楽しいみたいよ♪", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16002, /*where_type*/16, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"こちらでクランに加入できます\n素敵なクランがたくさんあって、\n目移りしてしまいますね", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16003, /*where_type*/16, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"クランに加入してみませんか？\nみんなといっしょに冒険した方が、\n楽しいですよ！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17002, /*where_type*/17, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/" こちらではクランバトルに\n 参加された仲間のみなさんの\n 活躍を振り返ることができます", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/18001, /*where_type*/18, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ようこそ！\nこちらでは、マイページや\nギルドハウスで流れる音楽の\n変更や購入が出来ますよ。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/18002, /*where_type*/18, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ＢＧＭの変更や購入は、こちらから\nできますよ。\n素敵な音楽を、たくさんご用意\nいたしました！", /*voice_id*/2, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/19001, /*where_type*/19, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"マイページやギルドハウスの\nＢＧＭを変更できますよ。\nあなたの好きな曲、\n私にも聴かせてくださいね。", /*voice_id*/3, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/19002, /*where_type*/19, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"こちらでは、ＢＧＭの変更が\nできますよ。\nどんな曲に変更しますか？\nふふふ、私も楽しみです♪", /*voice_id*/4, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/20001, /*where_type*/20, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"こちらでは、ＢＧＭを購入\nすることができますよ。\n試聴することもできるので、\nじっくり選んでくださいね。", /*voice_id*/5, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/20002, /*where_type*/20, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"いらっしゃいませ！\nＢＧＭの購入はこちらです。\n素敵な曲がたくさんあって、\n目移りしちゃいますね。", /*voice_id*/6, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
