INSERT INTO `navi_comment` VALUES (/*comment_id*/7001, /*where_type*/7, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"たまには\n協力するのもいいんじゃない？\nあたしも手を貸してあげる", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/7002, /*where_type*/7, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"多くの方と協力して\n戦えるようです\n主さまなら、さぞ\nご活躍なさることでしょう", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/7003, /*where_type*/7, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"たくさんの人と一緒に\n協力バトルです！\nがんばりましょう☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10001, /*where_type*/10, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"ジュエルはよく考えて\n使うことね\n無駄遣い厳禁！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10002, /*where_type*/10, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さまのジュエルで\n貴重な品々を購入できます\nですが、無駄遣いは\n『めっ』でございますよ", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10003, /*where_type*/10, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"ジュエルを食べ物…\nいえ、貴重品と交換できます！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/12001, /*where_type*/12, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"特定の条件を達成すると\n報酬がもらえますよ！\nがんばりましょう♪　", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13001, /*where_type*/13, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"思い出が詰まってるって\nいろいろあったけど\n振り返るのもいいんじゃない？", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13002, /*where_type*/13, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さまとの楽しい日々…\nこちらで何度でも\n思い返しましょう♪", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13003, /*where_type*/13, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"女の子との思い出が\nたくさん見られますよ！\nヒューヒュー♪\nモテモテですねー☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14001, /*where_type*/14, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"クエストを攻略すると、\nアイテムが手に入るんだって\nもらえるものは、\nもらっておきましょ！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14002, /*where_type*/14, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"育成に必要なアイテムが\n獲得できるクエストです\nわたくし、主さまのために、\n強くなりますよ", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14003, /*where_type*/14, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"このクエストを攻略すれば、\nアイテムがもらえますよ\nたくさん集めて、どんどん\n強くなっちゃいましょう！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/15001, /*where_type*/15, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さま、旅の準備を整えて\nより充実した冒険をいたし\nましょう", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16001, /*where_type*/16, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"クランに入れるらしいわよ\nせっかくだから入ってみたら？\nけっこう楽しいみたいよ♪", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16002, /*where_type*/16, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"こちらでクランに加入できます\n素敵なクランがたくさんあって、\n目移りしてしまいますね", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16003, /*where_type*/16, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"クランに加入してみませんか？\nみんなといっしょに冒険した方が、\n楽しいですよ！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17002, /*where_type*/17, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/" こちらではクランバトルに\n 参加された仲間のみなさんの\n 活躍を振り返ることができます", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17004, /*where_type*/25, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"模擬戦の戦績もアンタが\n気に入ったものがあれば\n記録しておけるのね", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17005, /*where_type*/25, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"主さま、今回のクランバトルでの\nご活躍が記録されています\nバトルの参考にいたしましょう", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17006, /*where_type*/25, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"今回のクランバトルでの戦績を\nおさらいできますよ！\n私にも見せてください☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/18001, /*where_type*/18, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ようこそ！\nこちらでは、マイページや\nギルドハウスで流れる音楽の\n変更や購入が出来ますよ", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/18002, /*where_type*/18, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ＢＧＭの変更や購入は、こちらから\nできますよ\n素敵な音楽を、たくさんご用意\nいたしました！", /*voice_id*/2, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/19001, /*where_type*/19, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"マイページやギルドハウスの\nＢＧＭを変更できますよ\nあなたの好きな曲、\n私にも聴かせてくださいね", /*voice_id*/3, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/19002, /*where_type*/19, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"こちらでは、ＢＧＭの変更が\nできますよ\nどんな曲に変更しますか？\nふふふ、私も楽しみです♪", /*voice_id*/4, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/20001, /*where_type*/20, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"こちらでは、ＢＧＭを購入\nすることができますよ\n試聴することもできるので、\nじっくり選んでくださいね", /*voice_id*/5, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/20002, /*where_type*/20, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"いらっしゃいませ！\nＢＧＭの購入はこちらです\n素敵な曲がたくさんあって、\n目移りしちゃいますね", /*voice_id*/6, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/21001, /*where_type*/21, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"ここで手に入れたアイテムで\n専用装備を作成できるみたいね\nアイテムが集まったら\nさっそく作ってみましょ！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/21002, /*where_type*/21, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"攻略すると珍しいアイテムが\n手に入りますよ\nこのアイテムで専用装備を\n作成できるようです", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/21003, /*where_type*/21, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"専用装備の作成に必要な\nアイテムを獲得できますよ\nたくさん集めてどんどん\n強くなりましょう！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/22001, /*where_type*/22, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"投票はこちらです\nあなたが応援したい女の子に\n思いを込めた一票を\nお願いします！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-430.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/22002, /*where_type*/22, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ようこそ！\nあなたが思いを寄せるのは\nどの女の子ですか？\n応援してあげてくださいね！", /*voice_id*/2, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-430.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/23001, /*where_type*/23, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"たくさんの投票、\nありがとうございました\n結果発表を楽しみに\n待っていてくださいね！", /*voice_id*/3, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-430.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/23002, /*where_type*/23, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ただ今、人気投票の\n集計中です\n結果が出るまでもう少しだけ\nお待ちくださいね", /*voice_id*/4, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-430.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24001, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"あなた様が手伝ってくださった\nチョコレートケーキ\n美味しくないはずがありませんわ", /*voice_id*/1, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/4.08, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24002, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"このような失敗、あなた様が\nするはずありません\nもう一度挑戦してみましょう", /*voice_id*/2, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.0, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24003, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"あなた様のために\n作っているケーキを\nあなた様に手伝っていただく……\nことになるなんて……クスクス", /*voice_id*/3, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/10.11, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24004, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"あまり気に病む必要はありませんわ\nこれからは私がたっぷりと\n時間をかけて教えて差し上げますから", /*voice_id*/4, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/4.0, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24005, /*where_type*/24, /*character_id*/109001, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"今回は調子が悪かった……のですか？\nあなた様が手伝ってくれたものなら\nわたしはどんなものでも……クスクス", /*voice_id*/5, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/6.0, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24006, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"あなた様が手がけた\nチョコレートケーキは\n私にとっての宝物……\n侮辱する者は生かしておきませんわ", /*voice_id*/6, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24007, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"とても素晴らしい出来栄えですわ！\nあなた様にふさわしい\nチョコレートケーキができました", /*voice_id*/7, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24008, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"私を手伝ってくれたあなた様は\nすでに私の虜……\n甘い世界に誘って差し上げますわ", /*voice_id*/8, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24009, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"あなた様の愛情\nしかと受け止めました\nこれが……私たちの初めての\n共同作業ですわね……クスクス", /*voice_id*/9, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24010, /*where_type*/24, /*character_id*/109002, /*face_type*/1, /*character_name*/"エリコ（バレンタイン）", /*description*/"これは世界一の\nチョコレートケーキですわ\nだって、あなた様が腕によりをかけて\n作ってくださったんですもの", /*voice_id*/10, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24011, /*where_type*/24, /*character_id*/109101, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"上手にできなくて残念だった？\nでも、弟くんの気持ちが嬉しいな\nいつだってお姉ちゃんは\nそばにいるからね♪", /*voice_id*/1, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/2.12, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24012, /*where_type*/24, /*character_id*/109101, /*face_type*/5, /*character_name*/"シズル（バレンタイン）", /*description*/"うーん、弟くんには珍しく\n緊張しちゃったのかな？\nもう一度チャレンジしてみる？", /*voice_id*/2, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/4.19, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24013, /*where_type*/24, /*character_id*/109101, /*face_type*/2, /*character_name*/"シズル（バレンタイン）", /*description*/"お姉ちゃんのお手伝い、\nよくできました！\nあー、\nまだつまみ食いしちゃダメだぞ！", /*voice_id*/3, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/2.3, /*change_face_type*/4, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24014, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"見てみて！\nスポンジに綺麗な焼き目がついてるよ\nさっすが弟くんだね！", /*voice_id*/4, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24015, /*where_type*/24, /*character_id*/109101, /*face_type*/5, /*character_name*/"シズル（バレンタイン）", /*description*/"まぁまぁ、落ち込まないで\n見た目は……個性的だけど、\n味は絶対美味しいはずだから！", /*voice_id*/5, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.23, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24016, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"弟くんみたいな\n可愛いケーキができたよ！\n早く食べてみたいでしょ♪", /*voice_id*/6, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24017, /*where_type*/24, /*character_id*/109101, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"あれれ？　弟くん、指に\nチョコレートがくっついてるよ？\nお姉ちゃんが舐めてあげるね、\nはぁむ！", /*voice_id*/7, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-385.0, /*pos_y*/8.0, /*change_face_time*/5.01, /*change_face_type*/2, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24018, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"なかなか上手にできたんじゃない？\nでも弟くんはまだまだできる子！\nお姉ちゃん、信じてるからね", /*voice_id*/8, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24019, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"わぁ～、\nとっても美味しそうにできたね！\n弟くん手慣れてたから\nお姉ちゃん安心して任せられちゃった！", /*voice_id*/9, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/24020, /*where_type*/24, /*character_id*/109102, /*face_type*/1, /*character_name*/"シズル（バレンタイン）", /*description*/"や～ん、弟くんが手伝ってくれた\nチョコレートケーキ！\nすっごく美味しそうにできたね♪", /*voice_id*/10, /*start_time*/"2019/01/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10011);
INSERT INTO `navi_comment` VALUES (/*comment_id*/26001, /*where_type*/26, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"この先は神聖な場所みたい。\n手に入るお宝も並じゃないけど\n気を付けていかないと\n痛い目にあうわよ", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/26002, /*where_type*/26, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"ここから先は神聖な場所\nでございます。\n気を引き締めてお宝の\n調査探索に参りましょう", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/26003, /*where_type*/26, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"ここから先は神聖な場所\nみたいです！\nどんなお宝が手に入るか\n楽しみですね！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/27001, /*where_type*/27, /*character_id*/106001, /*face_type*/1, /*character_name*/"キャル", /*description*/"ここにさらなる力を得るための\nアイテムが眠っているみたい！\nどんどん挑戦して\n強くなっちゃいましょ！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/27002, /*where_type*/27, /*character_id*/105901, /*face_type*/1, /*character_name*/"コッコロ", /*description*/"こちらにさらなる力を得る\nためのアイテムが眠っている\nようです。主さま、どこから\n挑戦いたしましょう。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/27003, /*where_type*/27, /*character_id*/105801, /*face_type*/1, /*character_name*/"ペコリーヌ", /*description*/"ここにさらなる力を得るための\nアイテムが眠っているみたい\nです！　どこから挑戦するか\n迷っちゃいますね！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/28001, /*where_type*/28, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ようこそ！\n皆さんの素敵な出会いを\nギルド管理協会が\nお手伝いします！", /*voice_id*/1, /*start_time*/"2019/09/24 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/28002, /*where_type*/28, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"ギルド管理協会です\nフレンドに関しては\nこちらでご確認ください", /*voice_id*/2, /*start_time*/"2019/09/24 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/29001, /*where_type*/29, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"こちらがフレンドの\n皆さんです\n素敵な関係を\n築いてくださいね！", /*voice_id*/3, /*start_time*/"2019/09/24 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-409.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/29002, /*where_type*/29, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"こちらの一覧で\nフレンドのプロフィールを\n確認できますよ", /*voice_id*/4, /*start_time*/"2019/09/24 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-409.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/30001, /*where_type*/30, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"フレンド管理は\nこちらになります\n新しいフレンドとの出会いを\n楽しんでくださいね！", /*voice_id*/5, /*start_time*/"2019/09/24 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-409.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/30002, /*where_type*/30, /*character_id*/190801, /*face_type*/1, /*character_name*/"カリン", /*description*/"フレンドの申請や\n承認ができますよ\nどうぞ、良き交流を！", /*voice_id*/6, /*start_time*/"2019/09/24 15:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-409.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
