INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24001, /*where_type*/24, /*character_id*/102301, /*face_type*/2, /*character_name*/"アヤネ", /*description*/"やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", /*voice_id*/401, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24002, /*where_type*/24, /*character_id*/102301, /*face_type*/1, /*character_name*/"アヤネ", /*description*/"私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", /*voice_id*/402, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/2.21, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24003, /*where_type*/24, /*character_id*/102301, /*face_type*/2, /*character_name*/"アヤネ", /*description*/"やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", /*voice_id*/403, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24004, /*where_type*/24, /*character_id*/192201, /*face_type*/2, /*character_name*/"アサヒ", /*description*/"特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", /*voice_id*/701, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/1.99, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24005, /*where_type*/24, /*character_id*/192201, /*face_type*/1, /*character_name*/"アサヒ", /*description*/"よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", /*voice_id*/702, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/2.21, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24006, /*where_type*/24, /*character_id*/192201, /*face_type*/2, /*character_name*/"アサヒ", /*description*/"試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", /*voice_id*/703, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/3.33, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24007, /*where_type*/24, /*character_id*/192301, /*face_type*/2, /*character_name*/"部員３", /*description*/"このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", /*voice_id*/801, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24008, /*where_type*/24, /*character_id*/192301, /*face_type*/1, /*character_name*/"部員３", /*description*/"オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", /*voice_id*/802, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/2.68, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24009, /*where_type*/24, /*character_id*/192301, /*face_type*/2, /*character_name*/"部員３", /*description*/"いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", /*voice_id*/803, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24010, /*where_type*/24, /*character_id*/192501, /*face_type*/1, /*character_name*/"", /*description*/"やった！", /*voice_id*/501, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24011, /*where_type*/24, /*character_id*/192501, /*face_type*/1, /*character_name*/"", /*description*/"勝ったよ！", /*voice_id*/502, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24012, /*where_type*/24, /*character_id*/192501, /*face_type*/1, /*character_name*/"", /*description*/"いい調子！", /*voice_id*/503, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24013, /*where_type*/24, /*character_id*/113501, /*face_type*/6, /*character_name*/"ミサト", /*description*/"あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", /*voice_id*/111, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/5.33, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24014, /*where_type*/24, /*character_id*/113501, /*face_type*/1, /*character_name*/"ミサト", /*description*/"みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", /*voice_id*/112, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/6.85, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24015, /*where_type*/24, /*character_id*/113501, /*face_type*/1, /*character_name*/"ミサト", /*description*/"練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", /*voice_id*/113, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/3.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24016, /*where_type*/24, /*character_id*/192401, /*face_type*/1, /*character_name*/"ダイチ", /*description*/"まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", /*voice_id*/611, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/6.11, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24017, /*where_type*/24, /*character_id*/192401, /*face_type*/1, /*character_name*/"ダイチ", /*description*/"ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", /*voice_id*/612, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/6.75, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24018, /*where_type*/24, /*character_id*/192401, /*face_type*/2, /*character_name*/"ダイチ", /*description*/"おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", /*voice_id*/613, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24019, /*where_type*/24, /*character_id*/113401, /*face_type*/2, /*character_name*/"ハツネ", /*description*/"きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", /*voice_id*/211, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.52, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24020, /*where_type*/24, /*character_id*/113401, /*face_type*/2, /*character_name*/"ハツネ", /*description*/"やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", /*voice_id*/212, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/3.89, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24021, /*where_type*/24, /*character_id*/113401, /*face_type*/1, /*character_name*/"ハツネ", /*description*/"よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", /*voice_id*/213, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/13.49, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24022, /*where_type*/24, /*character_id*/106501, /*face_type*/1, /*character_name*/"カヤ", /*description*/"いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", /*voice_id*/311, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/3.17, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24023, /*where_type*/24, /*character_id*/106501, /*face_type*/1, /*character_name*/"カヤ", /*description*/"どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", /*voice_id*/312, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.42, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24024, /*where_type*/24, /*character_id*/106501, /*face_type*/6, /*character_name*/"カヤ", /*description*/"なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", /*voice_id*/313, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/2.89, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24025, /*where_type*/24, /*character_id*/102301, /*face_type*/4, /*character_name*/"アヤネ", /*description*/"あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", /*voice_id*/421, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/3.71, /*change_face_type*/3, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24026, /*where_type*/24, /*character_id*/192201, /*face_type*/2, /*character_name*/"アサヒ", /*description*/"いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", /*voice_id*/721, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/5.1, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24027, /*where_type*/24, /*character_id*/192301, /*face_type*/1, /*character_name*/"部員３", /*description*/"ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", /*voice_id*/821, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/4.34, /*change_face_type*/3, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24028, /*where_type*/24, /*character_id*/192501, /*face_type*/1, /*character_name*/"", /*description*/"いい勝負だったよ\nまたやろうね", /*voice_id*/521, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/0.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24029, /*where_type*/24, /*character_id*/113501, /*face_type*/1, /*character_name*/"ミサト", /*description*/"とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", /*voice_id*/101, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-360.0, /*pos_y*/0.0, /*change_face_time*/5.62, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24030, /*where_type*/24, /*character_id*/113501, /*face_type*/1, /*character_name*/"ミサト", /*description*/"頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", /*voice_id*/102, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-360.0, /*pos_y*/0.0, /*change_face_time*/9.58, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24031, /*where_type*/24, /*character_id*/113501, /*face_type*/2, /*character_name*/"ミサト", /*description*/"いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", /*voice_id*/103, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-360.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24032, /*where_type*/24, /*character_id*/113501, /*face_type*/2, /*character_name*/"ミサト", /*description*/"夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", /*voice_id*/104, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-360.0, /*pos_y*/0.0, /*change_face_time*/9.13, /*change_face_type*/1, /*event_id*/10046);
INSERT INTO `pkb_navi_comment` VALUES (/*comment_id*/24033, /*where_type*/24, /*character_id*/113501, /*face_type*/1, /*character_name*/"ミサト", /*description*/"絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", /*voice_id*/105, /*start_time*/"2020/07/31 12:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-360.0, /*pos_y*/0.0, /*change_face_time*/8.27, /*change_face_type*/2, /*event_id*/10046);
