INSERT INTO `psy_note` VALUES (/*psy_product_id*/100, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ペコリーヌのプリン", /*flavor_1*/"プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", /*flavor_2*/"おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", /*flavor_3*/"やばいですね☆なの！", /*disp_order*/1, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/101, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"コッコロのプリン", /*flavor_1*/"いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", /*flavor_2*/"なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", /*flavor_3*/"主さま、なの！", /*disp_order*/2, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/102, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"キャルのプリン", /*flavor_1*/"ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", /*flavor_2*/"いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", /*flavor_3*/"ぶっ殺すぞ！なの！\n…デレデレはどこなの？", /*disp_order*/3, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/103, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"シェフィのプリン", /*flavor_1*/"オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", /*flavor_2*/"あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", /*flavor_3*/"おにーたん♪なの！", /*disp_order*/4, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/104, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"イリヤのプリン", /*flavor_1*/"イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", /*flavor_2*/"トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", /*flavor_3*/"ハーハッハッハ！\n世界征服なのじゃなのー！", /*disp_order*/5, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/105, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"アカリのプリン", /*flavor_1*/"アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", /*flavor_2*/"見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", /*flavor_3*/"お兄ちゃんのエッチ！　なの！", /*disp_order*/6, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/106, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ヨリのプリン", /*flavor_1*/"かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", /*flavor_2*/"んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", /*flavor_3*/"えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", /*disp_order*/7, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/107, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"シノブのプリン", /*flavor_1*/"シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", /*flavor_2*/"このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", /*flavor_3*/"お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", /*disp_order*/8, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/108, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ジュンのプリン", /*flavor_1*/"黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", /*flavor_2*/"固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", /*flavor_3*/"インフェルノシールド！なの！", /*disp_order*/9, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/109, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"トモのプリン", /*flavor_1*/"オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", /*flavor_2*/"口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", /*flavor_3*/"あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", /*disp_order*/10, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/110, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"マツリのプリン", /*flavor_1*/"王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", /*flavor_2*/"ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", /*flavor_3*/"タイガーヒーローボンバー！\nなの！", /*disp_order*/11, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/111, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"クロエのプリン", /*flavor_1*/"なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", /*flavor_2*/"クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", /*flavor_3*/"ま、いいんじゃん？\nしらんけどなの", /*disp_order*/13, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/112, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"チエルのプリン", /*flavor_1*/"きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", /*flavor_2*/"なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", /*flavor_3*/"ぷりーん★なの！", /*disp_order*/14, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/113, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ユニのプリン", /*flavor_1*/"見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", /*flavor_2*/"味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", /*flavor_3*/"端的に換言すれば\n青春の味なの？", /*disp_order*/15, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/114, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"サレンのプリン", /*flavor_1*/"オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", /*flavor_2*/"ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", /*flavor_3*/"幼なじみはあんた…なの？", /*disp_order*/16, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/115, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"スズメのプリン", /*flavor_1*/"たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", /*flavor_2*/"おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", /*flavor_3*/"ひゃああんなの～！", /*disp_order*/17, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/116, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"クルミのプリン", /*flavor_1*/"怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", /*flavor_2*/"甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", /*flavor_3*/"サルキデバレヤ！なの！", /*disp_order*/18, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/117, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"アヤネのプリン", /*flavor_1*/"くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", /*flavor_2*/"このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", /*flavor_3*/"プリンフルスイングなの～！", /*disp_order*/19, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/118, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"マホのプリン", /*flavor_1*/"マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", /*flavor_2*/"メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", /*flavor_3*/"みらくるみやりん\nくるりんぱ～☆なの！", /*disp_order*/20, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/119, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"マコトのプリン", /*flavor_1*/"カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", /*flavor_2*/"うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", /*flavor_3*/"ウルフェンバイトなの！", /*disp_order*/21, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/120, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"カオリのプリン", /*flavor_1*/"どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", /*flavor_2*/"一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", /*flavor_3*/"なんくるないさ～！なの！", /*disp_order*/22, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/121, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"カスミのプリン", /*flavor_1*/"勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", /*flavor_2*/"むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", /*flavor_3*/"ふふん。簡単な推理なの", /*disp_order*/23, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/122, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"マヒルのプリン", /*flavor_1*/"搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", /*flavor_2*/"ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", /*flavor_3*/"エリザベス～！なの～！", /*disp_order*/24, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/123, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"シオリのプリン", /*flavor_1*/"マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", /*flavor_2*/"ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", /*flavor_3*/"私の好きな本…読んで\nもらえたらうれしいです…\nなの", /*disp_order*/25, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/124, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"リマのプリン", /*flavor_1*/"牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", /*flavor_2*/"こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", /*flavor_3*/"もふもふにしてあげるなの！", /*disp_order*/26, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/125, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"リンのプリン", /*flavor_1*/"いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", /*flavor_2*/"んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", /*flavor_3*/"めんどくさいな～なの…", /*disp_order*/27, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/126, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ラビリスタのプリン", /*flavor_1*/"クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", /*flavor_2*/"プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", /*flavor_3*/"プリンの権能を解放するの～！", /*disp_order*/28, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/127, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"シズルのプリン", /*flavor_1*/"お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", /*flavor_2*/"とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", /*flavor_3*/"お姉ちゃんパワー全開！なの！", /*disp_order*/29, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/128, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"リノのプリン", /*flavor_1*/"リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", /*flavor_2*/"これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", /*flavor_3*/"お兄ちゃん、プリンをよこすの！", /*disp_order*/30, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/129, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ヒヨリのプリン", /*flavor_1*/"プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", /*flavor_2*/"食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", /*flavor_3*/"困った時はお互いさまさま！\nなの！", /*disp_order*/31, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/130, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ユイのプリン", /*flavor_1*/"ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", /*flavor_2*/"はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", /*flavor_3*/"フラワリングヒールなの～♪", /*disp_order*/32, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/131, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"レイのプリン", /*flavor_1*/"究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", /*flavor_2*/"確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", /*flavor_3*/"スラッシュテンペスト！なの。", /*disp_order*/33, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/132, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"アキノのプリン", /*flavor_1*/"豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", /*flavor_2*/"味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", /*flavor_3*/"おーほっほほほ！なの！", /*disp_order*/34, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/133, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ユカリのプリン", /*flavor_1*/"外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", /*flavor_2*/"プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", /*flavor_3*/"プリしゅわ最高～！なの！", /*disp_order*/35, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/134, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"タマキのプリン", /*flavor_1*/"餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", /*flavor_2*/"このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", /*flavor_3*/"にゃにゃにゃ！なの！", /*disp_order*/36, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/135, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ミフユのプリン", /*flavor_1*/"栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", /*flavor_2*/"プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", /*flavor_3*/"効率全開！なの！", /*disp_order*/37, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/136, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ミサトのプリン", /*flavor_1*/"エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", /*flavor_2*/"このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", /*flavor_3*/"あらあら～なの～♪", /*disp_order*/38, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/137, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ハツネのプリン", /*flavor_1*/"お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", /*flavor_2*/"あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", /*flavor_3*/"スヤスヤ～…なの…", /*disp_order*/39, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/138, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"アオイのプリン", /*flavor_1*/"お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", /*flavor_2*/"知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", /*flavor_3*/"ぼっちは卒業なの～！", /*disp_order*/40, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/139, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ノゾミのプリン", /*flavor_1*/"トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", /*flavor_2*/"口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", /*flavor_3*/"リズムあげるのー！", /*disp_order*/41, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/140, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"チカのプリン", /*flavor_1*/"ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", /*flavor_2*/"プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", /*flavor_3*/"風の加護を！なの！", /*disp_order*/42, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/141, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ツムギのプリン", /*flavor_1*/"歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", /*flavor_2*/"包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", /*flavor_3*/"騎士さんのために\nしたんじゃありません！なの！", /*disp_order*/43, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/142, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"クリスティーナのプリン", /*flavor_1*/"プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", /*flavor_2*/"んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", /*flavor_3*/"宴の始まりなの！", /*disp_order*/12, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/143, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ルカのプリン", /*flavor_1*/"大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", /*flavor_2*/"大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", /*flavor_3*/"一刀両断、覚悟しな！なの！", /*disp_order*/44, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/144, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ナナカのプリン", /*flavor_1*/"サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", /*flavor_2*/"一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", /*flavor_3*/"きゅぴーん！キタコレ！なの！", /*disp_order*/45, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/145, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"アンナのプリン", /*flavor_1*/"小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", /*flavor_2*/"一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", /*flavor_3*/"人呼んで、疾風のミヤコなの！", /*disp_order*/46, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/146, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"エリコのプリン", /*flavor_1*/"甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", /*flavor_2*/"なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", /*flavor_3*/"クスクス…クスクスクス…なの", /*disp_order*/47, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/147, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ミツキのプリン", /*flavor_1*/"健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", /*flavor_2*/"鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", /*flavor_3*/"どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", /*disp_order*/48, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/148, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"モニカのプリン", /*flavor_1*/"小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", /*flavor_2*/"ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", /*flavor_3*/"私は子どもではない！なの！", /*disp_order*/49, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/149, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ニノンのプリン", /*flavor_1*/"オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", /*flavor_2*/"このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", /*flavor_3*/"ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", /*disp_order*/50, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/150, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"クウカのプリン", /*flavor_1*/"オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", /*flavor_2*/"カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", /*flavor_3*/"…ぐふふ、じゅるり～なの", /*disp_order*/51, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/151, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ユキのプリン", /*flavor_1*/"とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", /*flavor_2*/"味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", /*flavor_3*/"あぁ、ミヤコはなんて美しいの～", /*disp_order*/52, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/152, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"アユミのプリン", /*flavor_1*/"知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", /*flavor_2*/"うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", /*flavor_3*/"ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", /*disp_order*/53, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/153, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ミソギのプリン", /*flavor_1*/"イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", /*flavor_2*/"見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", /*flavor_3*/"にひひ…イタズラの時間なの！", /*disp_order*/54, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/154, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ミミのプリン", /*flavor_1*/"素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", /*flavor_2*/"ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", /*flavor_3*/"ぴょんぴょこぴょーん、なの♪", /*disp_order*/55, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/155, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"キョウカのプリン", /*flavor_1*/"幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", /*flavor_2*/"ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", /*flavor_3*/"へんたいふしんしゃさんなの！", /*disp_order*/56, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/156, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"スズナのプリン", /*flavor_1*/"カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", /*flavor_2*/"見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", /*flavor_3*/"ハート狙い撃ち！なの！", /*disp_order*/57, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/157, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"イオのプリン", /*flavor_1*/"学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", /*flavor_2*/"プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", /*flavor_3*/"授業を始めます♪なの！", /*disp_order*/58, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/158, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ミサキのプリン", /*flavor_1*/"少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", /*flavor_2*/"ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", /*flavor_3*/"立派なレディ、なの！", /*disp_order*/59, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/159, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ホマレのプリン", /*flavor_1*/"謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", /*flavor_2*/"味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", /*flavor_3*/"プリンを持ってこないと\nお仕置きだよ～☆なの！", /*disp_order*/60, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/160, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"カヤのプリン", /*flavor_1*/"ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", /*flavor_2*/"最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", /*flavor_3*/"たぎってきたの！", /*disp_order*/61, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/161, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"イノリのプリン", /*flavor_1*/"なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", /*flavor_2*/"見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", /*flavor_3*/"下剋上、やってやるですなの！", /*disp_order*/62, /*init_flg*/0);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/162, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ムイミのプリン", /*flavor_1*/"天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", /*flavor_2*/"むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", /*flavor_3*/"どけどけ～！なの～！", /*disp_order*/63, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/163, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"ネネカのプリン", /*flavor_1*/"いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", /*flavor_2*/"いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", /*flavor_3*/"ミラーミラー！なの！", /*disp_order*/64, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/164, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"クレジッタのプリン", /*flavor_1*/"金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", /*flavor_2*/"むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", /*flavor_3*/"プリンに忠誠を誓いますわ！\nなの！", /*disp_order*/65, /*init_flg*/1);
INSERT INTO `psy_note` VALUES (/*psy_product_id*/165, /*condition_flavor_1*/3, /*condition_flavor_2*/5, /*psy_product_name*/"カリンのプリン", /*flavor_1*/"みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", /*flavor_2*/"手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", /*flavor_3*/"ギルド管理協会へようこそ！\nなの！", /*disp_order*/66, /*init_flg*/1);
