INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3, /*level*/1, /*item_detail*/"ランドソルの背景。安心感漂うその風景は、自分にとって帰るべき家があることを自覚させてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4, /*level*/1, /*item_detail*/"ギルドハウスの壁紙。窓から差し込む木漏れ日は、少女たちの平穏の証。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5, /*level*/1, /*item_detail*/"透明だけど壁。目には見えないけどちゃんと雨風はしのげる。中から外は見えるけど外からは見えない不思議な壁。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6, /*level*/1, /*item_detail*/"透明な床板。目には見えないけどそこにはきちんと床がある。高い階の床がこれだと、ちょっと怖い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/7, /*level*/1, /*item_detail*/"食べてもなくならないおにぎり。絶妙な塩加減と握り具合、そして無限に食べられるとあって、食いしん坊にはたまらない一品！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/8, /*level*/1, /*item_detail*/"魔導書を置くことができる本立て。その幻想的な意匠と雰囲気で、気分はさながら偉大な魔法使い！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/9, /*level*/1, /*item_detail*/"好きな野菜や具材を入れて、ぐつぐつ、ことこと、じっくり煮込んだら…美味しいシチューのできあがり！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/10, /*level*/1, /*item_detail*/"リマをモデルとしたぬいぐるみ。とにかく可愛い。更にもふもふレベルでは他の追随を許さない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/11, /*level*/1, /*item_detail*/"アロマオイルを霧にして部屋に拡散する。火を使わないので安全安心。最近の流行はプラノ平原の花をブレンドした、フローラルベースの香り。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/12, /*level*/1, /*item_detail*/"アロマオイルを霧にして部屋に拡散する。より高いところに置くことで、さらに広範囲に香りが拡散！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/13, /*level*/1, /*item_detail*/"落ち着いた色合いの本棚。マンガから魔導書までバラバラなサイズの本もきちんと収まる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/14, /*level*/1, /*item_detail*/"白いロイヤルな本棚。綺麗なデザインと多くの本を収納できる実用性から、とくに女性に人気がある。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/15, /*level*/1, /*item_detail*/"使い勝手がいい四角いテーブル。無理のない使用人数は４人。おしゃべりしたり、トランプしたりの大事な場所。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/16, /*level*/1, /*item_detail*/"実用性の高い木の机。ゲームをするもよし、恋バナをするのもよし！　でもやっぱり、みんなで食卓を囲むのが一番！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/17, /*level*/1, /*item_detail*/"簡素な木の椅子。簡素だけに丈夫なので、乱暴に扱っても壊れにくいし、壊れてもすぐに直せる。ただし、直せる人がいるときに限るが…", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/18, /*level*/1, /*item_detail*/"ちょっと腰かけるのに便利な丸椅子。座ったまま回ることはできないけど、どの方向にいる相手とも話ができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/20, /*level*/1, /*item_detail*/"眠るときも木の香りがして、気持ちがいい白木のベッド。ただしマットレスの下は板一枚なので、飛んだり跳ねたりは危険。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/21, /*level*/1, /*item_detail*/"素朴な木製のチェスト。服をしまったりへそくりを隠したり…勝手に開けるのはマナー違反なのでやめよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/22, /*level*/1, /*item_detail*/"木のパーテーション。しきりに使っても、あたたかい雰囲気でプライベートな空間を作り出すことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/23, /*level*/1, /*item_detail*/"優しい木の香り漂う壁。部屋の雰囲気を明るくし、香りにはリラックス効果もあるため、心身ともに休まる部屋づくりができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/24, /*level*/1, /*item_detail*/"木の床。二色の木を交互に貼ってできている。ワックスで磨けばつるつる！　教室のようなインテリアと相性が良い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/25, /*level*/1, /*item_detail*/"木の螺旋階段。上っているとする木のきしむ音に、どこか懐かしさを覚える。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/26, /*level*/1, /*item_detail*/"木の螺旋階段。下から聞こえる明るい声に、思わず駆け足で下りたくなるデザイン。危ないからゆっくり下りましょう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/27, /*level*/1, /*item_detail*/"高級感溢れるダークブラウンの猫脚テーブル。テーブルクロスと合わせて使うと、とっても上品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/28, /*level*/1, /*item_detail*/"高級感溢れるシックなマホガニーの机。古くからある老舗ブランドの品で、年齢層の高い人々に人気となっている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/29, /*level*/1, /*item_detail*/"背もたれに飾り彫りがあるダークブラウンの椅子。材質は見た目の良さと加工のしやすさで人気のアストライアン・マホガニー。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/30, /*level*/1, /*item_detail*/"小さいけど頑丈な丸椅子。踏み台にするのに手頃だが、座る以外の用途に使うのは危険です。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/32, /*level*/1, /*item_detail*/"落ち着いた色合いの、マホガニー材のベッド。ヘッドボードにクッションを置いて背を預ければカウチ的な使い方も可能。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/33, /*level*/1, /*item_detail*/"４つ抽斗のチェスト。見た目より深いので魔法使いのローブやステッキなど、かさばるものも楽々収納。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/34, /*level*/1, /*item_detail*/"マホガニーの一枚板から作成されている。パーテーションは目隠しであると同時に、玄関から入ってきた人が最初に目にする家具でもあるので手は抜けない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/35, /*level*/1, /*item_detail*/"装飾と補強に、マホガニー材の枠を使用した壁紙。シンプルなのに木のあたたかみも感じられる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/36, /*level*/1, /*item_detail*/"色味の違う木の板で作ったモザイクパターンの床板。ワックスがけ推奨。つるつるになった床でスケートごっこ非推奨。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/37, /*level*/1, /*item_detail*/"上の階に行くためのマホガニー材の螺旋階段。手すりにまたがって滑るのにも最適の曲線と勾配。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/38, /*level*/1, /*item_detail*/"下の階に行くためのマホガニー材の螺旋階段。気を付けないと転げ落ちても誰にも気づいてもらえない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/39, /*level*/1, /*item_detail*/"あたたかみのあるピンク色のテーブル。大切な人に手紙を書くのに使ってはいかが？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/40, /*level*/1, /*item_detail*/"可愛らしいピンクの大きな机。お菓子やティーセットなどを置きたくなるデザイン。ラブリーな部屋にしたい人おすすめの一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/41, /*level*/1, /*item_detail*/"キュートなピンクの椅子。その可愛い見た目に反して頑丈に座りやすく作られている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/42, /*level*/1, /*item_detail*/"布張りのスツール。座面の色はピンクと白のストライプ。可愛くて奪い合い必至なので、人数分お求めになることをおすすめ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/43, /*level*/1, /*item_detail*/"フカフカのソファ。このピンクはマルジェン海岸に群生するサンゴの色でランドソルではとても人気がある色。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/44, /*level*/1, /*item_detail*/"二人掛けのフカフカソファ。ゆったり座れる。ベッドに行くまでもなく、ここでゴロゴロとくつろいでいるうちに、寝てしまうこともしばしば…", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/45, /*level*/1, /*item_detail*/"フレームも枕もピンクのパイプベッド。枕やブランケットも色味をそろえてある。楽しい夢が見られそう♪", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/46, /*level*/1, /*item_detail*/"ピンクのチェスト。白木をピンクのペンキで塗装したもの。散らかりがちな日用品や装備品を片づけるのにピッタリ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/47, /*level*/1, /*item_detail*/"ピンクの合板製のパーテーション。ちょっとした目隠しだけど密室になるわけじゃない。聞こえちゃった話も聞かなかったことにするのがランドソルのマナー。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/48, /*level*/1, /*item_detail*/"床に敷くだけでアクセントになるピンクのラグマット。ここに立った人は、それだけでいつもより可愛く見えるかも。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/49, /*level*/1, /*item_detail*/"このピンクの壁の部屋にはつい花やぬいぐるみなどを置きたくなってしまう。これは「可愛さが可愛さを呼ぶ現象」としてランドソル学会の注目を集めている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/50, /*level*/1, /*item_detail*/"壁も家具もピンクでそろえた人が最後にたどりつくのがピンクの床板。ランドソル伝統のハッピーカラーでおうちをトータルコーディネイト！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/51, /*level*/1, /*item_detail*/"シンプルで使い勝手のいい白のテーブル。どんなにぎやかな部屋にも溶け込んで落ち着きをもたらす。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/52, /*level*/1, /*item_detail*/"白い大きな机。シンプルながらもどこか高級感漂うデザイン性は、部屋をスタイリッシュにまとめたい人たちに好まれている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/53, /*level*/1, /*item_detail*/"白いシンプルな椅子。机と組み合わせれば、お屋敷のリビングのような素敵な空間を作り出すことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/54, /*level*/1, /*item_detail*/"本革のスツール。今は真っ白でパリッと張りがあるけど、経年とともに柔らかく、くたっといい感じになっていく。先々が楽しみな家具。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/55, /*level*/1, /*item_detail*/"生成りのソファ。ナチュラルな風合いはみんなに好かれている。座り心地は比較的固め。実用本位のインテリア。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/56, /*level*/1, /*item_detail*/"生成りの二人掛けソファ。置く場所を選ばない優秀な家具だが、食べこぼしで汚れやすいのが唯一にして最大の弱点。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/57, /*level*/1, /*item_detail*/"白を基調としたパイプベッド。とても清潔な雰囲気。なので、寝具を選ばないと病院みたいになってしまう。さじ加減は大事。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/58, /*level*/1, /*item_detail*/"中くらいの白いタンス。大きくもなく、真っ白で柄も模様もないので主張しない。逆にインテリアの主役になり得る。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/59, /*level*/1, /*item_detail*/"白いパーテーション。玄関から入ったお客さんにダイレクトに家の中を見せないワンクッションのために置いておくもの。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/60, /*level*/1, /*item_detail*/"ハーフサイズのラグマット。白いので置く床の色を選ばない。魔法で汚れにくい加工をほどこしてあるため土足でも安心。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/61, /*level*/1, /*item_detail*/"常に落書きされる危機にさらされている。この壁にするからにはエルフの森で採れる、強力な洗浄成分を持つ猛毒きのこを常備しておくことをすすめる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/62, /*level*/1, /*item_detail*/"真っ白い床板。床なのに汚しそうで踏みづらいと思われるかもしれないが、それが床というものなので踏んでください。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/63, /*level*/1, /*item_detail*/"白い螺旋階段。お気に入りの服を着て花束を両手に持ち、歌いながら下りてくる遊びがランドソル各所で流行中！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/64, /*level*/1, /*item_detail*/"下の階に下りるための白い階段。踏み板と踏み板に隙間がないので、物を落としてしまう心配がなくていい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/65, /*level*/1, /*item_detail*/"大陸の南方で採れる、アストライアエボニーという黒い木で作られたテーブル。多少インテリアにまとまりがなくても、これ一つ置いてあるだけでびしっと締まる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/66, /*level*/1, /*item_detail*/"黒い大きな机。モダンなデザインとアストライアエボニーの素材で、街の紳士たちに好まれている一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/67, /*level*/1, /*item_detail*/"黒いシンプルな椅子。単体でももちろんおしゃれだが、白い家具と組み合わせてモノクロのインテリアにするのも人気だ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/68, /*level*/1, /*item_detail*/"革張りのスツール。革にもランクがあり、中には魔物の革を染めたインチキ商品もあるけど、これは本物の本革。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/69, /*level*/1, /*item_detail*/"黒革のソファ。大人の雰囲気を醸し出す。座るだけで２割増し大人っぽく見える。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/70, /*level*/1, /*item_detail*/"二人掛けの黒革のソファ。汚れてもサッと拭けて尚且つ目立たない。ちびっ子のいたずら予防にも効果的。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/71, /*level*/1, /*item_detail*/"黒のパイプベッド。シックな色とデザインで、どんな壁紙や床にも合う。寝具もモノトーンで統一されているハイセンスなベッド。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/72, /*level*/1, /*item_detail*/"黒の小物入れ。簡易的ではあるが下２段には鍵がついている。大切なものや人に見せたくないものをしまうのにピッタリ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/73, /*level*/1, /*item_detail*/"黒一色のパーテーション。その向こうではいったい何が…とつい覗きたくなるが、決して覗いてはいけません。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/74, /*level*/1, /*item_detail*/"黒いハーフサイズのラグマット。シックでデザインもよく、満足度の高い一品ではあるが、洗濯するときだけは注意しないと色落ちの可能性がある。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/75, /*level*/1, /*item_detail*/"部屋がぐっと締まって見える、黒い壁紙。何を飾っても映えるため、住む人のセンスが問われる。インテリア上級者向け。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/76, /*level*/1, /*item_detail*/"意外にも他のどんな床板より足跡がはっきりと付く。黒のカッコよさを保つためには、こまめな拭き掃除が必須。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/77, /*level*/1, /*item_detail*/"黒い螺旋階段。どこが段差なのか大変わかりにくいので、照明をつけずに上り下りすることは安全上すすめられない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/78, /*level*/1, /*item_detail*/"下の階に下りるための黒い螺旋階段。地底世界に引っ張り込まれそうとか、地獄に続いてるんじゃないかとか、黒いというだけでみんなの想像力を掻き立てる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/79, /*level*/1, /*item_detail*/"真っ赤な一人掛けソファ。総シルク張りの高級品。ランドソルでは、今のところウィスタリア家のみが取り扱っている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/80, /*level*/1, /*item_detail*/"二人掛け高級志向ソファ。一人掛けと対になっている。一人占めしてゆったり寝そべる使い方もおすすめ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/81, /*level*/1, /*item_detail*/"赤地に金糸で縫い取りをしてある高級感のあるラグマット。物を置いておくときの目印としても役立つ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/82, /*level*/1, /*item_detail*/"濃さの違う色の糸を組み合わせて模様を織り込んだ絨毯。柄はアストルムの伝統的なもの。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/83, /*level*/1, /*item_detail*/"赤い布張りの螺旋階段。布のおかげで足音が響かない。一見王宮にもありそうなデザインだがこれでお値段は他と変わらない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/84, /*level*/1, /*item_detail*/"このきらびやかな階段を下りる少女たちの姿はまさに、舞踏会へと向かうお姫様のように映るだろう。　", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/85, /*level*/1, /*item_detail*/"レトロな雰囲気漂う樽は、設置すると、しゃれた喫茶店のような大人の空間を作り出せる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/86, /*level*/1, /*item_detail*/"古くからある有名ブランドのシックな茶色のソファ。革の良い香りと、落ち着いた色合いが大人の時間を演出する。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/87, /*level*/1, /*item_detail*/"古くからある有名ブランドのシックな茶色の二人掛けソファ。クッションを枕にして横になれば、ベッドに負けないほどの安眠をもたらすだろう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/88, /*level*/1, /*item_detail*/"少し大きめの観葉植物。緑のある生活に癒されてみませんか？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/89, /*level*/1, /*item_detail*/"小ぶりな観葉植物。インテリアとしても、風水としても手軽な一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/90, /*level*/1, /*item_detail*/"三色の美しい花々。これ一つで女子力大幅アップ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/91, /*level*/1, /*item_detail*/"白い陶器の花瓶。どんな花を入れても綺麗に映えるが、太陽のようなひまわりは相性抜群。部屋に活気がでることだろう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/92, /*level*/1, /*item_detail*/"王宮感漂うロイヤルな白い暖炉。大理石でできた高級感と、細部までこだわった意匠にうっとりすること間違いなし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/93, /*level*/1, /*item_detail*/"落ち着いた雰囲気の煉瓦の暖炉。昔ながらのそのデザインは、いまにもサンタクロースが出てきそうでわくわくする。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/94, /*level*/1, /*item_detail*/"収納ばっちりな食器棚。下の引き戸には調味料などをしまうことができるありがたい一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/95, /*level*/1, /*item_detail*/"子供は近づいてはいけない薬棚。きちんと知識のある人と使おう！　決して変な薬を作って誰かに盛ってはいけない…", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/96, /*level*/1, /*item_detail*/"木製のボトルラック。豊富な種類の飲み物が保管されている。のどが渇いたらここに来よう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/97, /*level*/1, /*item_detail*/"昔ながらのシックな置時計。心地よい秒針の響きからは、職人のこだわりが感じられる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/98, /*level*/1, /*item_detail*/"ロイヤルな白い置時計。王宮の一室にあるような高級感あるデザインは老舗のブランドによるもの。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/99, /*level*/1, /*item_detail*/"食材調達に重宝する釣竿。釣りが趣味の少女がよく愛用している。丈夫でよくしなるとあってランドソルでは普及率が高い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/100, /*level*/1, /*item_detail*/"みんな大好き、可愛い猫のぬいぐるみ。素朴でどこか懐かしいその姿に、犬派もメロメロ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/101, /*level*/1, /*item_detail*/"由緒正しき王宮騎士団の鎧。鉄のプレートをつなぎ合わせたそれは、あらゆる攻撃をふせぐことができる。夜中になると動き出す…なんてことはない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/102, /*level*/1, /*item_detail*/"美しい純白のドレス。女の子なら一度は着たい一着。舞踏会やパーティーにはもってこい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/103, /*level*/1, /*item_detail*/"可愛らしいピンクのドレス。リボンやフリルがあしらわれ、とてもキュートな一着。有名デザイナーが手掛けたもので、ランドソルの女子の間で流行中。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/104, /*level*/1, /*item_detail*/"どこかのドジっ子メイドを彷彿とさせるメイド服。これを着て『おかえりなさいませ、ご主人様』と言えば、誰もが萌え萌えキュン間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/105, /*level*/1, /*item_detail*/"美しい女性の姿をかたどった彫像。街にはこの彫像が置いてあるデートスポットが存在し、なんと部屋の中に疑似的にその場所を作ることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/106, /*level*/1, /*item_detail*/"魔法使いとして名をはせた老人をかたどった彫像。強い力を持っていた老人は、アストライアの歴史に名を残し、今もなお多くの魔法使いから目標とされている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/107, /*level*/1, /*item_detail*/"数種類の花々で作られた壁掛け。とても華やかな一品で、なんの工夫をせずとも、飾るだけで部屋がおしゃれになる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/108, /*level*/1, /*item_detail*/"有名な画家の絵画。風景画で、どこか哀愁の漂う作品となっており、壁に飾ることで洗練されたインテリアに見える。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/109, /*level*/1, /*item_detail*/"トンドと言われる円形の絵画。描かれている人物のモデルは誰かわからないが、みんなそんなに興味もないのでそのままにしている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/110, /*level*/1, /*item_detail*/"鉄製の盾の壁掛け。騎士道感漂うインテリアとして一部に人気らしい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/111, /*level*/1, /*item_detail*/"二本の剣が飾られた壁掛け。インテリア用なので実用性はないが、騎士の家のような内装にすることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/112, /*level*/1, /*item_detail*/"おしゃれな黒い手すり。パーテーションとしても使用ができるため、インテリアの幅が広がる一品だ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/113, /*level*/1, /*item_detail*/"おしゃれな白い手すり。植物を囲えば、綺麗な花壇のできあがり。飛び越えたりジャンプしたりしてはいけない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/114, /*level*/1, /*item_detail*/"ステンドグラスになっているパーテーション。部屋に設置すると、さながら教会のような内装に！　見て楽しめるパーテーションになっている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/115, /*level*/1, /*item_detail*/"煉瓦でできた立派なパーテーション。たくさん設置すれば部屋に迷路を作ることもできる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/116, /*level*/1, /*item_detail*/"石でできた強固な壁。敵が攻め込んできてもなかなか壊れない頑丈さだ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/117, /*level*/1, /*item_detail*/"大理石の壁。ひんやりと冷たく、暑いときはほっぺをくっつけるといいらしい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/118, /*level*/1, /*item_detail*/"煉瓦でできた壁。雨風なんて怖くない立派な作りだが、だからと言って乱暴に使用してはいけない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/119, /*level*/1, /*item_detail*/"石畳の床。頑丈ではあるが、この上でのじゃれ合いは危ないのでやめよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/120, /*level*/1, /*item_detail*/"大理石の床。綺麗目な家具がよく合い、高級感あるインテリアにすることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/121, /*level*/1, /*item_detail*/"煉瓦の床。部屋がどこか異国情緒あふれる雰囲気に大変身。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/122, /*level*/1, /*item_detail*/"モザイクタイルの床。美しく意匠を凝らしており、お城のようなインテリアとよく合う。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/123, /*level*/1, /*item_detail*/"モノクロタイルの床。家具も白黒に統一することで、こだわりのある部屋にはや変わり！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/124, /*level*/1, /*item_detail*/"赤白タイルの床。どんなインテリアにも合うデザイン。ポップな雰囲気を作り出すことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/125, /*level*/1, /*item_detail*/"キュートなピンクの背景素材。女の子らしいデザインは、少女たちの可愛さをより引き立ててくれる。ラブリーな家具などとは相性が良い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/126, /*level*/1, /*item_detail*/"淡いピンクの背景素材。控えめなキュートさはどこか大人っぽい雰囲気を醸し出している。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/127, /*level*/1, /*item_detail*/"綺麗なブルーの背景素材。落ち着いた青は心を安らげてくれる。慎ましい家具などと相性が良い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/128, /*level*/1, /*item_detail*/"淡いブルーの背景素材。その青は少女たちを優しく包み込むような色になっている。どんなインテリアにも合う背景。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/129, /*level*/1, /*item_detail*/"イエローの背景素材。日差しのような暖かな雰囲気に、少女たちは元気満点になるにちがいない。明るいインテリアと相性が良い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/130, /*level*/1, /*item_detail*/"淡いイエローの背景素材。日向のように暖かい背景に少女たちもまどろみの中にいるような気分になるだろう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/131, /*level*/1, /*item_detail*/"とてもリアルな虎の敷物。裸足でいてもこの上ならまったく冷えない優れもの！　いまにも叫びだしそうな迫力がある。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/132, /*level*/1, /*item_detail*/"白のタイル。他のタイルと交互に使っておしゃれなチェック柄インテリアができる。汎用性の高いタイルだ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/133, /*level*/1, /*item_detail*/"灰色のタイル。渋いモダンな雰囲気を演出できる。インテリア上級者向け。是非お試しあれ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/134, /*level*/1, /*item_detail*/"赤いタイル。パンチのある色で部屋の雰囲気を一気に変えよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/135, /*level*/1, /*item_detail*/"青色のタイル。クールな、静けさのある部屋づくりをしたい人におすすめのアイテム。清涼感ある見た目なので暑がりの人にはいいかも？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
