INSERT INTO `room_item_detail` VALUES (/*room_item_id*/143, /*level*/1, /*item_detail*/"カフェにありそうなメニューボード。マーカーやチョークで彩られた黒板に書かれるのは、ほとんど『本日のおやつ』メニュー。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/144, /*level*/1, /*item_detail*/"『雲海の山脈』を制覇した証。山頂付近にのみ出現する貴重な魔物の肉。最高級の霜降りで、ほっぺたが落ちそうな美味しさ。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/145, /*level*/1, /*item_detail*/"『密林の大樹』を制覇した証。通称『世界一おいしいフルーツ』。刃を容易に通さないほど硬い外皮を持つが、その果肉は絶品。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/146, /*level*/1, /*item_detail*/"『断崖の遺跡』を制覇した証。遺跡で手に入れたレシピで作った点心セット。希少な肉や野菜などをふんだんに使った至高の料理。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/147, /*level*/1, /*item_detail*/"『蒼海の孤塔』を制覇した証。塔の頂上にのみ舞い降りる、極上の甘さを有した淡雪で作った綿飴。神すら魅了する甘味が、食べた者を幸福の絶頂へと導く。", /*lvup_trigger_type*/101, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/148, /*level*/1, /*item_detail*/"羽根がみっちりと詰まったふかふかのクッション。お部屋にアクセントを加えるビビットなピンク色のカバーがポイント！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/149, /*level*/1, /*item_detail*/"パーテーションがわりにも使える、ドレッシーなカーテン。高品質な桃色の布地は見た目はもちろん手触りも素晴らしく、ずっと触れていたくなってしまう心地良さ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/150, /*level*/1, /*item_detail*/"ホワイトとイエローの２色で塗り分けられたカォンの柱。どのようなジャンルの家具にも馴染むシンプルなデザインをしている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/151, /*level*/1, /*item_detail*/"マホの厳選チョイスにより選び抜かれた、とびきりのフワフワぬいぐるみがいっぱい。眺めて幸せ、触って幸せ、抱きしめて幸せの三拍子が揃った夢の楽園。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/152, /*level*/1, /*item_detail*/"団員たちの武器が入った樽。急な出立の際も素早く得物を手に外へ出ることができる。ときどき武器同士が絡まって樽ごと倒れてしまうのがたまにきず。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/153, /*level*/1, /*item_detail*/"絶対開けるなよ！　とマコトに念を押されている、いわく付きの宝箱。とても重くマコトにしか持ち上げられない。様々な噂があるが実際なにが入っているのかは、彼女しか知らない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/154, /*level*/1, /*item_detail*/"ぼんやりと淡い光が部屋全体を包み込む、癒やしオーラばっちりのランプ。明るすぎず暗すぎない光が、任務で疲れた身体と心をメルヘンチックな気分にいざなってくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/155, /*level*/1, /*item_detail*/"メルヘンチックな雰囲気が漂うカォンの壁。これに囲まれればおとぎ話の登場人物のような気分になり、どこでもそこはマホマホ王国。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/156, /*level*/1, /*item_detail*/"ネイティブ柄のラグやマットが敷き詰められた床。エスニックな雰囲気とカラフルな配色から個性的な印象を受ける。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/157, /*level*/1, /*item_detail*/"ホワイトとピンクの色彩がかわいらしいキュートな螺旋階段。その階段を上れば、まるでお姫様気分を体験することができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/158, /*level*/1, /*item_detail*/"下階へと下りるためのキュートな螺旋階段。長く伸びた柱の一部はくり抜かれ、ろうそくを立てるためのキャンドルホルダーとなっている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/201, /*level*/1, /*item_detail*/"漆塗りの椅子。伝統的な技法で作られており、職人の強いこだわりが感じられる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/202, /*level*/1, /*item_detail*/"漆塗りの机。上品で高級感のあるデザインからか、ランドソルの奥様方に人気の家具。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/203, /*level*/1, /*item_detail*/"漆塗りの座敷椅子。表面にあしらわれた桜模様が、春らしさを演出してくれる。もちろん、座り心地も良い。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/204, /*level*/1, /*item_detail*/"畳の床。イグサを編み込んで作られており、寝転がると良い香りがする。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/205, /*level*/1, /*item_detail*/"きらびやかな金箔の屏風。風よけや仕切りとしてだけでなく、芸術品としても扱われる一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/206, /*level*/1, /*item_detail*/"桜の描かれた置行燈。温かみのある木材と、上質な和紙を使用して作られている。灯りをともせば、桜模様がより一層美しく見える。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/207, /*level*/1, /*item_detail*/"ランドソルで流行りの茶屋を模して作られたインテリアグッズ。お菓子やお茶をここで食べれば、風情が溢れること間違いなし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/208, /*level*/1, /*item_detail*/"祭事の際に使われる鈴。主に舞で使用されており、振るう度に心地の良い鈴の音が響き渡る。しかし、シャンシャンしすぎると周りに迷惑になるので注意しよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/209, /*level*/1, /*item_detail*/"春の代名詞である桜の木。これさえ置けば、部屋がお花見会場に早変わり。舞い散る花びらと桜の香りが、春の訪れを感じさせてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/210, /*level*/1, /*item_detail*/"春をテーマにした襖絵が描かれた壁。東の国からの輸入品で、部屋を明るく華やかな雰囲気にしてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/211, /*level*/1, /*item_detail*/"赤絨毯が敷かれた、落ち着いた色合いの床。華やかな春の家具を、より一層引き立ててくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/301, /*level*/1, /*item_detail*/"スポンジでできたお手軽サイズのスツール。丈夫で軽く、ぶつかっても痛くないため、お子様も安心して使える一品。ピンクはかわいいうさぎのシルエット入り！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/302, /*level*/1, /*item_detail*/"スポンジでできたお手軽サイズのスツール。丈夫で軽く、ぶつかっても痛くないため、お子様も安心して使える一品。オレンジはかっこいい手裏剣のシルエット入り！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/303, /*level*/1, /*item_detail*/"スポンジでできたお手軽サイズのスツール。丈夫で軽く、ぶつかっても痛くないため、お子様も安心して使える一品。バイオレットはエレガントなちょうちょのシルエット入り！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/304, /*level*/1, /*item_detail*/"お友達を部屋に呼びたくなるキッズサイズの花柄ローテーブル。天板は汚れてもさっとひと拭きでキレイになる、お手入れ簡単な特殊加工が施されている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/305, /*level*/1, /*item_detail*/"魔物のイラストをラグに仕立てた大ヒットシリーズ。普段は恐いバルーンバードも、かわいいラグなら子ども部屋に彩りを添えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/306, /*level*/1, /*item_detail*/"これでキミも魔法使いの仲間入り！　手をかざせば光のちょうちょが飛び出す不思議な水晶。じっくり中を覗けば未来が見えるかも？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/307, /*level*/1, /*item_detail*/"今日からここが特等席！　思わず抱きしめたくなっちゃうプチグリフォンの小さな木馬。かわいらしい見た目が好評で、女性がインテリアとして購入することも。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/308, /*level*/1, /*item_detail*/"いたずら好き必見！　ビッグサイズの特製びっくり箱。何が出るかは、開けてからのお楽しみ！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/309, /*level*/1, /*item_detail*/"どこか懐かしい雰囲気を漂わせるファンシーなイラストが描かれた壁。胸躍る空間は子どもたちに夢のような時間を提供してくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/310, /*level*/1, /*item_detail*/"パステルカラーでまとめ上げられた明るい印象のフロア。かわいらしい小物とコーディネートすれば、気分はまるでリトルリリカルに。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/401, /*level*/1, /*item_detail*/"歌が大好きな人にオススメの大迫力ステージセット。人気アイドルグループが使っているステージセットのレプリカで、一基のメインユニットと二基のサイドユニットを組み合わせることで、本来のセットが完成するよ。３つのユニットがお部屋に揃ったとき、何か特別なできごとが起きるかも！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/402, /*level*/1, /*item_detail*/"歌が大好きな人にオススメの大迫力ステージセット。人気アイドルグループが使っているステージセットのレプリカで、このサイドユニット二基にメインユニットを一基組み合わせることで、本来のセットが完成するよ。もちろん単体でも遊べるので、設置してお気に入りの女の子のリアクションを楽しんでみよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/403, /*level*/1, /*item_detail*/"某トップアイドルがライブで着用しているきらびやかなステージ衣装のレプリカ。銀河をモチーフにした神秘的なデザインは、見るものを夜空へと誘ってくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/404, /*level*/1, /*item_detail*/"ホテルやイベント会場でお馴染みの間仕切りポール。上質な空間にぴったりの格調高いアイテム。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/405, /*level*/1, /*item_detail*/"イベントで疲れた身体を癒してくれる白銀のベンチ。王宮の調度品を思わせる豪奢なデザインは、お部屋をいつもよりちょっとリッチな雰囲気にしてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/406, /*level*/1, /*item_detail*/"人気アイドルグループのシンボルマークが描かれた小さくかわいいタペストリー。観賞用に保管用、布教用と、複数買いマストアイテム。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/407, /*level*/1, /*item_detail*/"人気アイドルグループのシンボルマークが描かれた大きな旗。室内の飾りだけでなく、パーテーションや寝室の目隠しなど、その使い方は自由自在。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/408, /*level*/1, /*item_detail*/"歴史的な建築物を思わせるシンプルだが貫禄ある白銀の柱。人気アイドルグループのシンボルマークが描かれた小さな垂れ幕が全体のイメージを一層引き立てている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/409, /*level*/1, /*item_detail*/"星型のチャームや、グラデーションが美しいすみれ色のカーテンドレープが印象的な内壁。薄暮をイメージさせる優雅な内装は、訪れたものを魅了する。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/410, /*level*/1, /*item_detail*/"某アイドルユニットのシンボルマークが描かれた純白のダンスフロア。ステージを設置すれば、そこはまさにライブ会場！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/501, /*level*/1, /*item_detail*/"夏の気配を感じさせるハンモック。寝苦しい日々を過ごすキミにピッタリ！　波に揺られるような心地よさの中でおやすみなさいっ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/502, /*level*/1, /*item_detail*/"アラベスク模様のパーテーション。みんなでリゾート気分を楽しもう！　違った味わいを出したいなら、何枚か置いてはいかが？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/503, /*level*/1, /*item_detail*/"おしゃれなデザイナーズソファ。青と白のコントラストが爽快さを演出！　座っていると波音が聞こえてくるかも？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/504, /*level*/1, /*item_detail*/"包み込むような二人掛けソファ。一人で広々使っても、二人で仲良く座ってもいい感じ！　実はクッション部分を取り外せるよ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/505, /*level*/1, /*item_detail*/"デザイン重視の食器棚。中央棚に植物や小物を置けば、おしゃれ度アップ！　インテリアとしても魅力的に！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/506, /*level*/1, /*item_detail*/"青を基調としたチェスト。丁度いい大きさだから、配置が簡単！　置き場に困った小物はこのチェストの上へご案内！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/507, /*level*/1, /*item_detail*/"爽快さを感じさせるクローゼット。時間が経っても色あせない！　鮮やかな白と青で彩りは決まり！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/508, /*level*/1, /*item_detail*/"背もたれありの青い丸椅子。実は結構柔らかい素材でできているので、座り心地がグッド！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/509, /*level*/1, /*item_detail*/"背もたれなしの青い丸椅子。シンプルで愛らしいシルエットは、幅広い層に人気！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/510, /*level*/1, /*item_detail*/"汎用性が高い吊り棚。色々なところに吊れて便利！　壁に彩りが欲しい時にもどうぞ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/511, /*level*/1, /*item_detail*/"上品さを漂わせる白いテーブル。このテーブルの上で手紙を書けばとてもリッチな気分になれるかも…！？　青のテーブルクロスはセットなのでとってもお得！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/512, /*level*/1, /*item_detail*/"夏を思わせる白テーブル。ロイヤルな青のテーブルクロスと中央に飾られた花で、今日を特別な日にしよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/513, /*level*/1, /*item_detail*/"リゾート地を連想させる色合いの壁。窓から外をのぞくと、海が見えそうな気がしてくるから不思議！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/514, /*level*/1, /*item_detail*/"映える青味の床。寒色系のインテリアとの相性は抜群！　見た目も涼しいので暑い時期には特におすすめ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/601, /*level*/1, /*item_detail*/"鮮やかで美しい水槽。何気なく見ているだけでも、心が穏やかに！　癒し効果が抜群の一品！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/602, /*level*/1, /*item_detail*/"柔らかな弾力が魅力のシードレイクの乗り物。お子様だけでなく、女性や男性、老若男女に人気！　インテリアとしてもリラクゼーションアイテムとしても、使える有能な子！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/603, /*level*/1, /*item_detail*/"人魚の気分を味わえる貝クッション。魚たちが元気に泳ぐ姿が目に浮かびそう！　座れば海の王子様になれるかも！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/604, /*level*/1, /*item_detail*/"ほんのり冷たいスツール。未体験の座り心地を与えてくれる！　この魅力は座った人にしかわからない！　最適な涼しさも提供してくれるよ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/605, /*level*/1, /*item_detail*/"海を切り取ったようなパーテーション。水槽でもあり、仕切りでもあり、インテリアでもある優れもの！　部屋中を囲むとまるでそこは海！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/606, /*level*/1, /*item_detail*/"美しいサンゴの置物。色鮮やかで、インテリアに最適！　部屋をマリン風にしたいならまずはコレ！　置くだけで魚になった気分を味わえそう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/607, /*level*/1, /*item_detail*/"色々な場所で映えるお魚シール。部屋中に貼れば、お魚天国の出来上がり！　お魚好きにとっては必需品！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/608, /*level*/1, /*item_detail*/"神秘的な雰囲気の背景。目を開けるとそこは海の国！？　お友達を招けば驚くと同時に喜んでくれるはず！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/609, /*level*/1, /*item_detail*/"非日常を演出する海底の壁。これがあれば海底散歩を楽しめること間違いなし！　熱帯魚のステッカーと相性抜群！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/610, /*level*/1, /*item_detail*/"海の空間を満喫できる床。君の部屋が、日常とかけ離れた場所に様変わり！　海の世界へようこそ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/611, /*level*/1, /*item_detail*/"人魚の気分を味わえる貝クッション。魚たちが優雅に泳ぐ姿が目に浮かびそう！　座れば海のお姫様になれるかも！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/612, /*level*/1, /*item_detail*/"ランドソルお米マイスター監修による、特注仕立てのおにぎりの着ぐるみ。その一粒一粒には、細部へのこだわりだけでなく、真心も込められている。実用性も追求しており、他にはない極上の着心地は、まさにおにぎりそのもの。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/701, /*level*/1, /*item_detail*/"【王宮騎士団（NIGHTMARE）】が修練で使う大砲。見た目は本物そっくりだが、実弾のかわりに可愛らしい星が飛び出す。パーティーのドッキリアイテムとしても活躍しそう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/702, /*level*/1, /*item_detail*/"【王宮騎士団（NIGHTMARE）】御用達の木人。本来は騎士見習いの修練に用いられるが、一般家庭からもエクササイズグッズとして注目を集め始めており、今後流行アイテムとなりそうな一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/703, /*level*/1, /*item_detail*/"眼光鋭く、来客を迎えるライオンの噴水。勢いよく流れる水は、癒し効果よりも緊張感を演出する。背を向けて１ルピを投げ込むと、願い事が叶うという噂がある。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/704, /*level*/1, /*item_detail*/"ジュンが王宮内に備えているスペアの鎧。置いてあるだけでも、ただならぬ威圧感が放たれている。埃一つ付いていないことから、常に手入れされていることがわかる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/705, /*level*/1, /*item_detail*/"マツリが修練に使うスペア用の盾。子供でも簡単に扱うことができるように、軽量な素材で作られている。厳しい修練を受けていると思われる傷跡があちこちに刻まれている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/706, /*level*/1, /*item_detail*/"上の階に登る王宮調の階段。大陸随一の腕前といわれる職人によるデザインで、屈指の美しさに仕上がっている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/707, /*level*/1, /*item_detail*/"下の階に下りる王宮調の階段。ピカピカに磨かれているため、滑って転がり落ちないように注意が必要！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/708, /*level*/1, /*item_detail*/"王宮のそれと同じく、特別な生地で織られたエレガントなタペストリー。真っ赤な色合いが、お部屋に気品と風格を加える。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/709, /*level*/1, /*item_detail*/"王宮に備え付けられているウォールランプ。オシャレな空間には、間接照明が必須！　ワンランク上のお部屋作りのために、あなたも使ってみては？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/710, /*level*/1, /*item_detail*/"品格そなわる王宮調の壁。純白を基調にしており、全面から高貴な雰囲気が漂う。窓の外から流れ込む風も、どこか違って感じるはず。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/711, /*level*/1, /*item_detail*/"清潔感に溢れる王宮調の床。歩くとコツンコツンと良い音が響き渡るため、音響面でも格調高さを演出してくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/801, /*level*/1, /*item_detail*/"何が起こるか分からない、ハロウィンらしい仕掛け満載のバスケット。これがあればハッピーな毎日間違いなし！　みんなで言おう、トリックオアトリート！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/802, /*level*/1, /*item_detail*/"ワクワクするハロウィンテーブル。お菓子を貰ったら、このテーブルでパーティーを開こう！　愉快なオバケたちと仲良くなれるかも！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/803, /*level*/1, /*item_detail*/"キュートな円型のハロウィンテーブル。普段とは違った雰囲気を演出してくれる！　今日は特別な一日になりそう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/804, /*level*/1, /*item_detail*/"可愛さ満点のスタンド。その圧倒的な存在感は他の追随を許さない！？　置けば、一気にお部屋がハロウィン色に！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/805, /*level*/1, /*item_detail*/"ハロウィン風のキャンドルスタンド。一日中ハロウィン気分を味わえる！　眺めているだけでリラックスできるかも！　", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/806, /*level*/1, /*item_detail*/"人気者のカボチャのオーナメント。ハロウィンの必需品であり、マスコットでもある置物！　みんなに愛されてるとってもニクいヤツ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/807, /*level*/1, /*item_detail*/"カボチャの飾りが可愛い椅子。部屋にあるだけで、ハロウィンらしさを演出してくれること間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/808, /*level*/1, /*item_detail*/"ハロウィンカラーの愛らしい見た目が、万人に人気のスツール。軽くて丈夫で使い勝手の良い、パーティーの隠れた名脇役！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/809, /*level*/1, /*item_detail*/"見るからに柔らかそうなソファ。座ればわかる、このソファのふかふかすぎる快適さ！　これがハロウィンの魔力なのかも！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/810, /*level*/1, /*item_detail*/"安眠を確約するハロウィン仕様のベッド。横になっただけで睡魔が押し寄せてくる！　悪夢なんてとんでもない！　目覚めれば元気いっぱい！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/811, /*level*/1, /*item_detail*/"ハロウィンデザインの食器棚。可愛らしい食器たちがテーブルに並べば、ハロウィンパーティの始まり！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/812, /*level*/1, /*item_detail*/"常に人気なカボチャのチェスト。夏服から冬服までなんでもござれの収納力！　愛嬌のあるデザインは、人々の心をつかんで離さない！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/813, /*level*/1, /*item_detail*/"チャーミングなパーテーション。中央に施されたカボチャがとってもラブリー！　覗ける部分が悪戯心をくすぐる！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/814, /*level*/1, /*item_detail*/"ハロウィン気分を盛り上げてくれる壁。月が幻想的な雰囲気を作り出してくれる！　いざ、ハロウィンワールドへ！　", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/815, /*level*/1, /*item_detail*/"ハロウィンの雰囲気にぴったりの床。ランプやテーブル、たくさんの料理を用意して、ダンスパーティーを開くのもいいかも？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/816, /*level*/1, /*item_detail*/"心震わす曲や、想い出の曲など、様々な曲を再生してくれるサウンドボックス。アンティークデザインならではの美しい旋律は、持ち主に音楽という安らぎを与えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/901, /*level*/1, /*item_detail*/"スイーツを焼くための本格的なオーブン窯。高温で温められた生地は、サクサクとした仕上がりになる。趣味のお菓子作りからホームパーティまで、幅広く活躍すること間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/902, /*level*/1, /*item_detail*/"コーヒーを淹れるために必要な道具が収められたキッチンボード。サイフォン式コーヒーメーカーから漂う芳醇な豆の香りは、室内を豊かな雰囲気で満たす。ひと手間かけた一杯は、日常を特別なものに変えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/903, /*level*/1, /*item_detail*/"オシャレなカフェランプ。部屋に落ち着きを与えるデザインに仕上がっており、ほどよく抑えられた明るさが、大人なムードを演出してくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/904, /*level*/1, /*item_detail*/"カフェ気分のウッドテーブルとチェアセット。テーブルの上に乗っている美味しい一杯とスイーツセットを食べながら、優雅なひと時を楽しもう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/905, /*level*/1, /*item_detail*/"木製のカフェチェア。何時間でも座っていられる座り心地の良さ。飲食を楽しんだり、物思いにふけるのに最適な一脚。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/906, /*level*/1, /*item_detail*/"しっかりとした作りの木製カウンターテーブル。調理をしながら、対面にいる人と会話することができる。幅広い調理スペースを有効活用して、いろんなメニューを作ってみよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/907, /*level*/1, /*item_detail*/"アクセントに植物を装飾した木製のパーテーション。周りの気になる視線をブロックできるので、カフェのインテリアには必需品のひとつ。心置きなくガールズトークに華を咲かせよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/908, /*level*/1, /*item_detail*/"ポップなイラストが目を引くカフェボード。丁寧な描き込みから、店員さんの気配りが窺い知れる。おすすめメニューをチェックしてみよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/909, /*level*/1, /*item_detail*/"上の階に登るための木製はしご。折りたたみ式なので、どこでも簡単に持ち運ぶことができる。登りたいと思った場所に登ってみよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/910, /*level*/1, /*item_detail*/"下の階に降りるための木製はしご。シンプルな作りだが、安定感はバツグン！　安心して降りることができるが、慌てずに一歩ずつゆっくりと降りることを心がけよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/911, /*level*/1, /*item_detail*/"レトロデザインの正統派なカフェの壁。その落ち着きのある内装は、美味しい一杯を期待させる。お部屋にいながら、豆の香り漂う素敵なカフェ気分を味わうことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/912, /*level*/1, /*item_detail*/"天然石を組み合わせた石畳の床。白を基調にした清潔感のある色合いは、カフェのインテリアにぴったりな仕上がりで、オシャレな印象を部屋に与えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/913, /*level*/1, /*item_detail*/"緑あふれる樹木に遊び心を詰め込んだネビアのおうち。どこからか持ち込んだティーセットと、柔らかいクッションが詰め込まれたゆりかご、鳥たちも一休みに訪れる快適空間です。眺めていると、楽しそうに飛び跳ねるネビアが見られるかも？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1001, /*level*/1, /*item_detail*/"物語が飛び出す魔法の絵本。クリスマスの奇跡を描いたこの一冊は、これまでの絵本とは一線を画すスケールで描かれている。見ているだけで自分も本の世界へと入り込んだかのような没入感を味わうことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1002, /*level*/1, /*item_detail*/"リマ監修、協力のもと作られた着ぐるみ。これを作るためにリマはサンタ衣装を自前で用意するほどのこだわりを見せた。極上のフワモコ感を忠実に再現しながらも、動きやすいよう軽量化されている。リマのアイディアがふんだんに盛り込まれた着ぐるみを着て、さぁ、みんなもレッツダンス！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1003, /*level*/1, /*item_detail*/"きらびやかな電飾とキュートなリボンで飾り付けられたクリスマスツリー。さまざまな発光パターンが見るものを楽しませてくれる。天辺の星は誰が飾るのか、仲良く決めてね。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1004, /*level*/1, /*item_detail*/"リースとキャンドル、オーナメントで飾り付けられた暖炉。ゆらゆらと灯るキャンドルの明かりが人々に安らぎをもたらす、心あったまる一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1005, /*level*/1, /*item_detail*/"クリスマスのために気合を入れて飾り付けられたテーブル。ミニサイズのツリーとキャンドルで飾られた食卓で、グラスを片手にメリークリスマス！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1006, /*level*/1, /*item_detail*/"クリスマスのため、丁寧に飾り付けられたテーブル。大きな料理は載せられないけれど、クリスマスの雰囲気づくりに大いに役立ってくれる、陰の立役者。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1007, /*level*/1, /*item_detail*/"ツリーの形を模したブックシェルフ。飾り付けられたベルが可愛らしく、クリスマス気分を盛り上げてくれる。もちろん実用性も抜群で、下段には引き出し収納も完備。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1008, /*level*/1, /*item_detail*/"サンタクロースが座る隙間がないほど、たくさんのプレゼントが積み込まれたそり。いい子にしていた子供たちに、年に一度のご褒美を届けよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1009, /*level*/1, /*item_detail*/"赤と緑、色違いの角が可愛いトナカイのぬいぐるみ。夜になるとそりを引き、星空を駆けているかもしれない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1010, /*level*/1, /*item_detail*/"普通とは一風変わった色合いをしたトナカイのぬいぐるみ。夜になるとそりを引き、雪原を走っているかもしれない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1011, /*level*/1, /*item_detail*/"トナカイの背に乗りたいという願望を叶えてくれる画期的な椅子。クリスマステーブルの横に並べれば、遊び心であふれた食卓が出来上がります。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1012, /*level*/1, /*item_detail*/"ブッシュ・ド・ノエルの質感を再現した、思わずかじりつきたくなるスツール。実際に食べることはできないが、座るとどこからか甘い香りを漂わせるという。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1013, /*level*/1, /*item_detail*/"クリスマスカラーが派手な二人掛けのソファ。意中の人と二人で座れば、特別な気分に浸ることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1014, /*level*/1, /*item_detail*/"クリスマスカラーが派手な一人掛けのソファ。特別な夜に、弾むようなクッション性を独り占め！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1015, /*level*/1, /*item_detail*/"フレームがそりの形をした特注品のベッド。サンタクロースも思わず熟睡してしまうような寝心地だという。枕元に吊るされた靴下は伸縮性抜群で、大きなプレゼントもばっちり入れられる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1016, /*level*/1, /*item_detail*/"クリスマス用のガーランドで彩られた壁。特別な夜のワクワク感を部屋中に振りまく、聖夜に欠かすことのできない一品です。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1017, /*level*/1, /*item_detail*/"床に敷かれているのは、暖かな色合いが聖夜にふさわしい円形のラグ。雪降る夜も足元あったか、思わず寝ころびたくなること間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1018, /*level*/1, /*item_detail*/"ふっくらまんまるの鏡餅。ずっしりしたボリューム感とふっくらした丸さは、豊かな新年の訪れを感じさせてくれる。これを部屋に飾って、充実した一年をスタート！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1019, /*level*/1, /*item_detail*/"新年を鮮やかに飾る門松。これを置くだけで正月気分がグッと高まり、厳かな気持ちで新たな年を迎えられる。鏡餅と合わせて飾り、新年の挨拶を交わそう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1101, /*level*/1, /*item_detail*/"星で飾られたティピーテント。積もりたての雪原を思わせるクッションに身を投げ出せば、ふわふわと夢のようなひと時を過ごすことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1102, /*level*/1, /*item_detail*/"ゆらゆらした座り心地のロッキングチェア。体を預けて椅子を揺らせば、ゆったりのんびりとリラックスできる。何時間でも座っていられる快適さに大満足の一脚。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1103, /*level*/1, /*item_detail*/"雪国イチのゴージャスマダム、マダムピックのぬいぐるみ。背中に乗れば優しい羽毛がふんわりと体を受け止め、極上のモフモフ感を味わえる。豪華絢爛な尾羽まで完全再現した珠玉の一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1104, /*level*/1, /*item_detail*/"丸い形がかわいらしいだるまストーブ。見た目とは裏腹に部屋をしっかりと暖める力強さも持ち合わせている。冷え込む冬の必需品！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1105, /*level*/1, /*item_detail*/"落ち着いた配色がクールなサイドテーブル。上に載ったぬいぐるみがチャーミングさを演出する。リラックスしたいとき、傍に置いておきたい一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1106, /*level*/1, /*item_detail*/"雪のように白いニットで作られたまん丸クッション。見た目通りのふっくら弾力感は、座った人を骨抜きにしてしまう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1107, /*level*/1, /*item_detail*/"夜のように黒いニットで作られたまん丸クッション。見た目通りのもっちり柔軟性は、座った人を虜にしてしまう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1108, /*level*/1, /*item_detail*/"冬の静かな時間を演出してくれる出窓。開放感のあるガラス窓から見えるオーロラは迫力満点！　星々の輝く夜空を眺めながら、贅沢な冬のひと時を過ごそう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1109, /*level*/1, /*item_detail*/"舞い落ちる雪をイメージしたガーランド。飾られた氷の結晶にはほのかな輝きが灯り、部屋をキラキラと照らしてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1110, /*level*/1, /*item_detail*/"ホワイトとブルーグレー、二色の色合いがマッチした壁。ほんのり淡い色彩に心落ち着く一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1111, /*level*/1, /*item_detail*/"暖かそうなラグが敷き詰められた床。バラエティ豊かな形をしたラグに寝転がれば、様々な肌触りを堪能できる。お友達をたくさん呼んで、みんなでゴロゴロしたくなること間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1112, /*level*/1, /*item_detail*/"しんしんと雪の降り積もる背景。氷晶の輝く情景は、冷たくも美に満ちた冬の息吹を感じさせてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1113, /*level*/1, /*item_detail*/"とっても甘いバレンタインのために、美味しいケーキを作り上げた者へ贈られるトロフィー。金色に輝くケーキは新たなスイーツの可能性を感じさせるとともに、甘いものへの食欲をかき立ててくれる。食べ過ぎにはくれぐれも注意！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1201, /*level*/1, /*item_detail*/"一周年という記念すべき日を盛大に祝うためのテーブル。色とりどりのスイーツが並べられたテーブルを囲み、これまでの思い出を存分に語り合おう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1202, /*level*/1, /*item_detail*/"一周年という記念すべき日を豪華に祝うためのサイドテーブル。これまで積み上げてきたたくさんの思い出話に花を咲かせながら、ドリンクを手に取り乾杯をしよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1203, /*level*/1, /*item_detail*/"ランドソルの象徴とも言えるお城を象ったオルゴール。ねじ巻きを回すことで流れ出す優しい音色に誘われて、思い出のフレーズを口ずさんでしまうこと間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1204, /*level*/1, /*item_detail*/"自然で彩られたモニュメント。豊かな緑は部屋に清涼感をもたらしてくれる。このモニュメントの前でポーズを決めれば、あなたも新たな物語の主役に！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1205, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第１弾！ギルド【美食殿】より、ペコリーヌのぬいぐるみ。「やばいですね☆」と今にも聞こえてきそうなキュートな外見に心奪われること間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1206, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第１弾！ギルド【美食殿】より、コッコロのぬいぐるみ。「主さま」と甲斐甲斐しくお世話してくれそうな、溢れる母性を感じることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1207, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第１弾！ギルド【美食殿】より、キャルのぬいぐるみ。「ぶっ殺すわよ！？」なんて言いつつも心根の優しさが見え隠れする、ついついナデナデしたくなる一品！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1208, /*level*/1, /*item_detail*/"鮮やかな花々を飾り付けたフラワーポール。視線を上げた先に目に入る花々は、心に癒しをもたらしてくれる。大切な記念日を飾るにはうってつけ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1209, /*level*/1, /*item_detail*/"誰もが夢見るバルコニー付きの壁。手すりに手をかけて外を眺めれば気分はお姫様！くれぐれも落っこちないように注意！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1210, /*level*/1, /*item_detail*/"豪華な模様が刻まれた赤絨毯の敷かれた床。上に立つだけで背筋がピンと伸びてしまいそうな、心地良い緊張感を与えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1211, /*level*/1, /*item_detail*/"『毒瘴の闇稜』を制覇した証。濃厚なコクと香りを持つという絶世の卵を土鍋でふっくら炊いたご飯にかけ、濃厚醤油をひと垂らし。食べても食べてもお腹が空くような感動が口の中を駆け巡る！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1301, /*level*/1, /*item_detail*/"黒と紫で彩られた豪奢なソファ。肘掛に身体を預けるだけで絵になる、退廃的な逸品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1302, /*level*/1, /*item_detail*/"永遠に美しくありたいアナタのためのドレッサー。
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1303, /*level*/1, /*item_detail*/"高貴なヘッドドレスを身に着けた、格調高い淑女のテディベア。彼女専用の椅子の上で、今日も優雅な笑みを浮かべ続けている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1304, /*level*/1, /*item_detail*/"真紅のクロスの上、異端の紫炎を掲げる禁断の燭台。紫の薔薇を添えてアナタに。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1305, /*level*/1, /*item_detail*/"落ち着いた色の豪華な寝台。その蠱惑の寝心地は一度眠りにつけば起床を許さないと言われるほど。天蓋が外からの光を防ぎ、昼間でも真夜中のような暗闇を堪能できる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1306, /*level*/1, /*item_detail*/"くぐる者を何処かへと誘うような、巨大な扉を模した布製のパーテーション。厚手のドレープにより防音は完璧で、カーテンの向こうには久遠の孤独と静寂が拡がる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1307, /*level*/1, /*item_detail*/"ゆったりとしたカーテンで囲まれた、神秘なる闇と静謐の空間。ルージュのような紅薔薇が紅涙のごとく滴り落ちる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1308, /*level*/1, /*item_detail*/"ミスティックなデザインのフロア。闇夜にも飲まれぬ気高き紫の上で、永遠の舞踏を──", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1401, /*level*/1, /*item_detail*/"可憐な花々に彩られたネビアの水浴び場。
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1402, /*level*/1, /*item_detail*/"東の国風の衣装掛け。収納目的だけではなく、インテリアとして着物を美しく魅せるための工夫が凝らされている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1403, /*level*/1, /*item_detail*/"木目も美しい収納用家具。職人が丹精を込めて作った堅牢な一品だが、登って遊ぶと危ないので注意が必要。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1404, /*level*/1, /*item_detail*/"東の国の伝統的な背景。非常にめでたい図柄で、ランドソルにも異国の春が訪れる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1405, /*level*/1, /*item_detail*/"その身をコマの如く回転させ、オーエド町の空をより遠くへ飛んだ者へ贈られるトロフィー。金色ハートは極限の状況によりもたらされる勇気と冒険心を称えるもの、ということになっている。なお大変危険なので大回転のマネ事は厳禁！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1501, /*level*/1, /*item_detail*/"シンプルな丸太で組まれたかわいらしいベッド。やさしい緑が豊かな森の息吹を感じさせる。やわらかなマットレスとさわやかな木々の香りに包まれて快眠間違いなし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1502, /*level*/1, /*item_detail*/"板材で組まれた簡素なパーテーション。板材の隙間から木漏れ日のように光を取り入れながら、ゆるやかに空間をわけることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1503, /*level*/1, /*item_detail*/"一枚板のパーテーション。安定した造りでしっかりとプライバシーを守ってくれる。広い板面はディスプレイスペースとして使うことも。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1504, /*level*/1, /*item_detail*/"木製の素朴な腰掛け。クッションはないものの、あたたかみのある木製の座面が身体にフィットするやさしい座り心地を実現。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1505, /*level*/1, /*item_detail*/"クッションが張られた簡易的な椅子。あらゆる場所でつかえるコンパクトさながら、十分な厚みのクッション面のおかげで長時間座っても疲れない優れもの。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1506, /*level*/1, /*item_detail*/"背もたれ付きのしっかりとした造りの椅子。シンプルな見た目でどっしりとした安定感があり、丸太の自然な香りが優しく背中を支えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1507, /*level*/1, /*item_detail*/"ゆったりとした一人掛けのソファ。木製の肘かけはまるで長年使いこまれたかのように手になじむ。優しいグリーンに包まれ、座るだけで気分は森林浴。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1508, /*level*/1, /*item_detail*/"素朴な木のソファベンチ。屋外用のベンチと似た構造ながらクッションを追加したことでゆったりとくつろぐことができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1509, /*level*/1, /*item_detail*/"切り出されたままの丸太で組まれた小棚。自然の曲線を活かしたあたたかみのある造形で、いろいろな物を置きたくなる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1510, /*level*/1, /*item_detail*/"素朴な作りのサイドテーブル。スタンダードなラウンドデザインで、どこに配置しても和みの空間を創出する。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1511, /*level*/1, /*item_detail*/"木目を活かしたローテーブル。ナチュラルな色と質感で、使い込むほどに味が出る一品。刻まれた年輪が森の偉大さを教えてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1512, /*level*/1, /*item_detail*/"みんなで囲めるダイニングテーブル。一見不揃いに見える天板だが、触ってみるとなめらかで心地よい肌触り。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1513, /*level*/1, /*item_detail*/"淡い色合いで落ち着いたストライプ柄のラグマット。優しい緑が見る者の心を和ませてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1514, /*level*/1, /*item_detail*/"天然由来の染料をつかったラグ。しっかりと編み込まれながらも優しい肌触りで床の上でのごろ寝にも最適。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1515, /*level*/1, /*item_detail*/"森の中の一軒家をイメージした内装。木のもつ本来の曲線を活かして作られており、鎧窓から差し込む光は木漏れ日のように室内を柔らかく照らしてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1516, /*level*/1, /*item_detail*/"天然の無垢材によるフローリング。いろいろな種類の端材を使用しておりとってもエコロジー。裸足でじかに木のぬくもりを味わいたい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1601, /*level*/1, /*item_detail*/"春の訪れを感じる花柄ベッド。花畑に横になっているような心地よさで、日々の仕事に疲れた人々に安眠をもたらし、春でなくとも暁を覚えないとか。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1602, /*level*/1, /*item_detail*/"人目に触れたくない乙女の秘密を護る騎士の盾、それがこちらの花柄パーテーション。ガーリーなデザインながら遮断性は抜群で、プライバシーもしっかり守護する。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1603, /*level*/1, /*item_detail*/"こぢんまりとしたプリティなスツール。座り心地、安定感ともに優れているが、健気に咲く花を思わせる模様が腰かけることを躊躇わせる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1604, /*level*/1, /*item_detail*/"甘い香りが漂ってきそうな花柄の椅子。デザインはおしゃれ寄りだが作りは頑丈で、花のような儚さとは無縁。末永くあなたと共に。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1605, /*level*/1, /*item_detail*/"置くだけで部屋が一気に華やぐ一人掛けソファ。ライトなスタイルながら座り心地にもこだわった本格派。長時間座っても疲れないので、幅広い世代から支持を受ける愛され家具。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1606, /*level*/1, /*item_detail*/"温かみのある花柄の二人掛けソファ。華やかな色合いがぬくもりを感じさせるおかげか、隣に座る相手がいなくても不思議と寂しくない。寂しくないのだ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1607, /*level*/1, /*item_detail*/"シックな佇まいに一輪の花が映えるクローゼット。気付くと花柄の服ばかりしまい込んでしまう不思議。花に魅せられたのかもしれないし、そうではないのかもしれない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1608, /*level*/1, /*item_detail*/"シンプルなデザインに花のワンポイントがかわいいチェスト。引き出しを開けるたびに仄かな花の香りがするため、用もないのに開けてしまうのを誰も責められない。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1609, /*level*/1, /*item_detail*/"チャーミングな花柄と丁度いいサイズ感が人気のローテーブル。ティータイムやガールズトークに文字通り花を添えること間違いなし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1610, /*level*/1, /*item_detail*/"スウィーツも食事も鮮やかに彩る、大きめのテーブル。女子会にピッタリなのはもちろん、花柄でお花見気分も味わえると評判のインテリア。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1611, /*level*/1, /*item_detail*/"花の息吹を感じる壁。四方より春めいた優しい色合いで包み込み、厳しい夏の暑さ、冬の寒さにも心穏やかに過ごせるので、子供がいるギルドで大変喜ばれる一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1612, /*level*/1, /*item_detail*/"バラの模様をあしらった優美な床。淡いピンクの中に咲く大きなバラがエレガントさを醸し、部屋のグレードを引き上げる。気高くあれ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1701, /*level*/1, /*item_detail*/"爽やかな色合いのビーチチェア。背もたれの絶妙な角度に体はリラックス、傍らにお気に入りのドリンクを置けば、そこはもうビーチリゾート！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1702, /*level*/1, /*item_detail*/"ひんやりとした水をたたえた水盤。色とりどりの花が水面に映え、見る者に清涼感をもたらして厳しい暑さも忘れさせてくれる！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1703, /*level*/1, /*item_detail*/"生き生きとした大きな葉が見事な観葉植物。部屋に一鉢置くだけで気分は南国！　付属のじょうろで水をかけてみずみずしさを高めよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1704, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第２弾！ギルド【自警団（カォン）】より、マホのぬいぐるみ。今日もお友達のぬいぐるみに囲まれて「王子はん」を待っている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1705, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第２弾！ギルド【自警団（カォン）】より、マコトのぬいぐるみ。ワイルドながらどこか「ほっとけない」シャイな乙女心を秘めている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1706, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第２弾！ギルド【自警団（カォン）】より、カスミのぬいぐるみ。「謎はすべて解けた」と言わんばかりのおすましフェイスで登場。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1707, /*level*/1, /*item_detail*/"「キミとつながるぬいぐるみ」をテーマとしたプリぐるみシリーズ第２弾！ギルド【自警団（カォン）】より、カオリのぬいぐるみ。「なんくるない」感じに作られたもふもふな一品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1801, /*level*/1, /*item_detail*/"アクアリウムを楽しむことができる大きなガラスの金魚鉢。鑑賞用としてはもちろん、金魚すくいも楽しめる一挙両得なスグレモノ。見て触れて存分に楽しもう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1802, /*level*/1, /*item_detail*/"海をたゆたうクラゲをモチーフにしたランプ。緑の淡い幻想的な光が室内を照らせば、身も心もゆらゆら軽くなる。ゆったりリラクゼーションしよう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1803, /*level*/1, /*item_detail*/"海をたゆたうクラゲをモチーフにしたランプ。青く涼やかな癒しの光が室内を照らせば、そこはまるで海の中。暑さを忘れヒーリング効果も抜群だ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1902, /*level*/1, /*item_detail*/"秋の様相を感じさせるししおどし。竹筒が頭を上げると、ゴーレムを模した石像がかわいくまばたき。紅葉が舞い散り水面に浮かぶ様は、なんとも風情がある。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1903, /*level*/1, /*item_detail*/"紅葉の意匠があしらわれた布団。良質な綿が使われており、軽くなだらかな肌触りで、秋の夜長もこの布団と一緒なら安眠できること間違いなし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1905, /*level*/1, /*item_detail*/"夜空に浮かぶ満月が描かれた襖。手前のススキが秋の雰囲気を高める。「月見ロップの襖」と合わせると一枚の絵のようになる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1906, /*level*/1, /*item_detail*/"ウイングロップが描かれた襖。「秋月模様の襖」と合わせれば、大きな耳をはばたかせ、月まで飛んで行こうとする愛らしい姿を見ることができる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1907, /*level*/1, /*item_detail*/"質の良い和紙が張られた障子。ついつい穴をあけてしまいたくなるが、張り替える人がかわいそうなのでやめておこう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1908, /*level*/1, /*item_detail*/"灯籠型の照明。本物と同じように石製となっており、優しい光があたりを照らしてくれる。一つだけ置くもよし、複数並べてもよし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1909, /*level*/1, /*item_detail*/"老舗旅館でも使われている座椅子。座布団はほどよい弾力でお尻にフィットしやすく、背もたれは座りやすいように少し曲がっているという作り手のこだわりが見える。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1910, /*level*/1, /*item_detail*/"お月見に必要なものが全部そろった一品。メインの月見団子は上品な甘さで、つきたてのお餅を使用している。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1911, /*level*/1, /*item_detail*/"信楽焼のマスター・センリの置物。魔物ではあるが、その恰幅の良い姿からか、商売繁盛のご利益があると考えられている。お店の出入り口に置くと効果があるそうだ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1912, /*level*/1, /*item_detail*/"上質な欅を使い、漆塗りで仕上げた座敷机。机上には、来客者をもてなすための秋の味覚が置かれている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1913, /*level*/1, /*item_detail*/"室内用のミニチュア紅葉。原種の半分ほどのサイズではあるが、飾った際の存在感は圧倒的。部屋が秋一色になること間違いなし。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1914, /*level*/1, /*item_detail*/"古き良き床の間の壁。漆喰で塗られており、防火性や明光性に優れている。飾られている刀は模造刀なのでご安心を。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1915, /*level*/1, /*item_detail*/"市松模様の畳。い草の良い匂いが香る。寝転がると気づかぬうちに眠ってしまうことも。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1916, /*level*/1, /*item_detail*/"一面の秋に囲まれることのできる背景。燃え盛るような紅葉と水面に映る月が生み出す調和は、心地よい晩秋の空気に抱かれているような気分になる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2001, /*level*/1, /*item_detail*/"ハロウィンのためにキョウカが準備したほうき型の杖。プリティーな猫さんのぬいぐるみはまるで生きているかのよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2002, /*level*/1, /*item_detail*/"ミサキの武器兼パートナーであるアイちゃんを模したクッション。強大な魔力を秘めた魔眼、のはずなのだが乱暴に扱われているせいで少し涙目である。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2003, /*level*/1, /*item_detail*/"ハロウィンに彩りを添える、いたずら好きの必需品。贈り物を開けてみると……箱から飛び出る不思議なかぼちゃ。トリックオアトリート！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2004, /*level*/1, /*item_detail*/"かわいらしいうさぎさんが包帯でぐるぐるに！　かわいらしいリボン風の結び目がチャームポイント。優しく抱きしめてあげよう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
