INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2201, /*level*/1, /*item_detail*/"沢山のクリスマスプレゼントが入った大きな袋。プレゼントだけでなく、愛と夢、希望もいっぱいに詰まっており、中身を取り出すたびにみんなを笑顔にする。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2202, /*level*/1, /*item_detail*/"愛らしい動物をモチーフにした雪だるま。特別な魔法がかけられているのか、室内でも溶けることはない。かわいいは永遠だ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2203, /*level*/1, /*item_detail*/"ツリーが主役のクリスマス仕様のスノードーム。ガラスの中でキラキラと舞い散る雪の幻想的な美しさに、時を忘れて見とれる人が続出！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2204, /*level*/1, /*item_detail*/"みんなの大好きなクリスマスを表現した、見ているだけで楽しい背景素材。クリスマスの家具と合わせれば、ワクワク感は何倍にも跳ね上がる！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2205, /*level*/1, /*item_detail*/"創設者の名前を冠した、いささか自己主張が強い、ゴージャスな温泉。湯量が豊富で効能も抜群なため、何かと忙しい女の子たちの間で話題沸騰中だとか。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2206, /*level*/1, /*item_detail*/"『緑竜の骸嶺』を制覇した証。竜の尾から棘を抜く際にとれてしまった肉を使った贅沢なおでん。生態系の頂点に座する幻の食材は美食の世界に新たな地平をもたらした。調理されてなお、旺盛な生命力で再生し続けている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
