INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2306, /*level*/1, /*item_detail*/"かわいいぬいぐるみと一緒にごろ寝できる多機能ベッド。ロフト形式なので収納スペースも確保できて、機能性もバッチリな逸品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2307, /*level*/1, /*item_detail*/"魔法の言葉とともに現れる、愛と希望が宿りし杖。真実を求めるカスミの想いに共鳴し、魔法探偵ミスティ★カスミへと変身させる。決め台詞は「魔法の力で捜査開始！」", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2308, /*level*/1, /*item_detail*/"魔法の言葉とともに現れる、愛と希望が宿りし弓。シオリの純真無垢なる想いに共鳴し、魔法狩人ピュアリー★シオリへと変身させる。決め台詞は「あなたの心に愛の矢を！」", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2401, /*level*/1, /*item_detail*/"二周年という記念すべき日を華麗に祝うために用意された長椅子。飾り付けの風船にこめられたあたたかな想いで、空も飛べちゃうかも？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2402, /*level*/1, /*item_detail*/"二周年という記念すべき日を華やかに彩るために用意された特製のバルーンスタンド。風船の一つ一つに二年間の思い出が詰まっている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2403, /*level*/1, /*item_detail*/"二周年という記念すべき日をやばいくらいに祝うために用意された、ギルド【美食殿】のメンバーを象ったバルーンスタンド。素敵な思い出でおなかいっぱいです☆", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2404, /*level*/1, /*item_detail*/"どこかレトロな雰囲気を漂わせた様々なゲームが楽しめる不思議なテーブル。勝って楽しく、負けて悔しい、あの懐かしいゲームたちをもう一度体験してみよう。白熱したバトルを提供してくれること請け合いだ！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2405, /*level*/1, /*item_detail*/"【ニュージェネレーションズ】との旅の記録で彩られたフォトボード。ボードの写真を見ればいつでもみんなとの冒険が目の前に。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2501, /*level*/1, /*item_detail*/"聖テレサ女学院が誇る、とある才媛が作り上げた学びの結晶。元となった『なかよしＸ』よりも大幅な小型化に成功し、搭載された各機能も先鋭化されている。「ちぇるーん☆」と鳴きながら後ろをついてくる機能は、製作者もどや顔で胸を張る完成度。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2502, /*level*/1, /*item_detail*/"豪奢な装飾とうすピンクのレザーがかわいいソファ。女の子っぽさと大人の雰囲気を両立させたアンビバレントな魅力が、幅広い年代に好まれている逸品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2503, /*level*/1, /*item_detail*/"猫足がかわいらしいうすピンクのドレッサー。磨き上げられた鏡は持ち主をよりかわいく美しく映すと、おしゃれに余念のないレディたちの絶大な支持を集める。しかしつい時間を忘れ鏡に映る自分の姿に見とれてしまうことも。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2504, /*level*/1, /*item_detail*/"うすピンクのドレスを纏ったウサギのぬいぐるみ。愛らしさの中に秘めた小悪魔的な魅力が、あなたを虜にすること間違いなし！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2505, /*level*/1, /*item_detail*/"バラの形をあしらったランプ。気高いバラにリボンのワンポイントがキュートで女性に大人気な一方、その大胆なデザインからアート作品としても注目を集めている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2506, /*level*/1, /*item_detail*/"上品で、そして可憐さも併せ持つ優美なベッド。乙女の憧れである格調高い天蓋が、安らかな眠りを約束する。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2507, /*level*/1, /*item_detail*/"うすいピンクのカーテンが映えるパーテーション。かわいめのデザインながら、カーテンの向こうのあらゆる秘密を守る頼もしい一品。しかし隙間から覗き見たくなる衝動が起こることもしばしば。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2508, /*level*/1, /*item_detail*/"柔らかな色合いのカーテンを用いた壁。花をモチーフにした窓枠や、シンプルながら品のある装飾が一段上の雰囲気を演出する。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2509, /*level*/1, /*item_detail*/"中央の大きな花の文様とチェック柄が印象的な床。明るめの色彩は気分を高揚させ、気付けば軽やかなステップを刻んでいると評判である。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2601, /*level*/1, /*item_detail*/"昔懐かしい黒板と教卓一式。授業をしてもよし、お絵描きをしてもよし。使い方は人によってさまざま！　ただし、使い終わった後はきちんと片づけましょう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2602, /*level*/1, /*item_detail*/"木製の机と椅子のセット。勉強にも居眠りにもちょうどいい高さ。教科書を枕にすれば、あっという間に夢見心地な気分に。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2603, /*level*/1, /*item_detail*/"ほうきとちり取りの掃除用具セット。短時間でささっと掃除できるよう、若干大きめサイズで作られている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2604, /*level*/1, /*item_detail*/"木製の個人用ロッカー。縦長タイプで、丈長の服も折りたたまずに軽々収納。詰め込み過ぎた荷物が雪崩を起こさないよう、日頃から小まめな整理整頓を心がけましょう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2605, /*level*/1, /*item_detail*/"木製の両面本棚。たっぷり本を詰め込めるので本好きたちにとても好評。ところどころの装飾に、職人技がきらりと光る。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2606, /*level*/1, /*item_detail*/"学校で使われているレトロデザインの壁掛け時計。振り子の音が一定のリズムで刻まれる。そして授業やテスト中にも関わらず、とても心地よい眠気を誘う。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2607, /*level*/1, /*item_detail*/"プリントなどを張り出すためのコルクボード。時間割表や連絡網のほかにも、いろんな行事のお知らせに使われる。みんなで協力した飾りつけは、かわいくカラフルな仕上がり。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2608, /*level*/1, /*item_detail*/"木造校舎というコンセプトでデザインされた上り階段。あたたかい木のぬくもりが感じられ、どこかノスタルジーな気分に浸らせる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2609, /*level*/1, /*item_detail*/"木造校舎というコンセプトでデザインされた下り階段。ほこりが隅っこに溜まりやすいので、掃除のときは念入りに。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2610, /*level*/1, /*item_detail*/"教室をモチーフにデザインされた壁。張り出された掲示物やプリントが飛ばされないよう、開けた窓は必ず閉めてから帰りましょう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2611, /*level*/1, /*item_detail*/"やや年季の入った木製フローリング。ワックスがけのおかげで、掃除しやすく傷つきにくい。学校の教室を作りたいなら、まずはこれを床に敷こう！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2701, /*level*/1, /*item_detail*/"プチコの住処をイメージしたインテリアで、自然の香りや、くり抜かれた倒木など、細部まで完璧に作り込まれた職人自慢の逸品。その完璧な出来栄えから、「まるでキノコが生きているかのようだ」、「この丁度いいサイズのキノコ、生でもいけてやばいですね☆」など、称賛の声が後を絶たないという。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2702, /*level*/1, /*item_detail*/"ぺコリーヌお手製の木製テーブル。自ら斧で大木を切り倒すところから始まり、幅が大きすぎてギルドハウスの入口から搬入できないといった苦心の末に完成した力作。製作者の汗とあたたかな想いが込められているせいか、このテーブルでの食事は特別おいしく感じられ、特にビーフシチューは絶品と評判。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2703, /*level*/1, /*item_detail*/"ぺコリーヌが作った素朴なデザインの椅子。座るのに程よいサイズの木を選んで作られた逸品。そのためか仲間との食事や談笑が弾み、不思議といつまでも座ってしまうと評判らしい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2704, /*level*/1, /*item_detail*/"ぺコリーヌが作った素朴なデザインの椅子。座るのに程よいサイズの木を選んで作られた逸品。そのためかのんびり仲間と過ごすのにちょうどよく、不思議といつまでも座ってしまうと評判らしい。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2705, /*level*/1, /*item_detail*/"【リトルリリカル】の３人が大冒険の末、手に入れたお宝、怪鳥の卵から生まれた巨大な雛。【リトルリリカル】の３人にすっかり懐いており、ご飯も兼ねたアイガモ農法でお手伝いもバッチリこなす。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2706, /*level*/1, /*item_detail*/"女の子たちとの思い出を記録した魔法の映写機。輝く円盤を入れるといつでも懐かしいあの日の記憶が鮮明によみがえる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2801, /*level*/1, /*item_detail*/"魔法の言葉とともに現れる、愛と希望が宿りし剣。大切なものを守り抜くというモニカの想いに共鳴し、魔法提督ラブリー★モニカへと変身させる。決め台詞は「空に代わって指揮を執る！」", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2802, /*level*/1, /*item_detail*/"魔法の言葉とともに現れる、愛と希望が宿りし突剣。正義を貫くトモの想いに共鳴し、魔法剣士シャイニー★トモへと変身させる。決め台詞は「煌めく剣技で悪を断つ！」", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2803, /*level*/1, /*item_detail*/"不思議なおやさい、ピップルたちのおうち。マナのあふれる生命の森で育った古木で、無数のピップルたちがおいしいおやさいになるべくのんびり日向ぼっこしている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2804, /*level*/1, /*item_detail*/"ギルド管理協会が製作したオーマ地域振興ポスター。初競りで最高価格を叩き出したグレートトゥンヌスと、それを漁師として釣り上げたギルド【美食殿】のメンバーがあしらわれている。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2805, /*level*/1, /*item_detail*/"３周年の感謝の気持ちが詰まったフォトブック。女の子たちがあなたとの思い出を振り返りながら手作りしてくれた世界に一つだけの宝物。ページをめくれば、これまでの冒険やみんなとの熱い想いが鮮明によみがえる！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2806, /*level*/1, /*item_detail*/"エルフの里を見守り続けるモノリスを模した精霊石。里のモノリスと同様の石で作られており、豊かなマナを周囲にたたえる。その加護により精霊の揺り籠にもなる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2807, /*level*/1, /*item_detail*/"『天上の浮城』を制覇した証。城の最奥に佇む泉から湧き出す奇跡の水。生命力を高める成分が含まれており、金色に輝いて見える。天にのぼるような甘さが全身を駆け巡り、どんなに疲れていても活力がみなぎってくる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2808, /*level*/1, /*item_detail*/"リマが素敵なレディを目指すうえで学んだ五つの教えを記した覚え書き。いつでも目に見える場所に張り、真の淑女を目指そう。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2809, /*level*/1, /*item_detail*/"ランドソルのとあるおもちゃ工場が製造したおもちゃ。そこでアルバイトをしていたアオイ・ミフユ・タマキの働きによって、合計で100体が作られた。即完売したほどの人気っぷりで、遊んでくれる子どもたちと友情を育んでいる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2810, /*level*/1, /*item_detail*/"『天上の浮城』を制覇した証。城の最奥に佇む泉から湧き出す奇跡の水。生命力を高める成分が含まれており、金色に輝いて見える。天にのぼるような甘さが全身を駆け巡り、どんなに疲れていても活力がみなぎってくる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2811, /*level*/1, /*item_detail*/"ラジオ番組を収録するためのブースセット。美味しいお茶とお菓子も必須アイテム。専用の椅子二点とセットにすることで特別なアクションが見れますよ。ギルドハウスから番組をお届け。おいっす～☆", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
