INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2814, /*level*/1, /*item_detail*/"カルミナのサマーライブグッズが飾られた、思い出いっぱいのコレクション棚。集めたグッズがずらりと並ぶ光景は、ライブの興奮と感動をいつでも思い出させてくれる。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2815, /*level*/1, /*item_detail*/"コッコロがギルドのみんなのために手作りした、マナを貯めるためのとても大きなガマ口。所持数を超えたマナを自動的に貯めておいてくれる。見た目よりもずっとたくさん貯蔵可能。コツコツ貯めたヘソクリは無駄遣い厳禁。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2816, /*level*/1, /*item_detail*/"特別な記念日のためにみんなで心を込めて作った日めくりのカレンダー。大切な思い出の数々が記録されており、いつでも見返すことが出来る。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2817, /*level*/1, /*item_detail*/"『砂瀑の底都』を制覇した証。砂瀑の最深部に自生する伝説のサボテンの蜜を、贅沢に用いた特製のパンケーキ。大地の栄養を多くに含んでおり、抜群の美肌効果で食べるほど美しくなれるという女子熱望の夢のようなスイーツ。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2818, /*level*/1, /*item_detail*/"エリザベスパークで人気を博した舞台「チーズお守り大作戦！」の追加公演告知ポスター。主演のクルミ、ミサキたちの姿が鮮やかに描かれている他、マヒルのお笑いショーや土産物の紹介もされている。おいでませ牧場温泉！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
