INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2820, /*level*/1, /*item_detail*/"ランドソル近郊に生息するイヌ科の魔物のぬいぐるみ。モデルとなった魔物は何を考えているのか分からない不思議な目つきで、時折しゃべるような鳴き声で人懐っこい様子を見せる。しかしながら餌と判断すれば容赦なく噛みつき巣へと持ち帰ろうとする習性があるため注意が必要である。このぬいぐるみはその危険な魅力を凛々しい立ち姿であますところなく再現している。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_trigger_type_2*/0, /*lvup_trigger_id_2*/0, /*lvup_trigger_value_2*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
