INSERT INTO `room_unit_comments` VALUES (/*id*/2160, /*unit_id*/104701, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2161, /*unit_id*/104701, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"仕事が門番だからか\n部屋にいても\nじっとしてしまう…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2162, /*unit_id*/104701, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"少年と会話したことを\n思いだすと\n自然と楽しくなるな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2163, /*unit_id*/104701, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"少年のことを考えると\n顔がにやけてしまう", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2164, /*unit_id*/104701, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"少年は今頃\n平和に過ごせて\nいるかな…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2165, /*unit_id*/104701, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ん？　悪いが\n兜は脱がないぞ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2166, /*unit_id*/104701, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"私と遊びたいのか？\nそう言われても\n何をしていいやら…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2167, /*unit_id*/104701, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2168, /*unit_id*/104701, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2169, /*unit_id*/104701, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"君といると、兜の中が\nいつもより暑いんだ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2170, /*unit_id*/104701, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"私としゃべりたい？\n物好きだな\nでも、嬉しいよ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2171, /*unit_id*/104701, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"早いな少年\nえ？　私の仕事に\n合わせてくれたのか", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2172, /*unit_id*/104701, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"昼食の誘い？\n嬉しいけど\n人前では食べないんだ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2173, /*unit_id*/104701, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2175, /*unit_id*/104701, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"鎧を着ているから\n感触が分かりづらい\nのが残念だ…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2176, /*unit_id*/104701, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2177, /*unit_id*/104701, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"良い香りだ…\n普段はどうしても\n鉄くさくなるからな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2178, /*unit_id*/104701, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2180, /*unit_id*/104701, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2181, /*unit_id*/104701, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"兜があっても\n意外と本くらいは\n難なく読めるんだ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2183, /*unit_id*/102601, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2184, /*unit_id*/102601, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ドングリの木\n近くに生えてない？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2185, /*unit_id*/102601, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"快適な寝床があれば\nそれだけで幸せだねぇ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2186, /*unit_id*/102601, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", /*insert_word_type*/0);
