INSERT INTO `room_unit_comments` VALUES (/*id*/2188, /*unit_id*/102601, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"あんパン買ったし\nあいつにも\n分けてあげよう", /*insert_word_type*/0);
