INSERT INTO `room_unit_comments` VALUES (/*id*/2190, /*unit_id*/102601, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ドングリでも\nつまみながら\n一緒にゲームしようよ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/2191, /*unit_id*/102601, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", /*insert_word_type*/0);
