INSERT INTO `room_unit_comments` VALUES (/*id*/2193, /*unit_id*/102601, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"う～\n疲れちゃったよ～\nおんぶして運んで～", /*insert_word_type*/0);
