INSERT INTO `room_unit_comments` VALUES (/*id*/2195, /*unit_id*/102601, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"あんたが一生\n世話してくれたら\n人生安泰なんだけどね", /*insert_word_type*/0);
