INSERT INTO `room_unit_comments` VALUES (/*id*/2200, /*unit_id*/102601, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"あ…だめだこれ。\n気持ち良くて\n落ちそう…", /*insert_word_type*/0);
