INSERT INTO `room_unit_comments` VALUES (/*id*/3123, /*unit_id*/109601, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3125, /*unit_id*/109601, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3126, /*unit_id*/109601, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3128, /*unit_id*/109701, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3129, /*unit_id*/109701, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"異世界の演劇…\n許されるならば、\n観てみたいです", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3130, /*unit_id*/109701, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3131, /*unit_id*/109701, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3132, /*unit_id*/109701, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3133, /*unit_id*/109701, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3134, /*unit_id*/109701, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3135, /*unit_id*/109701, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"お出かけでしたら、\nレムも同行\nさせてくださいね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3136, /*unit_id*/109701, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"レムにできること\nでしたら、なんでも\nお申し付けください", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3137, /*unit_id*/109701, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3138, /*unit_id*/109701, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3139, /*unit_id*/109701, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3140, /*unit_id*/109701, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3141, /*unit_id*/109701, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3142, /*unit_id*/109701, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"昼食の時間ですね\n何か食べたいものは\nありますか？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3143, /*unit_id*/109701, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3145, /*unit_id*/109701, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3146, /*unit_id*/109701, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ふふっ…異世界でも\nかわいいものは\n共通なのですね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3147, /*unit_id*/109701, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"よい香りですね\n魔女教の悪臭とは\n大違いです", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3148, /*unit_id*/109701, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3150, /*unit_id*/109701, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3151, /*unit_id*/109701, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"絵がいっぱい…\n異世界の本は\nとても面白いですね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3153, /*unit_id*/109801, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3154, /*unit_id*/109801, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3155, /*unit_id*/109801, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3156, /*unit_id*/109801, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3157, /*unit_id*/109801, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3158, /*unit_id*/109801, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3159, /*unit_id*/109801, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3160, /*unit_id*/109801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3161, /*unit_id*/109801, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3162, /*unit_id*/109801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3163, /*unit_id*/109801, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ラムになんの用？\nふざけた内容なら\nねじ切るわよ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3164, /*unit_id*/109801, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3165, /*unit_id*/109801, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3166, /*unit_id*/109801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"だらしない顔ね\n顔を洗って\nしゃんとなさい", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3167, /*unit_id*/109801, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3168, /*unit_id*/109801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3170, /*unit_id*/109801, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"…ここから動かない\nという、誓いを\n立てたくなるわね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3171, /*unit_id*/109801, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"これは…かわいいわね\nもちろん、\nラムほどではないけど", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3172, /*unit_id*/109801, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", /*insert_word_type*/0);
