INSERT INTO `room_unit_comments` VALUES (/*id*/3196, /*unit_id*/109901, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3197, /*unit_id*/109901, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3198, /*unit_id*/109901, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3200, /*unit_id*/109901, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", /*insert_word_type*/0);
