INSERT INTO `room_unit_comments` VALUES (/*id*/3645, /*unit_id*/111701, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3646, /*unit_id*/111701, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3647, /*unit_id*/111701, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3648, /*unit_id*/111701, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3649, /*unit_id*/111701, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3650, /*unit_id*/111701, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3651, /*unit_id*/111701, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3652, /*unit_id*/111701, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/3654, /*unit_id*/111701, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", /*insert_word_type*/0);
