INSERT INTO `room_unit_comments` VALUES (/*id*/5034, /*unit_id*/116601, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5035, /*unit_id*/116601, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5036, /*unit_id*/116601, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5037, /*unit_id*/116601, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5038, /*unit_id*/116601, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5039, /*unit_id*/116601, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5040, /*unit_id*/116601, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5041, /*unit_id*/116601, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5042, /*unit_id*/116601, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5043, /*unit_id*/116601, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5044, /*unit_id*/116601, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5045, /*unit_id*/116601, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/5047, /*unit_id*/116601, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", /*insert_word_type*/0);
