INSERT INTO `room_unit_comments` VALUES (/*id*/6170, /*unit_id*/121801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6171, /*unit_id*/121801, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6172, /*unit_id*/121801, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6173, /*unit_id*/121801, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6174, /*unit_id*/121801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6175, /*unit_id*/121801, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6176, /*unit_id*/121801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6178, /*unit_id*/121801, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6179, /*unit_id*/121801, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"救護院の皆も\nぬいぐるみが\n大好きなのよ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6180, /*unit_id*/121801, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"いい香りね…\nこれはアキノさんの\n趣味かしら？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6181, /*unit_id*/121801, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6183, /*unit_id*/121801, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"どんどん出てくる…\nこれ、救護院にも\nほしいわね…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6184, /*unit_id*/121801, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6187, /*unit_id*/122001, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6188, /*unit_id*/122001, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"一部の人からは\n海賊より海賊らしいと\n評判の父です", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6189, /*unit_id*/122001, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"潮風で錆びないよう\n武器の手入れを\nしておきましょうか", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6190, /*unit_id*/122001, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6191, /*unit_id*/122001, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6192, /*unit_id*/122001, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6193, /*unit_id*/122001, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6194, /*unit_id*/122001, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6195, /*unit_id*/122001, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"航海に向けた\n買い出しが必要ですね\n予定は空いていますか", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6196, /*unit_id*/122001, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6197, /*unit_id*/122001, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"夜の航海では\n父が明かり代わりに\nなってくれるんです", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6198, /*unit_id*/122001, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"船の上では食材が\n限られるので料理の\nいい練習になります", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6199, /*unit_id*/122001, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6200, /*unit_id*/122001, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"あの人の寝顔…\nもう少しだけ\n眺めていたいな…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6201, /*unit_id*/122001, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"船の上では\n規則正しい生活が\n重要ですから", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6202, /*unit_id*/122001, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"夜の海は幽霊が\n見分けやすいんです\n青白く光るので", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6204, /*unit_id*/122001, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"海に揺られるような\n心地よさですね\n気が休まります…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6205, /*unit_id*/122001, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6206, /*unit_id*/122001, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6207, /*unit_id*/122001, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"温かい…\n潮風で冷えた体に\n沁みますね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6209, /*unit_id*/122001, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6210, /*unit_id*/122001, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"アンナさんに教えて\nもらった小説は…\nあ、これですね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6212, /*unit_id*/102601, /*trigger*/1, /*voice_id*/103, /*beloved_step*/12, /*time*/0, /*face_id*/1, /*description*/"なにもしないを\nしてるんだよ\nあ～いそがしいそがし", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6213, /*unit_id*/102601, /*trigger*/2, /*voice_id*/103, /*beloved_step*/12, /*time*/0, /*face_id*/1, /*description*/"はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6214, /*unit_id*/121901, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6215, /*unit_id*/121901, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6216, /*unit_id*/121901, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6217, /*unit_id*/121901, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6218, /*unit_id*/121901, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6219, /*unit_id*/121901, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6220, /*unit_id*/121901, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6221, /*unit_id*/121901, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6222, /*unit_id*/121901, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6223, /*unit_id*/121901, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6224, /*unit_id*/121901, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6225, /*unit_id*/121901, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6226, /*unit_id*/121901, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6227, /*unit_id*/121901, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/1, /*face_id*/1, /*description*/"ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6228, /*unit_id*/121901, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6229, /*unit_id*/121901, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/3, /*face_id*/1, /*description*/"夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6231, /*unit_id*/121901, /*trigger*/3, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6232, /*unit_id*/121901, /*trigger*/3, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6233, /*unit_id*/121901, /*trigger*/3, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6234, /*unit_id*/121901, /*trigger*/3, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6236, /*unit_id*/121901, /*trigger*/3, /*voice_id*/7, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6237, /*unit_id*/121901, /*trigger*/3, /*voice_id*/8, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6239, /*unit_id*/122101, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6240, /*unit_id*/122101, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6241, /*unit_id*/122101, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6242, /*unit_id*/122101, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6243, /*unit_id*/122101, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6244, /*unit_id*/122101, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6245, /*unit_id*/122101, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6246, /*unit_id*/122101, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6247, /*unit_id*/122101, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6248, /*unit_id*/122101, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/6249, /*unit_id*/122101, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", /*insert_word_type*/0);
