INSERT INTO `room_unit_comments` VALUES (/*id*/7189, /*unit_id*/125801, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7190, /*unit_id*/125801, /*trigger*/1, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7191, /*unit_id*/125801, /*trigger*/1, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7192, /*unit_id*/125801, /*trigger*/1, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7193, /*unit_id*/125801, /*trigger*/1, /*voice_id*/5, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7194, /*unit_id*/125801, /*trigger*/1, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7195, /*unit_id*/125801, /*trigger*/1, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7196, /*unit_id*/125801, /*trigger*/2, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7197, /*unit_id*/125801, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7198, /*unit_id*/125801, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7199, /*unit_id*/125801, /*trigger*/2, /*voice_id*/4, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", /*insert_word_type*/0);
INSERT INTO `room_unit_comments` VALUES (/*id*/7200, /*unit_id*/125801, /*trigger*/2, /*voice_id*/101, /*beloved_step*/4, /*time*/0, /*face_id*/1, /*description*/"に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", /*insert_word_type*/0);
