INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1001001, /*where_type*/1001, /*character_id*/190801, /*motion_type*/13, /*description*/"こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！", /*voice_id*/0, /*start_time*/"2020-02-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1002001, /*where_type*/1002, /*character_id*/190801, /*motion_type*/12, /*description*/"動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", /*voice_id*/0, /*start_time*/"2020-02-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1003001, /*where_type*/1003, /*character_id*/190801, /*motion_type*/11, /*description*/"クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", /*voice_id*/0, /*start_time*/"2020-02-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1004001, /*where_type*/1004, /*character_id*/190801, /*motion_type*/3, /*description*/"左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", /*voice_id*/0, /*start_time*/"2020-02-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1005001, /*where_type*/1005, /*character_id*/190801, /*motion_type*/7, /*description*/"パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", /*voice_id*/0, /*start_time*/"2020-02-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1006001, /*where_type*/1006, /*character_id*/190801, /*motion_type*/2, /*description*/"素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", /*voice_id*/0, /*start_time*/"2020-05-31 12:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1007001, /*where_type*/1007, /*character_id*/190801, /*motion_type*/4, /*description*/"ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", /*voice_id*/0, /*start_time*/"2020-08-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1008001, /*where_type*/1008, /*character_id*/190801, /*motion_type*/6, /*description*/"カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", /*voice_id*/0, /*start_time*/"2021-08-31 12:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1009001, /*where_type*/1009, /*character_id*/190801, /*motion_type*/13, /*description*/"食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", /*voice_id*/0, /*start_time*/"2021-12-18 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1010001, /*where_type*/1010, /*character_id*/190801, /*motion_type*/4, /*description*/"チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", /*voice_id*/0, /*start_time*/"2022-02-15 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1011001, /*where_type*/1011, /*character_id*/190801, /*motion_type*/6, /*description*/"３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", /*voice_id*/0, /*start_time*/"2022-07-18 15:00:00", /*end_time*/"2030-04-01 14:00:00");
INSERT INTO `sd_navi_comment` VALUES (/*comment_id*/1012001, /*where_type*/1012, /*character_id*/190801, /*motion_type*/6, /*description*/"キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", /*voice_id*/0, /*start_time*/"2023-09-18 15:00:00", /*end_time*/"2030-04-01 14:00:00");
