INSERT INTO `srt_top_talk` VALUES (/*id*/1000101, /*talk_id*/10001, /*chara_index*/1, /*talk_text*/"ふっふっふ、これでカヤぴぃとボスを見返してやるです！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_000", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000201, /*talk_id*/10002, /*chara_index*/1, /*talk_text*/"しりとりでの戦い、お兄さんにも手伝ってもらうです", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_003", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000301, /*talk_id*/10003, /*chara_index*/2, /*talk_text*/"どんなケンカも売られた以上は買うのが喧嘩屋だ！やってやるぜ！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_004", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000401, /*talk_id*/10004, /*chara_index*/2, /*talk_text*/"石板でしりとりか……イノリのヤツおもしろいこと考えるな", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_005", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000501, /*talk_id*/10005, /*chara_index*/2, /*talk_text*/"しりとりだろうがなんだろうが返り討ちにしてやるぜ", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_006", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000601, /*talk_id*/10006, /*chara_index*/3, /*talk_text*/"ぐもも……（オデ、ハラヘッタ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_007", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000701, /*talk_id*/10007, /*chara_index*/3, /*talk_text*/"ぐもももも（シリトリ タノシイ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_008", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000801, /*talk_id*/10008, /*chara_index*/3, /*talk_text*/"ぐもももも！（イノリ イイヤツ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_1_009", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000901, /*talk_id*/10009, /*chara_index*/1, /*talk_text*/"カヤぴぃ相手でも手加減しないです", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_010", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1000902, /*talk_id*/10009, /*chara_index*/2, /*talk_text*/"上等だ！やるからには本気で来いよな", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_011", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001001, /*talk_id*/10010, /*chara_index*/1, /*talk_text*/"カヤぴぃ、謝るなら今のうちです", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_012", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001002, /*talk_id*/10010, /*chara_index*/2, /*talk_text*/"イノリこそ泣きを入れるなら今のうちだぜ？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_013", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001101, /*talk_id*/10011, /*chara_index*/1, /*talk_text*/"カヤぴぃ、これからは知性の時代ですよ？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_014", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001102, /*talk_id*/10011, /*chara_index*/2, /*talk_text*/"待て！オレがバカみたいにいうな！！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_015", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001201, /*talk_id*/10012, /*chara_index*/1, /*talk_text*/"さあ、あたしたちの力を見せてやるです", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_016", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001202, /*talk_id*/10012, /*chara_index*/3, /*talk_text*/"ぐも！（マカセロ！）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_017", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001301, /*talk_id*/10013, /*chara_index*/1, /*talk_text*/"よしよし、いいですようまくできたらチョコをあげるです！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_018", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001302, /*talk_id*/10013, /*chara_index*/3, /*talk_text*/"ぐも！（オデ チョコスキ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_019", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001401, /*talk_id*/10014, /*chara_index*/2, /*talk_text*/"なあイノリ……もしかして当初の目的見失ってないよな？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_023", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001402, /*talk_id*/10014, /*chara_index*/1, /*talk_text*/"もちろん覚えてるですカヤぴぃを倒し我々の知能を証明するです", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_024", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001501, /*talk_id*/10015, /*chara_index*/2, /*talk_text*/"いくらイノリ相手でも勝ちを譲る気はないからな！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_025", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001502, /*talk_id*/10015, /*chara_index*/1, /*talk_text*/"望むところです手加減はなしですよ", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_026", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001601, /*talk_id*/10016, /*chara_index*/2, /*talk_text*/"へっ、なかなかやるじゃねえか", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_027", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001602, /*talk_id*/10016, /*chara_index*/1, /*talk_text*/"カヤぴぃも思ったよりやるですね！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_028", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001701, /*talk_id*/10017, /*chara_index*/2, /*talk_text*/"お～い、ドラゴンもどき？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_029", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001702, /*talk_id*/10017, /*chara_index*/3, /*talk_text*/"ぐもっ！？（ヒッ！）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_030", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001801, /*talk_id*/10018, /*chara_index*/2, /*talk_text*/"いやあ、あんときは悪かったなはっはっは", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_031", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001802, /*talk_id*/10018, /*chara_index*/3, /*talk_text*/"ぐももも……（かやピィ コワカッタ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_032", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001901, /*talk_id*/10019, /*chara_index*/2, /*talk_text*/"お前ってさ……ほんとマヌケな顔してるよな", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_033", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1001902, /*talk_id*/10019, /*chara_index*/3, /*talk_text*/"ぐも……（オマエガ イウカ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_034", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002001, /*talk_id*/10020, /*chara_index*/3, /*talk_text*/"ぐも……（ハラヘッタ チョコホシイ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_044", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002002, /*talk_id*/10020, /*chara_index*/2, /*talk_text*/"なんだ？眠いのか？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_045", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002101, /*talk_id*/10021, /*chara_index*/3, /*talk_text*/"ぐもも（コイツ……キケン）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_046", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002102, /*talk_id*/10021, /*chara_index*/2, /*talk_text*/"うん？　なんだ？オレの舎弟にでもなりたいのか？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_047", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002201, /*talk_id*/10022, /*chara_index*/3, /*talk_text*/"ぐも…（かやピィノウキン）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_048", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002202, /*talk_id*/10022, /*chara_index*/2, /*talk_text*/"おい、今失礼なこと考えてるだろ？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_2_049", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002301, /*talk_id*/10023, /*chara_index*/2, /*talk_text*/"なあ、これいつまで続けるんだ？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_058", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002302, /*talk_id*/10023, /*chara_index*/1, /*talk_text*/"カヤぴぃが降参するまでです！", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_059", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002303, /*talk_id*/10023, /*chara_index*/3, /*talk_text*/"ぐもも！（コウサンシロ！）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_060", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002401, /*talk_id*/10024, /*chara_index*/3, /*talk_text*/"ぐも！（イノリ チョコホシイ）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_065", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002402, /*talk_id*/10024, /*chara_index*/1, /*talk_text*/"しょうがないですね、食べ過ぎはダメですよ", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_066", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002403, /*talk_id*/10024, /*chara_index*/2, /*talk_text*/"土産のチョコ、そうとう気に入ったみたいだな", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_067", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002501, /*talk_id*/10025, /*chara_index*/3, /*talk_text*/"ぐも！（かやピィニハ マケナイ！）", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_068", /*direction*/2);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002502, /*talk_id*/10025, /*chara_index*/2, /*talk_text*/"お？なんだ？腹でも減ってるのか？", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_069", /*direction*/1);
INSERT INTO `srt_top_talk` VALUES (/*id*/1002503, /*talk_id*/10025, /*chara_index*/1, /*talk_text*/"……全然話が通じてないです", /*sheet_name*/"vo_minigame_1004", /*cue_name*/"vo_minigame_1004_top_3_070", /*direction*/2);
