INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130011, /*equipment_name*/"ニャンピオンベルト", /*description*/"ヒヨリが着用するベルト。\n雄々しき獣が睨みを利かせたバック\nルは、闘争本能を鼓舞し、その拳に\n更なる力を与える。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/94.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130021, /*equipment_name*/"ブロッサムウィッシュ", /*description*/"ユイが着用しているカチューシャ。\n可愛らしく施されている花のレース\nと飾りは、仲間を守るための癒しの\n力を高めてくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130031, /*equipment_name*/"ゲイルグローブ", /*description*/"レイが愛用しているグローブ。この\n攻守一体のグローブからくり出され\nる一突きは、立ちはだかるどんな敵\nをも貫く。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/110.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130211, /*equipment_name*/"ピュアアクトベル", /*description*/"クルミがお守りにしているベル。\n荘厳な見た目から奏でられる可愛ら\nしい音色が、怯える心に勇気を与え\nてくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130281, /*equipment_name*/"グロリアスフェザー", /*description*/"サレン愛用のヘアアクセサリー。\n優しさの中に秘めた強さが象徴され\nている。その翼が羽ばたくとき、\n体は重力から解き放たれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/25.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130291, /*equipment_name*/"ブリリアントマイク", /*description*/"ノゾミ専用のハンドマイク。可愛ら\nしいリボンの中心には【カルミナ】\nのマークが描かれている。これを手\nにトップアイドルを目指す。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130301, /*equipment_name*/"紅蓮爆炎扇", /*description*/"ニノンが愛用する軍配。\n赤く染まった軍配が巻き起こす風は\n爆炎をまとい、立ち塞がるすべてを\n焼き尽くすまで消えることはない。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130321, /*equipment_name*/"紅輝石ウィスタリア", /*description*/"アキノの身を飾るブローチ。ウィス\nタリア家に代々伝わる輝石であり、\n所有者に高潔なる精神と類まれなる\n魔力を授けてくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/150.0, /*atk*/60.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130341, /*equipment_name*/"エターナルジョッキ", /*description*/"ユカリが手放せないジョッキ。飲ん\nでも飲んでも湧き出てくる命の水が\n所有者の体に染み渡り、その体に溢\nれんばかりの活力を与えてくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/12.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130461, /*equipment_name*/"ファンタズムタイヤキ", /*description*/"タマキが大切にしている首飾り。\nたいやき型のチャームに埋め込まれ\nた魔石によって、幻影を残すほどの\n身のこなしを手にできる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/5.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130481, /*equipment_name*/"溟竜槍レヴィアタン", /*description*/"ミフユ愛用のハルバード。\n一振りすれば穂先から水流が踊り、\n持ち主を水の加護によって激しくも\n流麗に守ってくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130581, /*equipment_name*/"プリンセスソード", /*description*/"ペコリーヌが使う『王家の装備』の\n真なる姿。もともと強力だったが、\n眠っていた力が解放されたことで、\n更なる強さを与えてくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130591, /*equipment_name*/"アメスアミュレット", /*description*/"コッコロが着用している花飾り。\n神と崇めし偉大な存在の加護を受け\nており、その花から漂う優しい香り\nは、戦う勇気を授けてくれる。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130601, /*equipment_name*/"ケイオスグリモワール", /*description*/"キャルが携える魔導書。本に宿る溢\nれんばかりの魔力をコントロールす\nることで、記された強大な術式を自\n在に操る。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/94.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130631, /*equipment_name*/"クインティタニアス", /*description*/"アリサが愛用している大弓。エルフ\nの間に伝わる伝説の弓であり、構え\nるだけで標的の動きは止まり、放た\nれる矢はあらゆる物質を射貫く。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
