INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130011, /*equipment_name*/"ニャンピオンベルト", /*description*/"ヒヨリが着用するベルト。\n雄々しき獣が睨みを利かせたバック\nルは、闘争本能を鼓舞し、その拳に\n更なる力を与える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130021, /*equipment_name*/"ブロッサムウィッシュ", /*description*/"ユイが着用しているカチューシャ。\n可愛らしく施されている花のレース\nと飾りは、仲間を守るための癒しの\n力を高めてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.3, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130031, /*equipment_name*/"ゲイルグローブ", /*description*/"レイが愛用しているグローブ。この\n攻守一体のグローブからくり出され\nる一突きは、立ちはだかるどんな敵\nをも貫く。", /*promotion_level*/98, /*hp*/5.5, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130041, /*equipment_name*/"トリックギフト", /*description*/"ミソギが最高傑作と自負するいたず\nらボックス。子供の遊びと侮ること\nなかれ、外見のインパクトと衝撃音\nで敵の度肝を抜くこと間違いなし。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.35, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.1, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130051, /*equipment_name*/"ジャスティスメダル", /*description*/"マツリが大切にしている、騎士団か\nら贈られた勲章。輝くメダルを見る\n度マツリの正義の心は強くなり、い\nかなる悪にも立ち向かう。", /*promotion_level*/98, /*hp*/6.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130061, /*equipment_name*/"デモンズトライデント", /*description*/"アカリの愛を一身に受けた魔槍。武\n器に秘められた強大な魔力は、小悪\n魔を刺激的な魔女へと、華麗に変貌\nさせる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.6, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130071, /*equipment_name*/"霊甘ゴーストプリン", /*description*/"ミヤコ最愛の巨大にして、素材の一\nつ一つを丁寧にこだわり抜いた究極\nのプリン。至高の域に至る美味しさ\nは、思わず体が透けてしまうほど。", /*promotion_level*/98, /*hp*/11.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.15, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130081, /*equipment_name*/"グリッターコンパクト", /*description*/"ユキが愛用しているコンパクト。\n眩い光を放つ装飾がユキの輝きを引\nき立て、全人類に多大な影響を与え\nるという。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.15, /*def*/0.2, /*magic_def*/0.2, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130091, /*equipment_name*/"螺旋霊撃疾風飛剣", /*description*/"疾風の冥姫が操りし魔装。凡俗では\n瞬く間に魔に呑まれ廃人となる。\n禍々しき宵闇色の刃が艶めく刹那、\n闇は疾風となり愚者を奈落へ誘う。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130101, /*equipment_name*/"ピュアメルヘンワンド", /*description*/"可愛らしくも絢爛な魔法杖。振れば\n支援の魔法と共にメルヘンチックな\n風が舞い、マホマホ王国の加護が付\n与される。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.1, /*def*/0.3, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130111, /*equipment_name*/"プロミネンスアロー", /*description*/"鮮烈な炎を宿すリノ専用の魔法矢。\n情熱的な想いが炎の源となっており\nその一撃は、戦闘を決定づける高威\n力を誇る。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.6, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130121, /*equipment_name*/"ミーティア☆リボン", /*description*/"シオリからハツネへと贈られた妹お\n手製のリボン。綺羅星に込められた\nシオリの想いが姉の魔力と呼応して\n敵の魔法防御力を低下させる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.05, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130131, /*equipment_name*/"ナナカサイツヨロッド", /*description*/"ナナカが振るう魔法の杖。\n天才魔法少女の電脳に閃光が走り、\n昂ぶった魔力を込めた超絶魔法で、\n敵を撃つことが出来るのだ！", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.75, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130141, /*equipment_name*/"ディテクティブルーペ", /*description*/"カスミが愛用する探偵道具の一つ。\n数々の事件を共に解決してきたこの\n虫眼鏡は、どんな些細な事象も\n見逃さず、相手の退路を断つ。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.5, /*def*/0.15, /*magic_def*/0.2, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130151, /*equipment_name*/"アガペーヴェール", /*description*/"ミサトが着用しているヴェール。\n清らかなヴェールから溢れ出るミサ\nトの慈愛が皆の精神を高揚させ、魔\n法クリティカルをアップさせる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.55, /*def*/0.15, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.15, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130161, /*equipment_name*/"クリティカルキス", /*description*/"スズナのとっておきのルージュ。そ\nの鮮やかな赤色は可憐さと妖艶さを\n併せ持ち、これを塗った唇からは何\n人たりとも目が離せない。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.9, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130171, /*equipment_name*/"球陽唐手秘伝ノ書", /*description*/"カオリが使う琉球空手の奥義が記さ\nれた巻物。特殊な術式により、達人\nの練気にのみ反応して文字が浮かび\nあがるという。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130181, /*equipment_name*/"グラマーウィップ", /*description*/"イオが教鞭として使っているムチ。\nどんなに味気ない勉学も、イオの手\nに掛かれば、甘いひと時に様変わり\nしてしまう。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130201, /*equipment_name*/"うさぎさんブレード", /*description*/"ミミが愛用している剣。\nその可愛らしさに夢中になったミミ\nが振り回せば、見た目に反した攻撃\n範囲で敵を痛い目に遭わせる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.4, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130211, /*equipment_name*/"ピュアアクトベル", /*description*/"クルミがお守りにしているベル。\n荘厳な見た目から奏でられる可愛ら\nしい音色が、怯える心に勇気を与え\nてくれる。", /*promotion_level*/98, /*hp*/5.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/10.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130221, /*equipment_name*/"デモンズジャベリン", /*description*/"ヨリの魔力を増幅することに特化さ\nれた魔槍。生命力と引き換えに得る\n魔力は、一振りで周囲を灰燼と化す\nほどのもの。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130231, /*equipment_name*/"偉大なクマ王ぷうきち", /*description*/"言わずと知れたアヤネの相棒がパワ\nーアップした姿。大いなる力を得た\nぷうきちの攻撃は敵の意識を長時間\n奪うことができる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.2, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130251, /*equipment_name*/"ロイヤルメイドドレス", /*description*/"ご奉仕の心を体現したエプロンドレ\nス。エプロンから溢れるサレンへの\n忠誠心がスズメの魔力を高め、敵の\n物理防御力を下げる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.8, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130261, /*equipment_name*/"ジュエリズムドングリ", /*description*/"膨大な魔力を内包する伝説のドング\nリが装飾された首飾り。リンの放つ\n魔法を増幅し、周囲の味方を大きく\n力づける。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.5, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130271, /*equipment_name*/"ノーマーシー", /*description*/"過激な愛情を破壊力へと変転させる\nエリコ最狂の愛を錬成する霊斧。\n一閃するたびに破滅への狂想曲が奏\nでられる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130281, /*equipment_name*/"グロリアスフェザー", /*description*/"サレン愛用のヘアアクセサリー。\n優しさの中に秘めた強さが象徴され\nている。その翼が羽ばたくとき、\n体は重力から解き放たれる。", /*promotion_level*/98, /*hp*/1.25, /*atk*/5.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130291, /*equipment_name*/"ブリリアントマイク", /*description*/"ノゾミ専用のハンドマイク。可愛ら\nしいリボンの中心には【カルミナ】\nのマークが描かれている。これを手\nにトップアイドルを目指す。", /*promotion_level*/98, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.05, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130301, /*equipment_name*/"紅蓮爆炎扇", /*description*/"ニノンが愛用する軍配。\n赤く染まった軍配が巻き起こす風は\n爆炎をまとい、立ち塞がるすべてを\n焼き尽くすまで消えることはない。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130311, /*equipment_name*/"グリムソウルローブ", /*description*/"蒼き霊力の炎をまとった、\nシノブ愛用の霊衣。\n霊の依代となる特殊な糸で紡がれ、\n霊たちの協力を得ることができる。", /*promotion_level*/98, /*hp*/12.5, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130321, /*equipment_name*/"紅輝石ウィスタリア", /*description*/"アキノの身を飾るブローチ。ウィス\nタリア家に代々伝わる輝石であり、\n所有者に高潔なる精神と類まれなる\n魔力を授けてくれる。", /*promotion_level*/98, /*hp*/7.5, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130331, /*equipment_name*/"エリザベスポンチョ", /*description*/"愛牛・エリザベスをイメージしてデ\nザインされたマヒルのポンチョ。着\nればボケのキレが増し、相手を混乱\nに陥れる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.05, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130341, /*equipment_name*/"エターナルジョッキ", /*description*/"ユカリが手放せないジョッキ。飲ん\nでも飲んでも湧き出てくる命の水が\n所有者の体に染み渡り、その体に溢\nれんばかりの活力を与えてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130361, /*equipment_name*/"コスモブルーロッド", /*description*/"キョウカ愛用の杖。宝玉に高位の水\n龍の力を宿し、使用者によってその\n形態を変えるという言い伝えが残さ\nれている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.9, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130371, /*equipment_name*/"天剣白嵐", /*description*/"ミクマ流に伝わる宝剣。\n羽のように軽くあつらえられた剣は\nトモの業前と呼応し、数多の敵を薙\nぎ払う旋風を巻き起こす。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130381, /*equipment_name*/"ハツネお手製の護り石", /*description*/"ハツネからシオリへと贈られた姉お\n手製の護り石。込められたハツネの\n想いが妹を勇気づけ、普段出せない\n力を発揮させる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.15, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130401, /*equipment_name*/"BB団の絆ベレー", /*description*/"アオイが身に着けているベレー帽。\nあしらわれた一対の羽は固い絆の象\n徴。紡がれた絆を胸に、アオイは強\nい意志で立ちはだかる敵を穿つ。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130421, /*equipment_name*/"翠霊譜ルーンノーツ", /*description*/"勇気の唱喚術が記された楽譜。チカ\nの澄みわたる歌声の加護により味方\nの感覚は鋭敏になり、敵の急所が突\nきやすくなる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.2, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.3, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130431, /*equipment_name*/"ウルフェンブレード", /*description*/"青狼の魂宿りし大剣。その一振りは\n装備者の豪胆さを象徴する竜巻の如\nく敵を襲い、同時に眠れる力を呼び\n覚ます。", /*promotion_level*/98, /*hp*/0.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130441, /*equipment_name*/"闇斧ナハトファング", /*description*/"イリヤと共に永き時を過ごした斧。\n紅き魔力の迸る巨刃は一振りで敵を\n殲滅、飛び散る鮮血の一滴までも啜\nり尽くし、主の糧とする。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/0.2, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.05, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130451, /*equipment_name*/"プレジャーチョーカー", /*description*/"クウカが着用している拘束具も兼ね\nたチョーカー。自らの動きを封じる\nことで敵を惹きつけ、人知れず悦楽\nを貪る。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.35, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130461, /*equipment_name*/"ファンタズムタイヤキ", /*description*/"タマキが大切にしている首飾り。\nたいやき型のチャームに埋め込まれ\nた魔石によって、幻影を残すほどの\n身のこなしを手にできる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130471, /*equipment_name*/"シュバリエメイル", /*description*/"正義の豪炎をまとったジュンの鎧。\n付与された魔力は敵のいかなる攻撃\nも寄せ付けず、味方には勇気と活力\nを与える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130481, /*equipment_name*/"溟竜槍レヴィアタン", /*description*/"ミフユ愛用のハルバード。\n一振りすれば穂先から水流が踊り、\n持ち主を水の加護によって激しくも\n流麗に守ってくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130491, /*equipment_name*/"セイクリッドブレード", /*description*/"シズル愛用の十字剣。\n弱き者を助けたいと願う気高き魂が\n剣に宿る時、防御の術式が発動し、\n対象を守護する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130501, /*equipment_name*/"魔眼杖デモネス・アイ", /*description*/"ミサキの武器兼パートナーの杖。\nさらなる進化を遂げた生ける魔杖だ\nが、悲しいかな現在はメイクの実験\n台と化している。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.85, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.4, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130511, /*equipment_name*/"ローズオブカース", /*description*/"妖美な薔薇の装飾をあしらった\nミツキ愛用の眼帯。\n魔力を込めると七色の光を放射し、\n相手の能力を減退させる。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130521, /*equipment_name*/"アンビシャスドレス", /*description*/"いつかこれを着たい…！そんなリマ\nの夢が詰まった、オーダーメイドの\nロングドレス。夢見る乙女の妄想力\nが今、覚醒める。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.25, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130531, /*equipment_name*/"フリューゲルコート", /*description*/"モニカが着用しているコート。\nその豪奢な仕立ては、背中に続く者\nたちの心身を奮い立たせ、ありとあ\nらゆる攻撃力を大幅に向上させる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.6, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.2, /*physical_critical*/0.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130541, /*equipment_name*/"ハイクチュリエベルト", /*description*/"ツムギが着用している、ボビンを格\n納した特製の携行ベルト。ボビンか\nら素早く引き出された魔法の糸が敵\nを縛り上げ、瞬く間に意識を奪う。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.2, /*physical_critical*/0.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130551, /*equipment_name*/"ストーキングマント", /*description*/"自分の存在が察知されない工夫がさ\nれた、アユミお手製のマント。\n気付かれぬ間に忍び寄ることができ\n心を寄せる先輩に更に近付ける。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.7, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130561, /*equipment_name*/"真打 絶海・波浪丸", /*description*/"ルカが帯刀する二本の刀。\n群がる悪党を前に二本目の刀・\n波浪丸をひとたび抜けば、\nたちまち戦場は凪ぐという。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130571, /*equipment_name*/"アシュケロン", /*description*/"剣の道を極めんとジータが手にした\n片手剣。寂滅した世界の中で自在の\n剣を振り続ける者を、いつしかひと\nは剣の聖と呼んだ。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.5, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.1, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130581, /*equipment_name*/"プリンセスソード", /*description*/"ペコリーヌが使う『王家の装備』の\n真なる姿。もともと強力だったが、\n眠っていた力が解放されたことで、\n更なる強さを与えてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130591, /*equipment_name*/"アメスアミュレット", /*description*/"コッコロが着用している花飾り。\n神と崇めし偉大な存在の加護を受け\nており、その花から漂う優しい香り\nは、戦う勇気を授けてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130601, /*equipment_name*/"ケイオスグリモワール", /*description*/"キャルが携える魔導書。本に宿る溢\nれんばかりの魔力をコントロールす\nることで、記された強大な術式を自\n在に操る。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.7, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130611, /*equipment_name*/"絆の証", /*description*/"ムイミが肌身離さず着用しているブ\nローチ。かつてこれを託してくれた\n相棒との絆の温もりが、天楼覇断剣\nを振るう力を与える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.95, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.45, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130631, /*equipment_name*/"クインティタニアス", /*description*/"アリサが愛用している大弓。エルフ\nの間に伝わる伝説の弓であり、構え\nるだけで標的の動きは止まり、放た\nれる矢はあらゆる物質を射貫く。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.8, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130651, /*equipment_name*/"ドラゴンズフィスト", /*description*/"カヤが身につける、巨大な竜の腕の\n如き篭手。装着者の激情に応じ噴き\n出す業火は、相対する者全てを焼き\n尽くすと伝えられている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.8, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.55, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130661, /*equipment_name*/"イノリのピコハン", /*description*/"イノリの愛用するハンマー。ホマレ\nによって改造されており、見た目と\nは裏腹に、ドラゴン族の覚醒にも耐\nえられる強度となっている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130701, /*equipment_name*/"ワンドオブミラージュ", /*description*/"ネネカが愛用している杖。繰り出さ\nれる幻影魔法は対象者を完全に欺き\n魔法にかけられたことすら忘れさせ\nる。全ては変貌大妃の掌の上。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.55, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.7, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130711, /*equipment_name*/"聖域剣アヴァロン", /*description*/"クリスティーナが振るう大剣。\n計算し尽された切っ先が描く剣筋は\n何人たりとも逃れる術を与えず一刀\n両断にし、絶対の勝利を約束する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130751, /*equipment_name*/"傘剣ビーチプリンセス", /*description*/"ペコリーヌ愛用の夏を彩る鮮やかな\n日傘剣。弾ける笑顔と華麗かつキュ\nートな剣技が合わされば、ビーチの\n視線は一人占め間違いなし。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130761, /*equipment_name*/"涼槍アクアスカッシュ", /*description*/"コッコロの夏仕様の槍。清涼感溢れ\nる澄み切った切っ先は目にも涼しい\n蒼の軌跡を描き、荒ぶる魔物も酷暑\nも一薙ぎでクールダウン。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.1, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130771, /*equipment_name*/"陽杖シャインサンデー", /*description*/"スズメの夏用に新調した杖。華やか\nなデコレーションがリゾート感を全\n力で演出し、さらにどんなに転んで\nもパフェがこぼれない優れもの。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.0, /*def*/0.3, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130781, /*equipment_name*/"猫輪サマーフロート", /*description*/"キャルが夏を楽しむために密かに用\n意した浮輪。本人は安定性の良さで\n選んだと言い張るが、可愛さ全振り\nのデザインなので怪しいところ。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.75, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130791, /*equipment_name*/"氷鯛アイスダガー", /*description*/"タマキが暑い夏を乗り切るために常\n備しているアイスキャンディー。\n凍らせたたいやきのフレーバーが足\nさばきを軽くさせ、敵を翻弄する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.5, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130801, /*equipment_name*/"蒼竜槍ミズチ", /*description*/"ミフユが効率的に夏を過ごすための\n槍。激しく荒れ狂う海神のような水\n流をまとった一撃は、最高効率で敵\nを薙ぎ払う。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.3, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.15, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130811, /*equipment_name*/"霊鎌パンプキンサイズ", /*description*/"ドクロ親父がシノブのために用意し\nた霊鎌。ハロウィンの加護を宿して\nおり、ランタンの灯は霊を冥界へと\n導き、その刃は悪霊をも切り裂く。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130821, /*equipment_name*/"プリンオブハロウィン", /*description*/"ハロウィンパーティーが表現された\n職人自慢のプリン。ほんのりと甘い\nカボチャ味は、あらゆるプリンを食\nべつくしたミヤコをも驚愕させた。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.3, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.5, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130831, /*equipment_name*/"魔箒ハロウィン・アイ", /*description*/"ミサキにハロウィンコーデされた生\nける魔杖アイちゃん。大人のばいお\nれんすと、ハロウィンコスのかわい\nさをあわせ持つ完璧な出来らしい。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.05, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130841, /*equipment_name*/"翠霊杖ルーンスノウ", /*description*/"聖夜の精霊たちを宿したチカの魔法\n杖。唱喚術に呼応した精霊たちによ\nるクリスマスの加護が、対象者の能\n力を大きく向上させる。", /*promotion_level*/98, /*hp*/4.0, /*atk*/0.0, /*magic_str*/2.3, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130851, /*equipment_name*/"クリスマスアクトベル", /*description*/"クリスマスの祝福を受けたクルミ愛\n用のベル。クルミがベルを奏でて託\nす健気な想いが、仲間に決してくじ\nけない勇気を呼び起こす。", /*promotion_level*/98, /*hp*/6.25, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130861, /*equipment_name*/"クリスマス王ぷうきち", /*description*/"クリスマスカラーで彩られたアヤネ\nの相棒。高まる愛らしさと共にアヤ\nネの能力は引き上げられ、より強力\nな一撃を生み出すことができる。", /*promotion_level*/98, /*hp*/2.5, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.05, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130871, /*equipment_name*/"猫明神ニャンフィスト", /*description*/"猫に縁のある神社に祀られていた由\n緒正しき籠手。装着者に勝利を招く\nとされ、ヒヨリの正しい心と呼応し\nそのご利益は絶大である。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.3, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130881, /*equipment_name*/"曉の祝杖ブロッサム", /*description*/"新年を祝う祭で使われてきた神具の\n杖。みんなの幸せを願いながら振る\nえば花びらが舞い散り、新たな年の\n門出を鮮やかに彩る。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.5, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/1.5, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130891, /*equipment_name*/"氷麗刀・初華", /*description*/"年初めに神を迎える神事に用いられ\nた最上の一振り。氷の如き冷たく美\nしい刀身は、深き夜の闇や禍々しき\n煩悩をも一刀両断する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.2, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.65, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130901, /*equipment_name*/"デストロイホイッパー", /*description*/"運命の人にチョコレートを作るため\n用意した、エリコにしか御しきれな\nい調理器具。愛の詰まった一撃はあ\nらゆるものを叩き壊し、すり潰す。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130911, /*equipment_name*/"デコレーションハート", /*description*/"大切な弟くんに美味しいチョコを作\nるため、シズルが身に着ける帽子。\nハートの飾りからあふれる甘い香り\nが、仲間に愛と安らぎを与える。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130921, /*equipment_name*/"アンの魔導書", /*description*/"アン愛用の魔導書。類稀なる魔法の\n才能を最大限に引き出すことができ\nる。国を導かんとする王女の決意が\n英霊の力を更に強力なものにする。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130931, /*equipment_name*/"オメメちゃん！", /*description*/"ルゥの相棒・オメメちゃん。見慣れ\nぬ異世界でもなぜか召喚され、いつ\nものように道具として用いられるの\nは、信頼関係があるから…のはず。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/1.75, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130941, /*equipment_name*/"ドラグアミュレット", /*description*/"グレアの持つ力を高めてくれる\n竜のアミュレット。宝石に込めら\nれた加護を受け竜姫の炎は勢いを\n増し、全ての敵を燃やし尽くす。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.6, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.15, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130951, /*equipment_name*/"千紫万紅艶椿", /*description*/"クウカの心を熱くする妖艶な着物。\n帯の締め付け具合によって興奮が高\nまり、それを解放した時、未知なる\n悦楽が回転とともに解き放たれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.35, /*magic_def*/0.6, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130961, /*equipment_name*/"なりきりクノイチセット", /*description*/"ニノンがオーエド町で買った忍者に\nなりきるための装備。武器自体に特\n別な力はないが、これを持つニノン\nはなぜかクノイチとして覚醒する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/2.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130971, /*equipment_name*/"レムの髪飾り", /*description*/"レムが常に身に着けている髪飾り。\n大好きな姉とお揃いであり、姉の髪\n色に合わせた赤の紐細工が気に入っ\nているらしい。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.65, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/2.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130981, /*equipment_name*/"ラムの髪飾り", /*description*/"ラムが常に身に着けている髪飾り。\n彼女にとってメイドとしての証でも\nあり、妹の髪色に合わせた青の紐\n細工が気に入っているらしい。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.6, /*def*/0.4, /*magic_def*/0.1, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130991, /*equipment_name*/"約束の花束", /*description*/"エミリアが大切にしている花束。舞\nい散る雪のように儚くきらめく花弁\nはマナに満ち溢れており、精霊たち\nに愛寵されている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.8, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.3, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131001, /*equipment_name*/"盛夏弓スプラッシュハート", /*description*/"夏の仕事に向けスズナが新調した\n弓。夏真っ盛りの海辺を思わせる涼\nし気なデザインと配色が、スズナこ\nだわりの推しポイント。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.8, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131011, /*equipment_name*/"恵愛剣サマーラブファミリア", /*description*/"夏の海辺でイオが生徒を護るために\n振るう剣。持ち前の生徒を想う心を\n癒しの力へ変えてくれる。この夏も\n大活躍間違いなし。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.05, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.3, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131031, /*equipment_name*/"繊麗剣サマーブライト", /*description*/"サレンが夏に携えている剣。救護院\nのママとしてではなく、一人の女性\nとしても夏を楽しんでほしいという\n子供たちの願いが込められている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/1.5, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131041, /*equipment_name*/"サマーウルフェンソード", /*description*/"特別な凪の海に顕れると云われる\n海狼の牙を鍛え上げた大剣。狼の\n飾りはマコトの大のお気に入り。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.4, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131051, /*equipment_name*/"琉球犬式・神舞之籠手", /*description*/"海神たる大海原に舞を捧げるための\n神具の籠手。布の模様は海への数多\nの感謝が込められており、この籠手\nで舞うカオリは大海を体現する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.5, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.4, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131061, /*equipment_name*/"夏華杖ファンシーロア", /*description*/"ビーチ仕様のマホ姫謹製、魔法杖。\n夏の情景が封じ込められており、\n一振りすれば、灯火が映し出す影絵\nと共に夜空に大輪の華が咲く。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.9, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.9, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131071, /*equipment_name*/"なかよしの弓琴", /*description*/"聖テレサ女学院編入に際し新調した\nアオイのぼっち克服のための最終兵\n器。お守り代わりのものだったが、\n今は絆の象徴となっている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.55, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131081, /*equipment_name*/"フロムダスク・ティルドーン", /*description*/"調理・裁縫・弟たちの図画工作など\n用途多彩なクロエの万能日用道具。\nと言い張ることで教務主任の所持品\n検査に対し絶大な防御力をふるう。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.85, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131091, /*equipment_name*/"ももいろ☆ぺんら☆なっくる", /*description*/"かわつよを追求するチエルお手製の\nはぴちぇる拳鍔。有事には蛍光ピン\nクの爪をぽきりと折れば発光魔法が\n発動して憧れの歌姫を応援できる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.95, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/2.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131101, /*equipment_name*/"ペンシルアックス・レプリカ", /*description*/"ユニが伊達や酔狂で携行する、鉾槍\nの模造品。重厚な外見に反してお子\n様も安心の軽量設計。自立性はなく\n支えが失われればパタリと倒れる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.8, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/10.0, /*wave_energy_recovery*/1.5, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131111, /*equipment_name*/"ハロウィン猫さんブルーム", /*description*/"ハロウィンのためにキョウカがこっ\nそり用意した黒猫がモチーフのほう\nき。時折またがって空を飛ぶ想像を\nするのはみんなには秘密らしい。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.8, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131121, /*equipment_name*/"魔拳トリックオアトリック", /*description*/"ハロウィンのために、ミソギが自信\n満々に準備した特別なイタズラ用の\n手甲。高い強度と精密性を両立し、\nどんなイタズラもバッチリらしい。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.2, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.05, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131131, /*equipment_name*/"ぐるぐるうさぎさんブレード", /*description*/"ハロウィンのためにミミがお母さん\nと作った、包帯でぐるぐる巻きのう\nさぎさんブレード。かわいらしさと\n手触り両方がアップしたらしい。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.65, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.6, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131141, /*equipment_name*/"ルナのおともだち", /*description*/"ルナが常に抱いているおともだち。\n物言わぬ小さな友は今日もつぶらな\n瞳のままルナを見つめ続けている。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.75, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/1.5, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131151, /*equipment_name*/"聖刻剣アヴァロン", /*description*/"サンタクロース候補となったクリス\nティーナに装いを合わせた聖域剣ア\nヴァロン。聖夜の奇跡により主に絶\n対の勝利を贈るという。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.3, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131161, /*equipment_name*/"聖霊剣セイントホープ", /*description*/"サンタクロース候補となったノゾミ\nの片手剣。アイドルとしての決意と\nたくさんの人への夢と希望の想いが\nその刃に眩い煌きを与えている。", /*promotion_level*/98, /*hp*/14.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.6, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.2, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131171, /*equipment_name*/"聖夜斧ノエルファング", /*description*/"サンタクロース候補となったイリヤ\n愛用の斧が聖夜の奇跡により魅せた\n新たな姿。柊により華麗に彩られし\n巨刃は不滅の輝きを宿している。", /*promotion_level*/98, /*hp*/5.0, /*atk*/0.0, /*magic_str*/3.75, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.4, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131181, /*equipment_name*/"迎春美食の晴着", /*description*/"ペコリーヌが用意した正月用の晴れ\n着。王家御用達の反物による逸品で\n豊穣を祈願した稲穂の総柄。王女の\n威厳と美食への敬意が溢れている。", /*promotion_level*/98, /*hp*/10.0, /*atk*/3.5, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131191, /*equipment_name*/"新春祈幸の精霊杖", /*description*/"晴れ着姿のコッコロが持つ神気溢れ\nる神聖な杖。新春を祝う巫女の舞が\n周囲の仲間たちにも活力を分け与え\n新たな年の幸せを約束する。", /*promotion_level*/98, /*hp*/1.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.5, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131201, /*equipment_name*/"賀正猫耳の羽子板", /*description*/"晴れ着姿のキャルが持つ特注品の羽\n子板。新たな年の多幸を願う気持ち\nが無数の羽根を生み出し、あらゆる\n災いを祓い退ける。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.7, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/1.25, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131211, /*equipment_name*/"神殿巫女の鈴飾り", /*description*/"神殿で巫女を務めるスズメが持つ神\n楽鈴。代々受け継がれた巫女たちの\n祈りが籠められており鈴の音の届く\n範囲の邪気を打ち祓う。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.35, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.5, /*energy_recovery_rate*/0.15, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131221, /*equipment_name*/"マジカルミスティロッド", /*description*/"魔法探偵ミスティ★カスミが操る魔\n法の杖。愛と希望の力を込めること\nで邪悪な敵を捕らえ、どんな真実も\n明らかにする輝きを放つ。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.6, /*def*/0.2, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131231, /*equipment_name*/"マジカルピュアリーボウ", /*description*/"魔法狩人ピュアリー★シオリが操る\n魔法の弓。愛と希望の力を込めるこ\nとで、どんな邪悪な敵も貫く必殺の\n矢を放つことができる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/2.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131241, /*equipment_name*/"ステージオブキュート", /*description*/"ウヅキの持つオーソドックスな剣。\n特別な力はなさそうに見えるがウヅ\nキの笑顔とあたたかい想いに応じて\n輝き、秘めた力を発揮する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.1, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.7, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131251, /*equipment_name*/"ステージオブクール", /*description*/"リンの操る蒼く美しき剣。クールに\n燃えるリンの熱い心に呼応して蒼い\n輝きを放ち、どこまでも高みを目指\nし走り続ける彼女の旅路を照らす。", /*promotion_level*/98, /*hp*/11.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131261, /*equipment_name*/"ステージオブパッション", /*description*/"ミオ愛用のきらめく星杖。ミオの明\nるい歌声に応え、小さな流星を呼び\nよせ、歌う様子はスペースオペラの\nよう。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.8, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131271, /*equipment_name*/"ドングリレンジャーランス", /*description*/"リンが鎧とともに買ってもらった母\nの愛がたくさん詰まった槍。凄まじ\nい硬度を誇っているがとても軽量で\nリンの力でも自在に振り回せる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.9, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131281, /*equipment_name*/"エリザベスフォーク", /*description*/"牧場に代々伝わる由緒正しい装備。\n魔物から牧場を守るためのものだが\n日常の動物たちの世話にも生かせる\nよう農具に似た形状である。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.35, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.3, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131291, /*equipment_name*/"ワンダーランドボウ", /*description*/"不思議な本の中でアリスの力を得た\nリノの弓。アリスと心を通わせるこ\nとにより救世主としての力を解放し\n世界を救う一撃を放つ。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.5, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131301, /*equipment_name*/"ワンダーランドクロック", /*description*/"不思議な本の中でアユミが持ってい\nた槍。周囲から限りなく気配を消す\nことで時の流れを止めるほどの効果\nを発揮するアユミにぴったりの槍。", /*promotion_level*/98, /*hp*/10.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.15, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.05, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131311, /*equipment_name*/"絶海・波浪丸 拵違", /*description*/"ルカが帯刀する名刀、絶海と妖刀、\n波浪丸の二刀。持ち主に合わせ夏の\n装いへとこしらえた逸品。不変の切\nれ味は白波をも切り裂く。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.65, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131321, /*equipment_name*/"海魔獄滅縛鎖錨", /*description*/"海獄に降臨せし疾風の冥姫がその身\nに纏いし呪われた縛鎖の錨。封じら\nれた暴虐の竜が咆哮する刻、地獄の\n亡者すら慄き逃げ惑う。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.1, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.4, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131331, /*equipment_name*/"ナナカナツカワロッド", /*description*/"夏の装いのナナカが持つ魔法の杖。\n天才魔法少女が夏のトキメキに触れ\nた時、秘められし真の力が解き放た\nれちゃうのだ！", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.55, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131341, /*equipment_name*/"グリッタースターズ☆ポンポン", /*description*/"ハツネが青春に全てを捧ぐ者へ、綺\n羅星のエールを放つポンポン。七色\nの応援は海辺を彩り、どんな逆境も\n乗り越えて、夢への道を切り拓く。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.65, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131351, /*equipment_name*/"ビーチビーナスメガホン", /*description*/"ミサトが青春に全てを捧ぐ者へ、慈\n愛の声を届けるメガホン。海辺の聖\n母の応援は、体の底から力を湧き上\nがらせ、夢に向かう後押しをする。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/1.5, /*def*/0.0, /*magic_def*/0.6, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131361, /*equipment_name*/"サマーシュバリエソード", /*description*/"ジュンが非番を海で過ごすためにあ\nつらえた涼やかな大剣。見た目に反\nして、放たれる斬撃は真夏の太陽の\nごとく灼熱。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.45, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131371, /*equipment_name*/"ハッピーエンジェルボウ", /*description*/"エンジェルとなったアカリの持つ天\n界の弓。杖として使用することも可\n能で、対となるヨリの矢と組み合わ\nせることで真価を発揮する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.65, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131381, /*equipment_name*/"ハッピーエンジェルアロー", /*description*/"エンジェルとなったヨリの持つ天界\nの矢。槍として使用することも可能\nで、対となるアカリの弓と組み合わ\nせることで真価を発揮する。", /*promotion_level*/98, /*hp*/15.0, /*atk*/0.0, /*magic_str*/3.2, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131391, /*equipment_name*/"ハロウィンナイトドレス", /*description*/"吸血鬼をイメージしたツムギお手製\nの新作ドレス。クールとキュートを\n両立したちょっぴり甘辛なコーデで\n攻めの姿勢をアピール。", /*promotion_level*/98, /*hp*/3.75, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131401, /*equipment_name*/"ビューティーホロウ", /*description*/"ツムギが仕立てたレイ専用ドレス。\n随所に繊細な装飾が施され、レイの\n剣技を邪魔しないように機能性にも\n配慮された珠玉のハロウィン衣装。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131411, /*equipment_name*/"ホワイトタイガーフォーム", /*description*/"ハロウィンの夜にマツリが獲得した\n新たな変身フォーム…という設定の\n新コスチューム。ツムギのお手製で\n着心地抜群、作りも頑丈である。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131421, /*equipment_name*/"マジカルラブリーソード", /*description*/"魔法提督ラブリー★モニカが操る魔\n法の剣。愛と希望の力を込めること\nで仲間たちを鼓舞し、どんな邪悪な\n敵にも立ち向かう勇気を与える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.9, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131431, /*equipment_name*/"マジカルシャイニーレイピア", /*description*/"魔法剣士シャイニー★トモが操る魔\n法の突剣。愛と希望の力を込めるこ\nとでどんな邪悪な敵も打ち砕く強烈\nな一撃を放つことができる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.5, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/3.75, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131441, /*equipment_name*/"ウィスタリアノエルドレス", /*description*/"アキノのクリスマスドレス。ウィス\nタリア家のお抱えテーラーの作りで\n絢爛豪華でありながら、質実剛健な\n装備としても珠玉の逸品である。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.6, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131451, /*equipment_name*/"サレンディアノエルドレス", /*description*/"サレンのためにスズメたちが用意し\nた、クリスマス用のパーティードレ\nス。古いドレスを仕立て直したみん\nなの思いが詰まった一着。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.3, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131461, /*equipment_name*/"クリスマスロワイヤルグラス", /*description*/"ユカリがクリスマスのために用意し\nた、特別なカクテルグラス。しゅわ\nしゅわ弾ける命の水が高揚感と共に\n最高の聖夜を演出してくれる。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131471, /*equipment_name*/"大悪党の新春晴れ着", /*description*/"ムイミがオーマを救った礼として、\n町の住民たちから贈られた晴れ着。\n大悪党らしさあふれる豪快な着こな\nしで、大立ち回りもなんのその。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.05, /*magic_str*/0.0, /*def*/0.2, /*magic_def*/0.2, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/131501, /*equipment_name*/"新春幻影和傘", /*description*/"晴れ着姿のネネカが持つ傘。ひとた\nび傘を開くと、幻影の魔法があふれ\n出し、目にした者の精神を夢幻の檻\nに囚え続けて離さないという。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.3, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
