INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130011, /*equipment_name*/"ニャンピオンベルト", /*description*/"ヒヨリが着用するベルト。\n雄々しき獣が睨みを利かせたバック\nルは、闘争本能を鼓舞し、その拳に\n更なる力を与える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130021, /*equipment_name*/"ブロッサムウィッシュ", /*description*/"ユイが着用しているカチューシャ。\n可愛らしく施されている花のレース\nと飾りは、仲間を守るための癒しの\n力を高めてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.3, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130031, /*equipment_name*/"ゲイルグローブ", /*description*/"レイが愛用しているグローブ。この\n攻守一体のグローブからくり出され\nる一突きは、立ちはだかるどんな敵\nをも貫く。", /*promotion_level*/98, /*hp*/5.5, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130061, /*equipment_name*/"デモンズトライデント", /*description*/"アカリの愛を一身に受けた魔槍。武\n器に秘められた強大な魔力は、小悪\n魔を刺激的な魔女へと、華麗に変貌\nさせる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.6, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130091, /*equipment_name*/"螺旋霊撃疾風飛剣", /*description*/"疾風の冥姫が操りし魔装。凡俗では\n瞬く間に魔に呑まれ廃人となる。\n禍々しき宵闇色の刃が艶めく刹那、\n闇は疾風となり愚者を奈落へ誘う。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130111, /*equipment_name*/"プロミネンスアロー", /*description*/"鮮烈な炎を宿すリノ専用の魔法矢。\n情熱的な想いが炎の源となっており\nその一撃は、戦闘を決定づける高威\n力を誇る。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.6, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130121, /*equipment_name*/"ミーティア☆リボン", /*description*/"シオリからハツネへと贈られた妹お\n手製のリボン。綺羅星に込められた\nシオリの想いが姉の魔力と呼応して\n敵の魔法防御力を低下させる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.05, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130171, /*equipment_name*/"球陽唐手秘伝ノ書", /*description*/"カオリが使う琉球空手の奥義が記さ\nれた巻物。特殊な術式により、達人\nの練気にのみ反応して文字が浮かび\nあがるという。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130211, /*equipment_name*/"ピュアアクトベル", /*description*/"クルミがお守りにしているベル。\n荘厳な見た目から奏でられる可愛ら\nしい音色が、怯える心に勇気を与え\nてくれる。", /*promotion_level*/98, /*hp*/5.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/10.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130221, /*equipment_name*/"デモンズジャベリン", /*description*/"ヨリの魔力を増幅することに特化さ\nれた魔槍。生命力と引き換えに得る\n魔力は、一振りで周囲を灰燼と化す\nほどのもの。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130261, /*equipment_name*/"ジュエリズムドングリ", /*description*/"膨大な魔力を内包する伝説のドング\nリが装飾された首飾り。リンの放つ\n魔法を増幅し、敵の抵抗力を失わせ\nる効果を備える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.5, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130271, /*equipment_name*/"ノーマーシー", /*description*/"過激な愛情を破壊力へと変転させる\nエリコ最狂の愛を錬成する霊斧。\n一閃するたびに破滅への狂想曲が奏\nでられる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130281, /*equipment_name*/"グロリアスフェザー", /*description*/"サレン愛用のヘアアクセサリー。\n優しさの中に秘めた強さが象徴され\nている。その翼が羽ばたくとき、\n体は重力から解き放たれる。", /*promotion_level*/98, /*hp*/1.25, /*atk*/5.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130291, /*equipment_name*/"ブリリアントマイク", /*description*/"ノゾミ専用のハンドマイク。可愛ら\nしいリボンの中心には【カルミナ】\nのマークが描かれている。これを手\nにトップアイドルを目指す。", /*promotion_level*/98, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.05, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130301, /*equipment_name*/"紅蓮爆炎扇", /*description*/"ニノンが愛用する軍配。\n赤く染まった軍配が巻き起こす風は\n爆炎をまとい、立ち塞がるすべてを\n焼き尽くすまで消えることはない。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130311, /*equipment_name*/"グリムソウルローブ", /*description*/"蒼き霊力の炎をまとった、\nシノブ愛用の霊衣。\n霊の依代となる特殊な糸で紡がれ、\n霊たちの協力を得ることができる。", /*promotion_level*/98, /*hp*/12.5, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130321, /*equipment_name*/"紅輝石ウィスタリア", /*description*/"アキノの身を飾るブローチ。ウィス\nタリア家に代々伝わる輝石であり、\n所有者に高潔なる精神と類まれなる\n魔力を授けてくれる。", /*promotion_level*/98, /*hp*/7.5, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130331, /*equipment_name*/"エリザベスポンチョ", /*description*/"愛牛・エリザベスをイメージしてデ\nザインされたマヒルのポンチョ。着\nればボケのキレが増し、相手を混乱\nに陥れる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.05, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130341, /*equipment_name*/"エターナルジョッキ", /*description*/"ユカリが手放せないジョッキ。飲ん\nでも飲んでも湧き出てくる命の水が\n所有者の体に染み渡り、その体に溢\nれんばかりの活力を与えてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130431, /*equipment_name*/"ウルフェンブレード", /*description*/"青狼の魂宿りし大剣。その一振りは\n装備者の豪胆さを象徴する竜巻の如\nく敵を襲い、同時に眠れる力を呼び\n覚ます。", /*promotion_level*/98, /*hp*/0.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130461, /*equipment_name*/"ファンタズムタイヤキ", /*description*/"タマキが大切にしている首飾り。\nたいやき型のチャームに埋め込まれ\nた魔石によって、幻影を残すほどの\n身のこなしを手にできる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130471, /*equipment_name*/"シュバリエメイル", /*description*/"正義の豪炎をまとったジュンの鎧。\n付与された魔力は敵のいかなる攻撃\nも寄せ付けず、味方には勇気と活力\nを与える。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130481, /*equipment_name*/"溟竜槍レヴィアタン", /*description*/"ミフユ愛用のハルバード。\n一振りすれば穂先から水流が踊り、\n持ち主を水の加護によって激しくも\n流麗に守ってくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130491, /*equipment_name*/"セイクリッドブレード", /*description*/"シズル愛用の十字剣。\n弱き者を助けたいと願う気高き魂が\n剣に宿る時、防御の術式が発動し、\n対象を守護する。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130511, /*equipment_name*/"ローズオブカース", /*description*/"妖美な薔薇の装飾をあしらった\nミツキ愛用の眼帯。\n魔力を込めると七色の光を放射し、\n相手の能力を減退させる。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130521, /*equipment_name*/"アンビシャスドレス", /*description*/"いつかこれを着たい…！そんなリマ\nの夢が詰まった、オーダーメイドの\nロングドレス。夢見る乙女の妄想力\nが今、覚醒める。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.25, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130581, /*equipment_name*/"プリンセスソード", /*description*/"ペコリーヌが使う『王家の装備』の\n真なる姿。もともと強力だったが、\n眠っていた力が解放されたことで、\n更なる強さを与えてくれる。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
