INSERT INTO `unit_comments` VALUES (/*id*/1007014, /*unit_id*/100761, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"冥土のおみやげに\nオマエらをプリンにしてやるの\nプリンはおみやげに\nもってこいなの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1007015, /*unit_id*/100761, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オマエの恨みはミヤコが晴らすの\nだからスプーン二本なの\n二人分の恨み、\nミヤコが思い知らせてやるの～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
