INSERT INTO `unit_comments` VALUES (/*id*/1008006, /*unit_id*/100831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな宝石や宝物も…\nボクの美しさと輝きには\n勝てないんだなあ…\n罪作りな、ボク♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008007, /*unit_id*/100831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ああ、本当にボクは美しい♪\nボクの美しさを表現するには…\nいや、言葉じゃ表現できないよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008008, /*unit_id*/100831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ボクほど美しくない、なんて\n恥ずかしがることじゃないよね\nだって改めて言うまでもなく\n当たり前じゃない、そんなの♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008009, /*unit_id*/100831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか、どんな宝石よりも\n価値のあるこのボクを…\nキミにプレゼントするって\n言ったら、キミは喜ぶかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008010, /*unit_id*/100831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このボクがここにいるという\nプレシャス…キミと\n二人で作っていくときめき…\n世界は希望に満ちているんだね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008011, /*unit_id*/100801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.9, /*change_face*/2, /*description*/"誕生日おめでとう♪\n今日のキミは輝いて見えるね\n綺麗で美麗で華麗で壮麗だ\nきっと世界で二番目に美しいよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008012, /*unit_id*/100831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー♪\n今日はボクの美しさを、\n独り占めにしてもいいよ♪\n全部受け止めきれるかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008013, /*unit_id*/100861, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ボクの美しさはもはや神秘の領域\n奇跡や魔法に等しいよ\nボクと同じ時代を生きてることを\n誉れに思ってよね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008014, /*unit_id*/100861, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"笑顔も泣き顔も、怒った顔だって\n究極にかわいいボク♪\nねえ、キミはどのボクが\n一番好きなのかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008015, /*unit_id*/100861, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もし美しさが罪になるのなら\nボクは世界一の大罪人に\nなっちゃうね！　ああ、自分の\n美しさが恐ろしいよ…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008016, /*unit_id*/100861, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ここまでボクについてこられる\nなんて、キミは想像以上だよ\nそんなにボクの隣がいいのかい？\nならこれからもいるといいよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1008017, /*unit_id*/100861, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"常に更新し続ける美の最高到達点\nそれを見続ける権利を\nキミにあげるよ！　ふふ、これで\nもうボクから離れられないね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009001, /*unit_id*/100901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"貴様も早く前世の記憶を\n取り戻すのだ！　我が宿敵にして\n盟友、閃光のシグルドよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009002, /*unit_id*/100901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"我が真名はちゃんと覚えたか？\nアンネローゼ・フォン・\nシュテッヒパルムだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009003, /*unit_id*/100901, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/5.0, /*change_face*/3, /*description*/"くっ…！　魔力を封じた\n「瞳」が疼く…！\n目薬？　いらんわっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009004, /*unit_id*/100901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"シグルドよ、現世においても\n我ら盟友の絆は不滅だ！\nはーっはっはっはっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009005, /*unit_id*/100901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/5, /*description*/"我ら二人で手を取り合い\n『機関』の野望を打ち砕こう！\nそしてその暁には、前世と同じく\nめ、夫婦の誓いを…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009006, /*unit_id*/100931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"その目に、魂に刻めっ…！\nこれぞ我が禁断の\n最上究極殲滅魔法だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009007, /*unit_id*/100931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴様も技を持っているなら\nかっこよく放つべきだ！\n例えばだな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
