INSERT INTO `unit_comments` VALUES (/*id*/1009009, /*unit_id*/100931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"防御を捨て、攻撃に特化した\n『撃滅形態』をとくと見よ！\n…ど、どこを見ているのだ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009010, /*unit_id*/100931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルドよ…貴様のおかげで\nいつも私は自分を貫けている\nだから…ありがとう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009011, /*unit_id*/100901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.8, /*change_face*/2, /*description*/"今日は貴様が転生した日か\n記念に私からこれを授けよう！\n見よ！　暗黒騎士の紋章を！\n…どうだ。かっこいいだろう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009012, /*unit_id*/100931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルドよ、誕生日おめでとう！\n私からは日記をプレゼントだ！\n我らの活動を書き残すがいい！\n…字は書ける、よな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009013, /*unit_id*/100961, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"慄くがいい！\n何人たりとも\n我が魔瞳の魔力には\n抗えはしない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009014, /*unit_id*/100961, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"さあ、始めようか！\n破壊と再生…\nあらたな神話を\n目に焼き付けるがいい！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009015, /*unit_id*/100961, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"世界が終末を迎えようとも…\n我らの絆は滅びはしない！\nそうだろう？　シグルドよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009016, /*unit_id*/100961, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シグルド、失われし我が半身よ…\nって、聞こえてたのか？\nいや、その…\nな、なんでもないってばぁ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1009017, /*unit_id*/100961, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"我が封印は完全に解かれた！\n魂の盟友と共にある限り、\n疾風の冥姫は止められん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010001, /*unit_id*/101001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"王子はん、王子はん\nおぶういかがどすかぁ？\nおぶういうんはお茶どす！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010002, /*unit_id*/101001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"女の子は夢の国で暮らすことに、\n夢を見るんどすなぁ\n運命の王子はんと夢の国～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010003, /*unit_id*/101001, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/5.5, /*change_face*/2, /*description*/"古今東西の絵本ではなぁ、\nお姫はんを守るのは、\n王子はんの役目なんよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010004, /*unit_id*/101001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"怪我したときは、\nうちを頼ってほしいわぁ\n特別な呪文があるんどす\n痛いの痛いの飛んでけ～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010005, /*unit_id*/101001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"朝、目を覚ましたら、\nまずは小鳥はんたちと、\nおはよーって挨拶するんどす～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010006, /*unit_id*/101031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『くるりんぱっ☆　』あららぁ、\nここ洞窟やってんなあ\nでもうちの魔法で明るうなったし\nそれにとってもきれいですやろ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010007, /*unit_id*/101031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え、手ぇ引いてくれはるん？　\nおおきに、嬉しおす…\nほなうちは王子はんの足元明るう\n照らして、一緒に歩きまひょ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1010008, /*unit_id*/101031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"王子はんの道筋はうちが\n照らします～\nせやから、王子はんは安心して\n一緒にいておくれやす～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
