INSERT INTO `unit_comments` VALUES (/*id*/1028016, /*unit_id*/102861, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたもだいぶここに\nなじんでるわよね\nこのままずっと\n居てくれてもいいのよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1028017, /*unit_id*/102861, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたの前でくらい、肩の力を\n抜いてもいいわよね\nママ・サレンはちょっとお休み\n…なんてね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029001, /*unit_id*/102901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"私、今のポジションには\n満足してないんだ！\nトップアイドルめざして\n歌も踊りも磨かないとね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029002, /*unit_id*/102901, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.0, /*change_face*/5, /*description*/"…私の写真集みてるの？　この\n派手な水着は撮影用だってば！\nプライベートで着るやつは\nもっと可愛いもん。…見たい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029003, /*unit_id*/102901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"トップアイドルへの道は\nまだまだ遠いなあ…\nダンスと歌のレッスン、\nもうちょっと頑張ろうっと！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029004, /*unit_id*/102901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.4, /*change_face*/2, /*description*/"歌も踊りも\nもっともっと上手になりたいな！\nキミには、一番輝いてる私を\n見てほしいから♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029005, /*unit_id*/102901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/2.3, /*change_face*/5, /*description*/"アイドルのお勉強は進んでる？\nでも…私以外のアイドルの\nことはあまり勉強しなくても\nいいからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029006, /*unit_id*/102931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"トップアイドルになれたら\n世界はどんな風に見えるんだろ？\nもしそうなった時も\nキミは隣にいてくれるかな…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029007, /*unit_id*/102931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ステージに立つからには\nチャレンジする気持ちを\n忘れたらダメだよね！\nこれからも前向きにいくよー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029008, /*unit_id*/102931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、がんばってトップアイドルの\n高みを目指すから！\n応援、よろしくね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029009, /*unit_id*/102931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが望むなら\nアイドルやめてもいいかなって\n思ったこともあったんだよ？\n…ちょっとだけね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029010, /*unit_id*/102931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この歌、片思いの女の子の心が\nよく出てるねって？\n…ふふふ、キミって鈍感♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029011, /*unit_id*/102901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/1.2, /*change_face*/1, /*description*/"おめでとう♪　キミの誕生日を\nお祝いできるように、\nずっと前からスケジュールを\n調整してたんだ！　うれしい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029012, /*unit_id*/102931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー、トゥユー♪\nハッピーバースデー、トゥユー♪\nどう？　キミだけのための\nプライベートライブだよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029013, /*unit_id*/102961, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今の私にできる最高の歌を\nキミに贈るよ。キミを想って\nキミのために歌うから…\nどうか最後まで聴いてね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029014, /*unit_id*/102961, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この歌には私の色んな想いを\n込めたんだ。え、どんな\n想いかって？　ふふ、それは秘密\nキミ自身で見つけてね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029015, /*unit_id*/102961, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなの応援がトップアイドルを\n目指す私の力になるんだ\nだからこれまで以上にいーっぱい\n応援してくれなきゃダメだよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029016, /*unit_id*/102961, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私がトップアイドルになっても\nキミには今まで通りで\nいてほしいな。そういうのが\n一番うれしいんだよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1029017, /*unit_id*/102961, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アイドルは恋愛禁止だけど\nいつかちゃんとキミに…ううん\nまずはトップアイドルになるんだ\n勇気を出すのはその後だよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030001, /*unit_id*/103001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"デケデケデン！\nこのニノン、ショーグンのために\n忠義を尽くすデース！\n見ててクダサイ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030002, /*unit_id*/103001, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/4.0, /*change_face*/2, /*description*/"一大事デス！\nアクダイカンが現れたデス！\n一緒に成敗するでゴザルデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030003, /*unit_id*/103001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"あ、ショーグン！\nそこでなにを…ハッ！？\nもしや忍法の修行デスか！？\nニノンもやるデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030004, /*unit_id*/103001, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"ショーグンのためなら、\nたとえ火の中、水の中デス！\nなぜなら…ワタシの命は、\nショーグンのものだからデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030005, /*unit_id*/103001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.5, /*change_face*/5, /*description*/"ヤマトナデシコ…\nニノンの国にも伝わる言葉デス\nワタシがそうなったら、\nショーグンもメロメロデスよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030006, /*unit_id*/103031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか…\nショーグンと共に\n天下統一するデス！\nそれがニノンの夢なのデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030007, /*unit_id*/103031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ニノンに切れぬものはないデス！\nショーグンはヤカタブネに\n乗ったつもりで\n安心していてクダサーイ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030008, /*unit_id*/103031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ニノンが、ショーグンのゾーリ\nふところで\nあっためておいたデス！\n一緒にでかけマショ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030009, /*unit_id*/103031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグン、手をつなぐデース！\nえへへ…\nこれでニノンとショーグンは、\n固い絆で結ばれたデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030010, /*unit_id*/103031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグン！　ご飯にするデス？\nお風呂にするデス？\nそれとも…ワ・タ・シ…？\nは、恥ずかしいデス…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030011, /*unit_id*/103001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/2, /*description*/"誕生日おめでとうデス！\nお祝いの紅白マンジェウを\n用意したデス！　二人羽織で\n食べさせてあげるデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030012, /*unit_id*/103031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンのお祝いするデス！\n誕生日は、おジャシキ遊びで\nシュラシュシュシュデース！\nどんちゃん騒ぎデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030013, /*unit_id*/103061, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"チューシン・ニノン、呼ばれて\n飛び出てシュバババッー！\n命燃やしてショーグンを\nお守りいたしマス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030014, /*unit_id*/103061, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シンリャクすること火の如くデス\nささ、ショーグン、ご命令を！\n最高のイクサバタラキを\nご披露いたソウロウデス！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030015, /*unit_id*/103061, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグン、見てクダサイ！\nメラメラ燃える究極忍法デス！\nこれでゾーリもホカホカに…\nアアー！　も、燃えてるデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030016, /*unit_id*/103061, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"シュババババッ！\nこの印さばき、どうデスか？\nえ、ゆ、指がきれい？　うぅ…\n不意打ちはズルいデ～ス…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1030017, /*unit_id*/103061, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンのおそばにいるだけで\nワタシは笑顔になりマス！\nそれこそがワタシの一番の\nゴホウビデース！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031001, /*unit_id*/103101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.5, /*change_face*/2, /*description*/"今日の運勢は最悪です\nえっと、気にしないでください\nちなみに…私の占いは、\nよく当たります", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031002, /*unit_id*/103101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.5, /*change_face*/4, /*description*/"あなたの後ろの人、\n誰ですか…？\nえ？　見えない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031003, /*unit_id*/103101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.0, /*change_face*/2, /*description*/"タロットカード、星占い、\n四柱推命…いろいろ\nありますが、私のおススメは、\n父を使った水晶占いです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031004, /*unit_id*/103101, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.0, /*change_face*/5, /*description*/"父はあなたを\n誤解しているようです\nとてもいい人なのに…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031005, /*unit_id*/103101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/5, /*description*/"今日のあなたには、\n女難の相がでていますね\nあまり私以外の女性とは、\n話をしない方が…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031006, /*unit_id*/103131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"舞がきれい…ですか\nうれしい\n修練した甲斐がありました", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031007, /*unit_id*/103131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これは祓魔の舞です\nまだ練習中なので\nあまり見ないでください\n…恥ずかしいですから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031008, /*unit_id*/103131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"父がこの舞を見るたびに\n成仏しそうだと褒めてくれます\n父に成仏されては\n困るのですが…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031009, /*unit_id*/103131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたに褒めてもらえると\nいつもより舞がうまくできます\nプラスの気は\n霊を祓うのに最適なんです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031010, /*unit_id*/103131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"生きている人で舞を見せるのは\nあなたが初めてです\n少し恥ずかしいですけど\n…あなたに見てほしいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031011, /*unit_id*/103101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/10.3, /*change_face*/5, /*description*/"あなたが歳を取って、\nおじいさんになったら、一緒に\n私もおばあさんになりますね\nもちろん、幽霊になったら…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031012, /*unit_id*/103131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n今日は、とてもいい運勢ですよ\n金運とか健康運とか…\nその…れ、恋愛運とか…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031013, /*unit_id*/103161, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたがそばにいれば\n何も恐れるものはありません\n降りかかる不安や恐怖は\n私が全て刈りとります", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031014, /*unit_id*/103161, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえ誰からも恐れられようとも\nあなたを守るためなら\n私はためらうことなく\nこの刃を振るいます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031015, /*unit_id*/103161, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キレイな鬼火…\n妖しくもはかなげで\nそして私たちの行く末を照らす\n希望の灯火なんです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031016, /*unit_id*/103161, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よければ力をお貸しください\nあなたとは共に助け合い\n守り合う仲でいたいですから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1031017, /*unit_id*/103161, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたのそばにいると\n胸が高鳴ってしまいます…\nこれは魂の共鳴？\nあるいはもっと別の…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032001, /*unit_id*/103201, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.0, /*change_face*/1, /*description*/"困ったことがあれば、\n何でも申してくださいな！\n私がなんとかして\n差し上げますわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032002, /*unit_id*/103201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.0, /*change_face*/1, /*description*/"ごきげんよう\n私が一人前の商人になるため、\nご指導ご鞭撻のほど\nよろしくお願いしますわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032003, /*unit_id*/103201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.5, /*change_face*/5, /*description*/"私は確かに方向音痴ですが、\nそこまで酷くありませんわ\n…ところで、私の家は\nどちらかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032004, /*unit_id*/103201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"あなたさまが私の騎士になる日を\n私はいつまでも待っていますわ\nその時には、二人で\n色々な場所に行きますわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032005, /*unit_id*/103201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"お金で買えないものも\nあるのですわね…\n例えば、あなたさまの\n心…ですわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032006, /*unit_id*/103231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『一人前の商人とは\n一人前の武人でもある！』\nウィスタリア家の教えを\n今こそ守りますわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032007, /*unit_id*/103231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この程度の相手、\n私の敵ではありません\n手早く片付けて、\n勝利の祝宴と参りますわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032008, /*unit_id*/103231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の商売を邪魔するやからは\nなんぴとたりとも許しません！\n成敗してさしあげます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032009, /*unit_id*/103231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたさまが\nそばにいてくださることが、\nどれほど私を強くしているか、\nお気づきかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032010, /*unit_id*/103231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたさまと一緒なら、\n私は迷わずに、\nどこまでもまっすぐ進みますわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032011, /*unit_id*/103201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"今日はあなたさまの誕生日？\nまあ、おめでとうございます！\nすぐにパーティーを\n開きましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032012, /*unit_id*/103231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日、おめでとうございます\nあなたさまの誕生日の\n価値の前では、どんな宝石も\nかすんで見えてしまいますわね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032013, /*unit_id*/103261, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おーほっほっほ！\n情熱の炎が燃え滾りますわ！\n今の私は完全無欠！　遅れずに\nついてきてくださいませ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032014, /*unit_id*/103261, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やはり私には赤が似合いますわ\nこの全てを焼き尽くす炎でさえ\n私にとっては艶やかに彩る装飾の\n一つでしかありませんわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032015, /*unit_id*/103261, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたさまがいてくれれば\n私は不死鳥の如く蘇りますわ！\nさあ、一気呵成に\n決めてしまいましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032016, /*unit_id*/103261, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたさまと一緒にいると\n知らない世界を知ることが\nできますわ。さあ、次は何を\n見せてくださるの？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032017, /*unit_id*/103261, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私が背中を任せられるのは\nあなたさまだけですわ\nそして肩を並べ共に歩むのも\n私はあなたさまがいいですわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033001, /*unit_id*/103301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"お笑いの道は奥が深けぇべ！\nオラと一緒に、\n笑いの道、極めるべ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033002, /*unit_id*/103301, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/4.0, /*change_face*/4, /*description*/"オラが一番おっかねぇのは、\n敵に負けることじゃねえ\nネタがすべることだ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033003, /*unit_id*/103301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.0, /*change_face*/1, /*description*/"あんちゃん、この牛乳ウマいべ？\nお乳を出したのは、ウマじゃなくて\n牛だけどな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033004, /*unit_id*/103301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"あんちゃん、オラと一緒に、\nお笑い界の頂点めざすべ！\nめざせ、お笑いチャンピオン！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033005, /*unit_id*/103301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"あんちゃん、オラと一緒に\nネタ、作るべ！\nもう二時間だけ「寝た」後に…\nなんちゃって！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033006, /*unit_id*/103331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ほーい、集まってけれ～！\nエリザベスパーク印の\n牛乳、ヨーグルト、チーズ！\n直売だべよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033007, /*unit_id*/103331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"街の衆、味見してけろ！\n出来立てだぁ、ほんとに旨ぇぞ～\nオラ嘘はつかねえよ、\n冗談は言うけどな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033008, /*unit_id*/103331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"街のみんながこんなに\n応援してくれてんだ！\n絶対に爆笑をかっさらうべよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033009, /*unit_id*/103331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オラのギャグ、おもしろいべか？\nなんつってもあんちゃんは、\nオラの初めてのファンだ\nきっとオラたち波長が合うだよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033010, /*unit_id*/103331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"な、なあ…あんちゃんのこと\n相方って呼んでもいいべか？\nそれがダメなら婿さんに…\nあ、逆だ！　こっぱずかしー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033011, /*unit_id*/103301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"ひょうたん、常備してっか？\nなにキョトンとしてんだべ？\n今日誕生日のあんちゃんのための\nギャグだべ！　おめでとう～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033012, /*unit_id*/103331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデーだべ！\n今日はあんちゃんのためだけに\n渾身ネタを、飛ばーすでー！\nいっぱい笑ってほしいべよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033013, /*unit_id*/103361, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オラの手料理をあんちゃんに\n食べてもらうの、なんかドキドキ\nするべな…味には自信あっから\n楽しみにしててけろ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033014, /*unit_id*/103361, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんちゃんと二人でキャンプ\nなんてのもいいかもしんねぇな\n朝までみっちりネタ合わせ…\n最高の時間になりそうだべ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033015, /*unit_id*/103361, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"牧場がこんなににぎやかに\nなるなんて…それもこれも\nあんちゃんが励ましてくれた\nおかげだべ。あんがとな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033016, /*unit_id*/103361, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなの笑顔が行商人としても\n牧場主としても一番の幸せだべ\nこの景色をずっと、あんちゃんと\n一緒に見ていきてぇなぁ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033017, /*unit_id*/103361, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オラの相方はエリザベスだけって\n思ってきたけど、あんちゃんも\n加えてトリオってのもいいなぁ…\nまじめに考えてくれねぇべか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034001, /*unit_id*/103401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"何か困ったことがあったら\n相談してね\nいつでも力になるから\nお姉さんとの約束よ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034002, /*unit_id*/103401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.5, /*change_face*/2, /*description*/"キミ、いま暇だったりしない？\nだったら私と一緒に、\nどこか飲みにいきましょ？\nもちろんキミはジュースで、ね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034003, /*unit_id*/103401, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.0, /*change_face*/5, /*description*/"仕事持って帰ってきたけど\n終わりそうにないよ～！\nうぅ…キミ、\n少し手伝ってくれない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034004, /*unit_id*/103401, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/7.5, /*change_face*/2, /*description*/"えへへ～…\nキミって暖かいんらね～\nちょっと飲みすぎらから、\nキミで暖を取らせて～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034005, /*unit_id*/103401, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"キミ、ご飯はちゃんと食べてる？\nあ、良かったらお姉さんが、\n作ってあげようか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034006, /*unit_id*/103431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はぁ…このベリー、いい香り♪\n飲み物にしたらさぞ美味しい…\nいけないいけない！\n仕事中なのに！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034007, /*unit_id*/103431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"だめよユカリ…！\nこのままじゃいつもみたいに\nダメなところを見せちゃうわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034008, /*unit_id*/103431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"な～んか、ちょっとだけ\nいい気分になってきたかも…\n気のせい…かなぁ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034009, /*unit_id*/103431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが大人になった時のために\n飲み物、一本作っておこうか？\nその日がきたら、二人で一緒に\nお祝いしながら飲みましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034010, /*unit_id*/103431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇ…今度、一緒に\nデートしない？\nそれとも、私みたいなのが\n相手じゃ、いやかな…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034011, /*unit_id*/103401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.0, /*change_face*/1, /*description*/"誕生日おめでとう！\n今日はいっぱい\nお祝いしてあげるね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034012, /*unit_id*/103431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミの誕生日というわけで\nまずは…かんぱーい！\nねえ、今日はずっと\n一緒にいてもいいかな…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034013, /*unit_id*/103461, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"んふふ～気持ちいい～♪\nほら、キミも飲も飲も！\nもちろんキミはジュースだからね！\nはい、かんぱーいっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034014, /*unit_id*/103461, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あぁ～染み渡るわぁ～\n仕事の後はこれに限るわよね～！\nそうだキミ、肩揉んでくれない？\nんっ、あ～っ、上手ね、キミ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034015, /*unit_id*/103461, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はお姉さんのおごりよぉ\nたくさん食べていいわよぉ～\n本当は手料理とかご馳走して\nあげたいんだけど…今度ね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034016, /*unit_id*/103461, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私がこんな風にリラックス\nできるのはキミの前だけよ～\n迷惑かけちゃうかもしれないけど\nキミは許してくれるわよね…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1034017, /*unit_id*/103461, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よぉ～っし言っちゃおうかな～！\n私ねぇ～、キミのことが～…！\nふふ、や～っぱ秘密～！\n…あ、危なかったわ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036001, /*unit_id*/103601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.9, /*change_face*/5, /*description*/"今は、勉強の時間です\n話しかけないでくださ…\nもう、そんな顔しないでください\n遊んであげますから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036002, /*unit_id*/103601, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/4.8, /*change_face*/4, /*description*/"ピーマンとにんじんを食べないと\n大きくなれない…？\nううっ…大きくはなりたい\nですけれどぉ～っ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036003, /*unit_id*/103601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"聞いてください\n身長が伸びていました！\n一ミリも！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036004, /*unit_id*/103601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.2, /*change_face*/5, /*description*/"今は子供ですけど、\nすぐに大きくなりますから\nその時には…その…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036005, /*unit_id*/103601, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ふふっ。とても頑張りましたね\n今日はほめてあげます\n頭をナデナデしてあげましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036006, /*unit_id*/103631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私を守ってあげる？　\n余計なお世話です！\nで、ですが…\nありがとうございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036007, /*unit_id*/103631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こ、腰が抜けちゃいました…\nは、恥ずかしい…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036008, /*unit_id*/103631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うぅ…どうして\nこの人がいる時に限って、\nかっこ悪くなっちゃうのかな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036009, /*unit_id*/103631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ま、魔物なんてこわくないです！\nで、でもその…守ってくれて\n助かりました…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036010, /*unit_id*/103631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"街の外は魔物が出て危ないです\nですから、私が守ってあげますよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036011, /*unit_id*/103601, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/3.0, /*change_face*/2, /*description*/"今日、お誕生日なんですよね\nおこづかいを貯めて、\nカップケーキを買ってきたので、\n召し上がってください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036012, /*unit_id*/103631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nどんどん大人になっちゃう…\n私も早く追いつかないと…\nな、なんでもないです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1036013, /*unit_id*/103661, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、もう子供じゃないんですから\nひとりでも戦えますっ！\n今度は私があなたを守れるように\nなるんですからっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
