INSERT INTO `unit_comments` VALUES (/*id*/1037001, /*unit_id*/103701, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/8.2, /*change_face*/1, /*description*/"おや、何をしてるのかな？\n不審な行動をしたら、\nあなたでも逮捕するよ？\nなんて、冗談だけど♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1037002, /*unit_id*/103701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.6, /*change_face*/2, /*description*/"稽古に付き合ってほしいな\n大丈夫大丈夫！\nきちんと手加減するから！\nふふ、多分だけどね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1037003, /*unit_id*/103701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.6, /*change_face*/2, /*description*/"さて、今日も町の見回りに\n行かないと。あなたも来る？\n何があっても、私が守って\nあげるから安心していいよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1037004, /*unit_id*/103701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.4, /*change_face*/4, /*description*/"うーん、自分では器用な方だと\n思ってたんだけどなぁ…\nあなたの前だと上手くいかない\n…どうしちゃったのかなぁ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1037005, /*unit_id*/103701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/9.1, /*change_face*/5, /*description*/"からかうつもりなのに、あなたには\nいつも逆にやられちゃう\nまったく、調子が狂って困る\nほんと…困るよ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1037006, /*unit_id*/103731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"相手の動きを見て、正確に急所へ\n必殺の一撃を叩きこむ！\nそれが剣術の基本だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
