INSERT INTO `unit_comments` VALUES (/*id*/1038015, /*unit_id*/103861, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すぅ…はぁ…すぅ…はぁ…\n呼吸を合わすって不思議ですね\nあなたと一つになったみたいで、\n力が湧いてきます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
