INSERT INTO `unit_comments` VALUES (/*id*/1040003, /*unit_id*/104001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今日も、お花に水あげて、\n動物さんにご飯をあげて～♪\nえへへ、ぼっちでも、\n楽しみはそれなりにあります", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
