INSERT INTO `unit_comments` VALUES (/*id*/1040005, /*unit_id*/104001, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/3.5, /*change_face*/6, /*description*/"あの人は、私のことを…はっ、\nいつからそこに！？　ええと、\nこれは、花占いです。ど、どんな\n占いかって…内緒ですっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040006, /*unit_id*/104031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いまは微力な私だけど、きっと\nみなさんのお役に立ちます\nそうすれば…いつか友達、\n増えるかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040007, /*unit_id*/104031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぼっちだとしても…\n私にもきっとできることが\nあるはずだって思ったんです\n精一杯がんばりますね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040008, /*unit_id*/104031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"守りたいものを守る…\nそのためにがんばります\nＢＢ団は私に、色々なことを\n教えてくれました！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040009, /*unit_id*/104031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ＢＢ団の活動のたびに\nあなたに会えるのが楽しみで…\n毎日、早く明日がこないかなって\n思っちゃいます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040010, /*unit_id*/104031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ…私にこの先、\nどれだけ友達が増えても、\n一番はあなただから…\n安心してくださいねっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040011, /*unit_id*/104001, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/9.2, /*change_face*/2, /*description*/"お、おたたん、おたんこなすー！\nすみません、ハッピーなお祝いの\n言葉を口にしたことがなくて…\n誕生日おめでとう五郎丸ー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040012, /*unit_id*/104031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n森にいる小鳥さんたちと\n合唱団を結成して、\nお祝いの歌を作ってきました！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040013, /*unit_id*/104061, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ＢＢ団の絆の強さ、\nこの一撃で証明します！\n見ていてくださいね、団長さん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040014, /*unit_id*/104061, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"困っている友達を助けるのは\n当然！　ですよね、団長さん！\nあなたがピンチの時には\n誰より早く駆け付けますからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040015, /*unit_id*/104061, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"団長さんと一緒なら、\nどんな困難でも乗り越えられる\n気がします。だって私たち、\n友達ですからっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040016, /*unit_id*/104061, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もしこの先、私に友達がたくさん\nできたとしても、団長さんは私の\n特別な人です。だってあなたは\n私の最初の友達なんですから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1040017, /*unit_id*/104061, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"時々、団長さんと一緒にいると\nなんだかドキドキしちゃいます\nこの気持ちは…友情が深まった\n証なのでしょうか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042001, /*unit_id*/104201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"私の唱喚術は、人々を\n守るためのもの…この力を、\nもっと役立てたいんです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042002, /*unit_id*/104201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"あの…良かったらまた、\nアイドルの練習に\n付き合ってもらえませんか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042003, /*unit_id*/104201, /*use_type*/1, /*voice_id*/3, /*face_id*/7, /*change_time*/3.5, /*change_face*/6, /*description*/"～～…♪\nい、いつからそこに！？\n…今の、聞いてました？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042004, /*unit_id*/104201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.0, /*change_face*/5, /*description*/"あなたの前だと、\n緊張せずに歌えるんです…\nどうしてでしょう…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042005, /*unit_id*/104201, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/"歌にはいろんな可能性がある…\nそれを教えてくれたのは仲間と\n…あなたです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042006, /*unit_id*/104231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…もう少しだけ、近くに…\nそばにいるだけで、不思議と\nメロディが浮かんでくるんです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042007, /*unit_id*/104231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"目が覚めました？　向こうの街で\nお祭りが始まりましたよ\n後で一緒に行ってみませんか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042008, /*unit_id*/104231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…書きとめられないくらい\n心の中から言葉が溢れてくる…\n私、こんなに歌いたかったんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042009, /*unit_id*/104231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか…聞いてくださいね\nあなたへの思いを綴った歌を…\n恥ずかしがらずに、歌いますから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042010, /*unit_id*/104231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"波の音が心地いいですね\nずっと一緒に聞いていたいくらい\n…私、今なにか言いました？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042011, /*unit_id*/104201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.6, /*change_face*/5, /*description*/"お誕生日おめでとうございます\n歌以外にいいプレゼントが\n思いつかなくて…\nよかったら聞いてください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042012, /*unit_id*/104231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n短いものですが、お祝いに\n曲を書いてみました\nここで歌ってもいいですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042013, /*unit_id*/104261, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたと共に歩んだ日々が\n作り上げたこの歌を\nあなたに聞いてもらえて…\n私、とても幸せです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042014, /*unit_id*/104261, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"自分で言うのもなんですが\nこの歌は特に自信作なんです\nあなたの心に響かせてみせます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042015, /*unit_id*/104261, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"歌っていると心が軽くなって\nつい体が動いてしまうんです\nよければあなたもどうですか？\nさあ、お手をどうぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042016, /*unit_id*/104261, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつかあなたにこの思いが\n届くと信じて歌詞を綴り…\nそして歌い続けることを\nここに誓います", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1042017, /*unit_id*/104261, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたが望むなら唱喚士でも\nアイドルでもない\nあなただけの歌い手として\nいつでも曲を捧げます…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043001, /*unit_id*/104301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.3, /*change_face*/2, /*description*/"さて、パトロールにでも行くか\n一緒に行きたいなら、\nついてきてもいいぜ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043002, /*unit_id*/104301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"おい、飯はちゃんと\n食ってんだろうな？\n食ってないなら、\nあたしが用意してやるよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043003, /*unit_id*/104301, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.2, /*change_face*/5, /*description*/"あたしみたいな奴、\n普通の女の子扱いするのなんて、\nおまえぐらいだぜ\nわ、わりとうれしいな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043004, /*unit_id*/104301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.6, /*change_face*/2, /*description*/"あたしの親友の恩人なら、\nあたしの恩人みたいなもんだ\nお互い仲良くしていこうぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043005, /*unit_id*/104301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.1, /*change_face*/5, /*description*/"お前って、けっこう男気もあるし\n肝っ玉もすわってるよな…\n一緒にいて、あたしは楽しいよ\nお前はどうだ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043006, /*unit_id*/104331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしから離れんなよな！\nどんなやつが来ようが、\nぶった切ってやる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043007, /*unit_id*/104331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弱い奴らばっかで、\n暴れ足りねぇ\n歯ごたえのある奴は、\nいねぇのか！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043008, /*unit_id*/104331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前と一緒なら、\nどんな敵も楽勝だぜ！\nお前はあたしが守るからよ、\nだからあたしのことも、頼むぜ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043009, /*unit_id*/104331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よっしゃー！\n敵が全部片付いたら、\n二人でパーッと飯だ飯！\nお前の好物たくさん作ってやる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043010, /*unit_id*/104331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力がみなぎってくるぜぇ！\nあたしから離れんなよ！\n何があっても絶対に\nお前を守ってやんよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043011, /*unit_id*/104301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.2, /*change_face*/2, /*description*/"へぇ、今日が誕生日だったのか\nおめでとさん！\nんじゃ、せっかくだし\nなんかおごってやるよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043012, /*unit_id*/104331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日ってお前の誕生日だよな？\nぐ、偶然、たまたま作った\nケーキがあるんだけど…\n食わねぇか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043013, /*unit_id*/104361, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしの本能が目覚めちまったぜ\n…こっから先は手加減なしだ\nこの研ぎ澄まされた爪と牙で\n刻みつけてやる…あたしの力を！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043014, /*unit_id*/104361, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なぁ…その、大丈夫だよな？\n全力のあたしを見て…\n引いたりしてないよな？\nっ！　そっか、よかった…へへ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043015, /*unit_id*/104361, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力がありあまってるからか\n体がうずきやがる…えっ？\nおまえが相手をしてくれる？\nいいね、じゃあぶつかりあうか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043016, /*unit_id*/104361, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"へへっ、すげぇ力だろ？\nおまえのために強くなったんだぜ\nだから…その、なんだ\n…あたしから目をそらすなよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1043017, /*unit_id*/104361, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしがおまえを守る\nおまえがあたしを守る\nそういう関係が続けばいいよな\nずっと…ず～っと、なっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044001, /*unit_id*/104401, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.0, /*change_face*/1, /*description*/"お主…わらわと何処かで\n会ったことはないか？\nお主を見ていると、何故か\nなつかしさを覚えるのじゃが…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044002, /*unit_id*/104401, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/6.4, /*change_face*/1, /*description*/"むう…どういうわけか\n無性にトマトジュースが\n飲みたい気分じゃの…\nお主、どこぞで求めてまいれ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044003, /*unit_id*/104401, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.9, /*change_face*/2, /*description*/"昼間は思うように動けぬ…\n夜になるまで、じっとしておこう\nそれまでお主は、わらわの\n話し相手になるのじゃぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044004, /*unit_id*/104401, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.2, /*change_face*/4, /*description*/"お主はちっとも、わらわを\n恐れないのじゃな？\nな？　可愛らしいじゃと？\n眷属の分際で何を申すー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044005, /*unit_id*/104401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/9.0, /*change_face*/5, /*description*/"お主は、特別な眷属じゃ\n今より「昼を総べる者」を\n名乗るが良いぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044006, /*unit_id*/104431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわの真の姿は美しかろう？\nなに？　見慣れてないから\nいつもの方が良い？　なんと\n張り合いのないことを…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044007, /*unit_id*/104431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわの野望を聞きたいのか？\nもちろん世界征服じゃ！\n女と生まれたからには\n一度は目指したいものよの♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044008, /*unit_id*/104431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわは伝説の吸血鬼\nお主の命を奪うなど\nたやすいことじゃが…まあ、\n眷属ゆえ生かしておいてやろうぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044009, /*unit_id*/104431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"世界を征服した暁には\nその半分をお主に分け与えよう\nど、どういう意味かわかるな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044010, /*unit_id*/104431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"昼間にお主と会うために、\n最近は昼夜逆転してしまった…\n昼ふかしは、美容によくないの\nじゃが…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044011, /*unit_id*/104401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/12.9, /*change_face*/6, /*description*/"今日は誕生日か？　めでたいな！\nいくつになったのじゃ？\nそうか。わらわがその歳の頃は…\nはて？　何をしていたかのう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044012, /*unit_id*/104431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"眷属の誕生日を祝して、\nわらわの膝枕を贈呈しようぞ\nどうじゃ、心地よいじゃろう？\n腕枕でも胸枕でも良いのじゃぞ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044013, /*unit_id*/104461, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そうじゃ、この威厳ある姿こそ\nわらわの本当の姿\nお主だけは特別じゃ\n存分に見惚れるがよいぞ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044014, /*unit_id*/104461, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわこそが夜を統べる者\nさあ、夜の散歩へゆくぞ\nついてくるがよい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044015, /*unit_id*/104461, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はーっはっはっは！　見よ！\n月までもがわらわたちを\n祝福しておるようじゃ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044016, /*unit_id*/104461, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"くっくっく……\nこれぞわらわの真の力じゃ\n眷属として誇りに思うがよい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1044017, /*unit_id*/104461, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんじゃ？\nそのように見つめられると\nうっかり魅了して\nしまいそうじゃのう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045001, /*unit_id*/104501, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/4.0, /*change_face*/2, /*description*/"ぐふふふ…\nえ？　な、何でもないですよ？\nあなたを見て、妄想なんて…\nはぁはぁ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045002, /*unit_id*/104501, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/5.0, /*change_face*/6, /*description*/"「ドＳさんは、クウカを\n追いつめるとムリヤリ…」\nでゅふふふふ…あっ！\nいいいつの間に居たのですか！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045003, /*unit_id*/104501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.3, /*change_face*/6, /*description*/"この間読んだ本が、\nとっても素敵で…え？\n内容ですか？　そ、それは…\n聞かないほうがいいですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045004, /*unit_id*/104501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.0, /*change_face*/5, /*description*/"夢の中にまでドＳさんが\n出てきてくれました…\n寝ても覚めても、クウカは\n支配され続けてしまうのですね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045005, /*unit_id*/104501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.9, /*change_face*/5, /*description*/"ドＳさんと二人っきり…\nこ、これは…！？\nクウカはどんな辱めを\n受けてしまうのでしょう…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045006, /*unit_id*/104531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな汚れたクウカを見る\nドSさんの冷ややかな視線\n…ふひっ、ふひひ！\nああ、また昂ぶってしまいます！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045007, /*unit_id*/104531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな醜態をさらしながらも\nこの後さらにドSさんに\nめちゃくちゃにされることを\nクウカは望んでいますぅ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045008, /*unit_id*/104531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"「魔物どもにこんなにされて\n嬉しいんだろええおい！」なんて\n言葉責めするつもりですね！？\nさすがはドＳさん、上級者…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045009, /*unit_id*/104531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もうお嫁にいけません…\nだから、一生ドSさんの\n支配下に置いてください！\nもっと体に教え込んでください！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045010, /*unit_id*/104531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"クウカはもうドＳさんなしでは\n生きられない体に…\nこ、この責任はちゃんと\n取ってくれるのですよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045011, /*unit_id*/104501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.5, /*change_face*/2, /*description*/"誕生日おめでとうございます\nケーキに深く刺さるロウソクが、\nとろっとろに溶けていますよ\nフゥー…って消してください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045012, /*unit_id*/104531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドＳさんの誕生日ですね…\nクウカの体に巻いたリボンを\n乱暴にほどいて、包装紙を破る\n子供のように服も…ぐふふふふ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045013, /*unit_id*/104561, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"気持ちいいです～っ♪\nクウカの体を昂らせるこの力…\n全て、ドＳさんに捧げます！\nどうぞお好きなように…ぐふふ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045014, /*unit_id*/104561, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はっ！？　ドＳさんの目が\nクウカに告げています…！\n「どこが成長したか見せてみろ」\n分かりました…み、見てください", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045015, /*unit_id*/104561, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドＳさんの激しい攻めを\n受け止めるためなら、クウカは\nどこまでも強くなれるんですっ！\nねっ、ドＳさん♪　…じゅるり♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045016, /*unit_id*/104561, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドＳさんとクウカは以心伝心…\n言葉がなくとも通じ合っています\n今は…クウカを弄びたいのですね\n…違うのですか！？　そんな～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045017, /*unit_id*/104561, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドＳさん…いえ、あなたといると\nクウカはいつも心地良くて…\n胸の奥が、温かくなるのです\nだからいつまでも…ふふっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046001, /*unit_id*/104601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"野良猫たちに、\nたい焼きを買ってあげてるにゃ！\nみんな喜んでくれるんだにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046002, /*unit_id*/104601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.3, /*change_face*/2, /*description*/"早く準備して出かけるにゃ！\n「時はルピなり」だにゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046003, /*unit_id*/104601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"お客さ～ん♪\nここだけのオイシイ話が\nあるんだにゃ～\n一口乗ってみないかにゃ～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046004, /*unit_id*/104601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/4.9, /*change_face*/2, /*description*/"あたしは気まぐれだから、\nちゃーんとつかまえて\nほしいにゃ？\nでないと逃げちゃうにゃ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046005, /*unit_id*/104601, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/6.0, /*change_face*/2, /*description*/"ふふふ\nキミが怪我したら…\nぺろぺろ舐めてあげにゃくも…\nないかにゃ～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046006, /*unit_id*/104631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これでも、困ってる人がいたら\n助けずにはいられないのにゃ！\n今夜は正義のため、猫と一緒に\n飛び回るのにゃー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046007, /*unit_id*/104631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今度、ギルドにも\n遊びに来るといいにゃ\nにゃふふ、これで人手が増え…\n…にゃ、にゃんでもないにゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046008, /*unit_id*/104631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしは気まぐれだけど、\nやる時はやる猫なのにゃ\n今日はやるかにゃ？\nだったら…全力にゃ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046009, /*unit_id*/104631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ。キミといられるなら…\n家猫になっちゃっても\nいいかにゃ～\nにゃんてにゃ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046010, /*unit_id*/104631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あとで、キミと二人で\n食べようと思って\nたい焼きを取ってあるのにゃ\n半分こして食べようにゃ～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046011, /*unit_id*/104601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"今日はキミの誕生日みたいにゃ！\nだから今日だけ特別に\nたい焼き食べ放題に\nしてあげるにゃ～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046012, /*unit_id*/104631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日、おめでとうにゃ！\n気まぐれなあたしでも、\n今日ぐらいは…キミだけの\n猫になってあげてもいいにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046013, /*unit_id*/104661, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミとあたしがそろえば、どこに\nお宝が隠されてようと楽勝にゃ！\nファントムキャッツに\n不可能はないのにゃ～♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046014, /*unit_id*/104661, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"す、すっごいお宝にゃ…！\nこんなにいっぱいあるなら、\nちょっとくらいネコババしても…\nな、なんでもないですにゃ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046015, /*unit_id*/104661, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このお宝があれば、\n苦しんでる人たちを\nいっぱい助けられるにゃ～♪\nさっそく一緒に配りに行くにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046016, /*unit_id*/104661, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが支えてくれるから、\nあたしはファントムキャッツで\nいられるにゃ…いつも\n一緒に居てくれてありがとにゃ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1046017, /*unit_id*/104661, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"い、いつもは猫とあたしでお宝を\n頂戴してるけど、誰かと一緒も\n悪くにゃいというか…その、\nまた一緒に来てくれるかにゃ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047001, /*unit_id*/104701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こう見えて私は女なんだ\nわけあって鎧は外せないが\nよろしく頼むよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047002, /*unit_id*/104701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"鎧に太陽光を浴びせると\n表面が恐ろしく熱くなるが\n中の私は意外と平気なんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047003, /*unit_id*/104701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"体を動かすのは好きだが\n水泳は苦手なんだ\n鎧が重くてね…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047004, /*unit_id*/104701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"顔を見せられない分\n今なにをどう思っているか\n伝えるよう努力するよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047005, /*unit_id*/104701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の表情は見えないかも\nしれないが、君の表情は\nしっかり見えているよ、いつもね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047006, /*unit_id*/104731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この剣を振らずに済む\n世の中にするためにも、\n私は剣を振らねばならないんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047007, /*unit_id*/104731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな強敵が相手であろうと\n私は恐れずに立ち向かうよ\n子供たちの笑顔のために", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047008, /*unit_id*/104731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえ炎に包まれた戦場でも\n助けを呼ぶ声がある限り\n私はその地へと赴こう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047009, /*unit_id*/104731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私が戦っている時\n君の視線を背中に感じると\n心が安らぐんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047010, /*unit_id*/104731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は団長として人々の安息は\n守らねばならないが、君だけは\n優先して守ってしまいそうだ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047011, /*unit_id*/104701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は君の誕生日なんだってね\n一つ大人になったんだから、今年は\nあまり無茶をしないでくれよ？\n君のことが心配なんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047012, /*unit_id*/104731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！　何か\nお願い事はないかい？　そ、それは\nダメだ少年！　兜を脱ぐなんて…\n恥ずかしくて爆発してしまうよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047013, /*unit_id*/104761, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君がそばにいるから…\n限界のその先に手が届く\nこの力は私だけのものじゃない\n君と私…二人の力だ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047014, /*unit_id*/104761, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなが何事もなく\n平和な日常を送れたらうれしい\nそのために私は剣を振るい\n戦っているんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047015, /*unit_id*/104761, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦いで臆病になって退くな\n私たちの後ろには\n守るべき者がいると思え\n安心しろ、私がついている", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047016, /*unit_id*/104761, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君はまた何かに巻き込まれたのか\n…どうして分かるのかって？\nふふっ、顔を見ればすぐに分かる\n私で良ければ力になろう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1047017, /*unit_id*/104761, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君は私を特別扱いしない\nだから、一緒にいると\nとても居心地が良いんだ\nこれからも変わらずに頼むよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048001, /*unit_id*/104801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/"素材を稼ぐか、ルピを稼ぐか…\nどっちの戦略も練ってあるから\n必要なら相談してね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048002, /*unit_id*/104801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.9, /*change_face*/2, /*description*/"今回用意した計画もバッチリよ\nもし街の外に行くのなら\n早めに出かけましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048003, /*unit_id*/104801, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"効率的な動きに大事なのは、\n観察する力よ\n普段からちゃんと鍛えてる？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048004, /*unit_id*/104801, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.1, /*change_face*/5, /*description*/"かけ持ちしすぎちゃったから\nバイトを手伝ってくれない？\nほら、キミなら頼りになるし", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048005, /*unit_id*/104801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.8, /*change_face*/2, /*description*/"キミのスケジュール管理、\n私がしてあげようか？\n24時間つきっきりで\nもちろん管理費はいただくけど", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048006, /*unit_id*/104831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よければキミのお財布、\n私が管理してあげよっか？\nえっ？　盗らないわよっ！\n見損なわないでっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048007, /*unit_id*/104831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この町に泥棒が現れたの！？\nまじめに働いてる人への\n冒涜じゃない！\nゆ、許せないわっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048008, /*unit_id*/104831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は特売セールの日！\nもちろんキミも、一緒にきてね！\n油断したら命に関わるわよ！\nそれじゃあ…とっつげき～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048009, /*unit_id*/104831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あの…キミって、\n好きなタイプっているのかしら？\nえ？　ちょ、ちょっと気になった\nだけよ。それだけだってばっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048010, /*unit_id*/104831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミを傷つけるやつがいたら…\nこうしてやっつけてあげるわ！\n私の大切な弟子だもの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048011, /*unit_id*/104801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ケーキの準備よし！\nプレゼントもよし！\nさあ、効率的な\n誕生日パーティーを始めるわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048012, /*unit_id*/104831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう\nお祝いの言葉なんて\n非効率的かもしれないけど…\nキミにはちゃんと言いたかったの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048013, /*unit_id*/104861, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミの力と私の力が合わされば、\n相手が誰であろうと\n負けるはずがないわ！\n最大効率で一気に片付けるわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048014, /*unit_id*/104861, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"攻撃に大事なのは、１に効率、\n２に効率！　３も４も５も効率！\n究極の効率を体現したこの技で、\n纏めてぶっ飛ばしてやるわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048015, /*unit_id*/104861, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな討伐依頼は\nさっさと終わらせて、\nバーゲンセールに行くわよ！\n…もちろん、キミと二人でね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048016, /*unit_id*/104861, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"敵に包囲されたか…\n前の敵は私がやるから、\nキミは後ろから援護をお願い！\n…私の背中、預けたからね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1048017, /*unit_id*/104861, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この力…キミの優しい心が、\n私に力を与えてくれてるのね…\nありがとう…私、キミのためなら\nどこまでも強くなってみせるわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049001, /*unit_id*/104901, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/4.8, /*change_face*/2, /*description*/"なんでもかんでもどんなことでも\nお姉ちゃんに言ってね！\nもっともっと頼って甘えて～\n遠慮しないでいいんだぞっ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049002, /*unit_id*/104901, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/3.2, /*change_face*/2, /*description*/"いつでもお姉ちゃんに\n甘えてくれていいんだぞっ☆\nナデナデだって、ギューだって、\nいっぱいしてあげちゃうからね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049003, /*unit_id*/104901, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/4.3, /*change_face*/2, /*description*/"お姉ちゃん、うれしくって、\nついチョップが出ちゃいそう～\nこれはお姉ちゃんの愛情だから、\nしっかり受け止めてね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049004, /*unit_id*/104901, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/2.8, /*change_face*/2, /*description*/"色んなこと、全部やってあげる♪\nだって、弟くんのお世話をするのは\nお姉ちゃんの役目だもん♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049005, /*unit_id*/104901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.1, /*change_face*/2, /*description*/"なになに？　用事？\nいつでも、どこでも、\nどんなことでも、\nお姉ちゃん、大歓迎だよ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049006, /*unit_id*/104931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"可愛い弟くんを困らせる人は、\nお姉ちゃんが\nぜーんぶ成敗してあげる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049007, /*unit_id*/104931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんなことがあっても、\n弟くんはお姉ちゃんが\n絶対守るからね！\n弟くんには指一本触れさせない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049008, /*unit_id*/104931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お姉ちゃんは、弟くんを\n守るために強くなったんだもん\nお姉ちゃんは、弟くんの盾\n弟くんの剣。弟くんの鎧だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049009, /*unit_id*/104931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんとのひとときを邪魔する\n野暮な奴は許さないよ！\nチョップくらいじゃ\n済まさないんだから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049010, /*unit_id*/104931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんをいじめるなんて、\n神さまが認めても、\n天が見逃しても、\nお姉ちゃんが許さない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049011, /*unit_id*/104901, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/7.9, /*change_face*/2, /*description*/"キミが一つ歳をとっても、\nお姉ちゃんも歳をとってるから、\n私はずっと弟くんのお姉ちゃんの\nまんまだよ♪　安心した？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049012, /*unit_id*/104931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとう！\n弟くんがどれだけ大人になったか\nお姉ちゃんに、しっかり見せて！\nじーーーーーーーーーーーーっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049013, /*unit_id*/104961, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんっ、お姉ちゃんだよっ☆\nえへへ、ビックリした？\nじゃあ今度は驚かないように\n耳元で優しく…お姉ちゃんだよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049014, /*unit_id*/104961, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お姉ちゃん、強くなっちゃった！\nこの力で弟くんを守っちゃうね♪\nそのためには～、もっとも～っと\n一緒にいなくちゃね？　ねっ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049015, /*unit_id*/104961, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇ弟くん、お姉ちゃんの悩みを\n聞いてくれるかなぁ？　あのね、\nもっと弟くんに甘えてほしいんだ\n…どうしたらいいと思う？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049016, /*unit_id*/104961, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぎゅう～っ♪\nお姉ちゃんハグ、温かいでしょ？\nあっ…ちょっと離れてもいい…？\n…ドキドキ、伝わっちゃったかな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1049017, /*unit_id*/104961, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぜ～んぶお姉ちゃんに任せてね♪\nん？　全部ってどれくらいって？\n弟くんが望むことすべて、だよっ\n…私の幸せは、キミの幸せだもん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050001, /*unit_id*/105001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"ちょっとこの雑誌見てー！\n右の服と左の服、\nどっちがアタシに似合うと思う？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050002, /*unit_id*/105001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.9, /*change_face*/6, /*description*/"ね、ちょっと聞いていい？\nアンタって３桁の掛け算とかも\n出来たりするわけ？\nま、まさか割り算とかも…！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050003, /*unit_id*/105001, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/3.5, /*change_face*/5, /*description*/"大人のレディには教養も大事？\nきょ、教養ってあれよね、\n成績とは関係ないのよね！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050004, /*unit_id*/105001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"同年代の子と話しても、みんな\n子どもっぽくって退屈なのよね\nだからアンタと遊んであげるわよ\n感謝しなさいよね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050005, /*unit_id*/105001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.4, /*change_face*/5, /*description*/"まずカフェに行って、次に…\nアンタはこうゆうの、\n詳しくなさそうだから\nアタシがエスコートしないとね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050006, /*unit_id*/105031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大人なレディだもん、\n魔物退治くらい簡単よ！\nまぁ、どうしても気になるなら、\n付いてきてもいいけど？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050007, /*unit_id*/105031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この前、魔物を蹴散らして\nがっつりお宝ゲットしたのよ！\n「子どもに大金は必要ない」って\n親にとりあげられたけど…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050008, /*unit_id*/105031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どきなさーい魔物ども♪\nレディ・ミサキのお通りよ！\nアンタはちゃんと、\nエスコートしなさいよね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050009, /*unit_id*/105031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんか最近、アンタと出かける\n機会、多いわよね\nアタシはけっこう楽しんでるわよ\nアンタはどう？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050010, /*unit_id*/105031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"二人で魔物退治して、\nそのお金で豪華にディナー！\nそれってすっごく大人よねぇ♪\nで、デートじゃないわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050011, /*unit_id*/105001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/9.8, /*change_face*/6, /*description*/"誕生日、おめでと！\nプレゼントは…ア・タ・シ・よ♪\n…あれ？　引いてる？\n雑誌に書いてあったのに…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050012, /*unit_id*/105031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\nアタシが、大人なバースデーを\nプロデュースしてあげるわ\n楽しみにしてなさい♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050013, /*unit_id*/105061, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うふ♪\n今日のアタシは一味違うわよ\nアタシがよそ見しないように\nしっかりエスコートしてよね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050014, /*unit_id*/105061, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇねぇ、アタシキレイでしょ♪\n…むぅ。かわいいじゃなくて、\nキレイって言いなさいよ\n言うまで帰さないんだからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050015, /*unit_id*/105061, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え？　いつもより大人っぽい？\nほんと！？　…じゃなくて…\n当然のことよ。だってアタシは\n正真正銘のレディなんだから♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050016, /*unit_id*/105061, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大人のレディなら微笑むだけで\nみんなを虜にしちゃうのよ♪\nねぇ、アンタはどう？\nアタシにドキドキする！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1050017, /*unit_id*/105061, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシ、これからもっと\nキレイになると思うわ\nだからその日まで、アタシから\n目を離さない方がいいわよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051001, /*unit_id*/105101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"ねえ君、今時間ある？\n怖がらなくても大丈夫\n危ないことはしないわ\n私を信じて…ね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051002, /*unit_id*/105101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.3, /*change_face*/5, /*description*/"良かったら一緒に実験しない？\n新しい発見があった時の\n興奮は計り知れないわよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051003, /*unit_id*/105101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"科学の発展はみんなのため\nこう見えても、病気や怪我を\n治すのが目標なの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
