INSERT INTO `unit_comments` VALUES (/*id*/1051005, /*unit_id*/105101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/1.7, /*change_face*/5, /*description*/"いつも実験に協力してくれて\nありがとう。お礼に今度、\nとっておきの秘薬をあげるわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051006, /*unit_id*/105131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あーら、私におしおきされたいの？\nそう…目覚めちゃったのね\nたっぷりと可愛がってあげる♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051007, /*unit_id*/105131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇ、どうかしら…？\n私を見て、何か感じない？\n恐怖？　…おかしいわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051008, /*unit_id*/105131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"痛みが快楽に変わる瞬間は、\n必ず訪れるものよ…\n君も体験してみない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051009, /*unit_id*/105131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君を虜にするための香水を\nつけてきたはずなのに、\nどうして魔物ばっかり…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051010, /*unit_id*/105131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君といると、不思議だけど…\n鼓動がぐっと早まるの\n…確かめてみる？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051011, /*unit_id*/105101, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/12.8, /*change_face*/2, /*description*/"君、今日が誕生日でしょう？\nはいこれ。私からのプレゼント\n特別に調合した薬なの。効能？\n…飲んでからのお楽しみ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051012, /*unit_id*/105131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n私からの贈り物は…これよ\n元気が出る特製ドリンク♪\n一気に飲んじゃだめよ…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051013, /*unit_id*/105161, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"探求心は人間の限界を超えていく\n全ての不可能はやがて可能になる\n…君と実験を続けていけば\nそれを証明できると信じているわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051014, /*unit_id*/105161, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"新しい薬の効果は絶好調ね\nこれも君がずーっと実験台を\n務めてくれたおかげよ♪\n…あら、何か言いたそうな顔ね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051015, /*unit_id*/105161, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"新しい研究のアイデアが\n次から次へと溢れてくるのよ\nどんな実験をするつもりかって？\nふふっ、すぐに体験させてあげる", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051016, /*unit_id*/105161, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"研究に行き詰ったときでも\n君の顔を思い出すと、もう少し\nがんばろうって気になるのよ\n私にとっては、栄養剤ね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1051017, /*unit_id*/105161, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どれだけ研究を重ねても、結婚の\n正解だけは分からないわね\nでも、君と検証していけば\n答えも出るかしら…ふふっ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052001, /*unit_id*/105201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/5, /*description*/"私、力持ちだからそれを活かして\nお仕事もいっぱい頑張ってるの♪\nだけど、これって女子的には\nちょっとどうなのかしら～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052002, /*unit_id*/105201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.0, /*change_face*/9, /*description*/"どう？　すてきな鎧でしょ？\nデザインもおしゃれだし\nボディにジャストフィットなの！\n私のお気に入りよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052003, /*unit_id*/105201, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/0.0, /*change_face*/5, /*description*/"も～、あなたったらさっきから\nモフモフしすぎじゃない？\nせっかくブラッシングして\n毛並みを整えたばかりなのに～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052004, /*unit_id*/105201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.8, /*change_face*/1, /*description*/"少しでも女の子らしく\n見えるように、私、がんばるわ！\nこのままあなたのそばに\n居られるように…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052005, /*unit_id*/105201, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/0.0, /*change_face*/5, /*description*/"はぁ～…憧れちゃう…\n普通の人間の女の子みたいに\nおしゃれしたり食べ歩きしたり\nあなたとデートしたいな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052006, /*unit_id*/105231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな見た目だからって\n最初からあきらめちゃダメよね！\nあなたに嫌われたくないし\n身だしなみには気を付けるわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052007, /*unit_id*/105231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なあに？　またモフモフするの？\nまあいいけどね、あなただから\nい～こい～こしてあげるわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052008, /*unit_id*/105231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔物に間違われることも多いけど\n私、運命なんかに負けないわ！\nあなたと会ってから、ますます\nそう思えるようになったの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052009, /*unit_id*/105231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたは私のこの姿を見ても\n女の子として接してくれた！\n人の内側をきちんと見てくれる\nそんなあなたが大好き！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052010, /*unit_id*/105231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんなに綺麗な空の下を\n大好きな人と一緒に歩けること\n普通の女の子になったみたいで\nすっごく嬉しいの！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052011, /*unit_id*/105201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.2, /*change_face*/9, /*description*/"お誕生日おめでとう～！\nあなたのためにお祝いのプランを\nいっぱい考えたの♪\nやだっ、私ったらはしゃぎすぎ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052012, /*unit_id*/105231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデートゥーユー♪\n人間の男の子の誕生日をこうして\nお祝いできるなんてね…\n私がプレゼントをもらった気分♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052013, /*unit_id*/105261, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつかこの素敵なドレスを着て\nあなたと踊れたらいいな…\n踊りの練習だけでも\n始めておこうかしら♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052014, /*unit_id*/105261, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"舞台の幕が上がったら\n私はお姫様…まさかこんな形で\nお姫様になれるなんて\n思わなかったけど、うれしいわ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052015, /*unit_id*/105261, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私がお姫様になったら\n何をしようかしら…\n王子様と二人きりの舞踏会とか\nしてみたいわね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052016, /*unit_id*/105261, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、あなたと出会ってから毎日が\nどんどん楽しくなっていくの！\n次はいつ会えるんだろうって\n考えてるだけで幸せよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1052017, /*unit_id*/105261, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたが王子様だったら、\n誰のどんなピンチにでも\n駆けつけてくれるんでしょうね！\n優しい王子様に憧れちゃう♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053001, /*unit_id*/105301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"貴公と私、共に\nヴァイスフリューゲルの\n興隆のために\n力を尽くそうではないか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053002, /*unit_id*/105301, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/2.7, /*change_face*/5, /*description*/"こ、子供ではない！\nた、確かに少々背は低いが…\nまだ成長期に違いない！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053003, /*unit_id*/105301, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"ん？　どうした？\n私と鍛錬がしたいのか？\n殊勝な心がけだな、貴公！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053004, /*unit_id*/105301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"軍人は、受けた恩は返すものだ\nだから…私にできることが\nあれば、なんでも言ってくれ\n頼りにしてくれよな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053005, /*unit_id*/105301, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.0, /*change_face*/5, /*description*/"と、ところでだな…\n今度、貴公と二人で、\n我が本国を旅行するとか…\nど、どうだろうか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053006, /*unit_id*/105331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何度言えばわかるのだ！\nだから、私は子供ではないっ！\nば、バカにするな～！\nこ、こら、頭をなでるな～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053007, /*unit_id*/105331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"天空より舞い降りし\nヴァイスフリューゲルの剣技！\nその華麗なる姿を、\n見せてやる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053008, /*unit_id*/105331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴公！\n気がたるんではいないか？\n今すぐ準備をして平原に来い！\n私と共に鍛錬だ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053009, /*unit_id*/105331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は子供ではない、が…\n貴公から見たら、やはり子供に\n見えてしまうのだろうか？\nそうでないと、いいのだが…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053010, /*unit_id*/105331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴公に私の命は預けた\nこの身が尽きるまで…\n軍人の誇りを持って、\n二人で戦いぬくぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053011, /*unit_id*/105301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.1, /*change_face*/2, /*description*/"今日は貴公の誕生日らしいな\n大切な一日を共に祝えて、\n私も嬉しく思うぞ！\n心から祝おうじゃないか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053012, /*unit_id*/105331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう\nプレゼント代わりに…\n今日は私が共に過ごしてやろう\nじ、時間を無駄にするでないぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053013, /*unit_id*/105361, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"待たせたな！\nこれが私の新しい奥義だ！\n友よ、見届けてくれ！\n我らの勝利の瞬間を！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053014, /*unit_id*/105361, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"驚くのはまだ早いぞ！\nなんといっても私は成長期！\n修行すれば更に大きく…\n背の話ではなく奥義の話だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053015, /*unit_id*/105361, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よし！　また成功だ！\n貴公、見てたか！？\n…『努力の天才』？\nて、照れるではないか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053016, /*unit_id*/105361, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の勝利が貴公の勝利となる！\nだからこそ負けられん！\n我が名誉よりも、友への想いで\n私はこの勝負に勝つのだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1053017, /*unit_id*/105361, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どれほどこの身が傷つこうとも\n敵は誰一人通さんっ！\n我が友を…\n貴公を守るのは、この私だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054001, /*unit_id*/105401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/1.9, /*change_face*/5, /*description*/"いいですか騎士さん\nレイ様に変なことしたら\nただじゃおきませんよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054002, /*unit_id*/105401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.8, /*change_face*/2, /*description*/"騎士さんがレイ様に\n変なことしないように\n私がしっかり見張ります！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054003, /*unit_id*/105401, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/2.0, /*change_face*/1, /*description*/"ああ、また服が…\nちょっとこっちに来てください\nすぐに直してあげますから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054004, /*unit_id*/105401, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.8, /*change_face*/6, /*description*/"あの…騎士さんって\nやっぱりレイ様のこと…\nな、なんでもないです！\n忘れてください！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054005, /*unit_id*/105401, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/6.5, /*change_face*/6, /*description*/"どうして騎士さんは\nひどいことした私にここまで\n優しくしてくれるんですか？\nまさか…また縛られたいとか！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054006, /*unit_id*/105431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何匹来ても平気です！\n騎士さんがそばにいてくれるから！\n…変な意味じゃありませんよ！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054007, /*unit_id*/105431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そこはさすがに近すぎます\nもう少し下がってください\nさもないと、日頃の鬱憤\nぶつけちゃいますよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054008, /*unit_id*/105431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"素材が余ったら騎士さんにも\n何か作ってあげましょうか\nそうですね…何がいいですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054009, /*unit_id*/105431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"最近も…相変わらずレイ様と\n仲いいんですか…？\nき、聞いてみただけですっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054010, /*unit_id*/105431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士さんは私に何か\n手伝ってほしいことあります？\n報酬は特別になしでいいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054011, /*unit_id*/105401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.3, /*change_face*/5, /*description*/"た、誕生日なんですよね？\n一応おめでとうございます！\n…どこで知ったかなんて\nどうでもいいじゃないですか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054012, /*unit_id*/105431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あの、おめでとうございます…\n騎士さんの誕生日ですよ！\nこれ…ハンカチです…え？\nプレゼントですっ！　バカっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054013, /*unit_id*/105461, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士さんとの絆が強くなるほどに\n私の力も強くなります！\n…って、いいですけど\nちょっと近くないですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054014, /*unit_id*/105461, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一人一人が輝く服を作るのが\n私の夢なんですっ！\nこの力があれば、\nそれを叶えられますよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054015, /*unit_id*/105461, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見ていてください！\nアイドルとしてだけじゃなく\n仕立て屋としても\nみんなを笑顔にしてみせますっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054016, /*unit_id*/105461, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつもお世話になってますから\n今度は私がお手伝いしますね？\nべ、別に騎士さんと一緒に\n居たいわけじゃないです…からっ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1054017, /*unit_id*/105461, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もしかして、私の運命の赤い糸は\nレイ様じゃなくて騎士さんと\nつながってたり…して…？\nやだ、私なにを考えてるんだか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
