INSERT INTO `unit_comments` VALUES (/*id*/1055006, /*unit_id*/105531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔物退治のお手伝いを\n私なんかにさせていただいて\nありがとうございます！\nが、頑張りますから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
