INSERT INTO `unit_comments` VALUES (/*id*/1055010, /*unit_id*/105531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩と私のコンビネーション\n…バ、バッチリですね！\nえへへへ、共同作業だぁ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1055011, /*unit_id*/105501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.8, /*change_face*/5, /*description*/"あっあの、先輩っ！\nお誕生日おめでとうございます！\nお祝いさせてくだひゃい！\nちゃ、ちゃんと言えましたっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1055012, /*unit_id*/105531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\nプ、プレ、プレゼントどうぞっ！\n先輩に似合う服を見つけました\nそれから次は、先輩が好きな…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1055013, /*unit_id*/105561, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩に助けてもらって\nばっかりじゃありません…！\nこれからは、私だって…\n先輩と一緒に戦います！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
