INSERT INTO `unit_comments` VALUES (/*id*/1055015, /*unit_id*/105561, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キ、キリっとした顔で、\n先輩に成長したところを\nちゃんと見せなきゃ…！\n眉毛をあげればいいのかな…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1055016, /*unit_id*/105561, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"先輩と一緒にいろんなことを\n経験して、自信がついてきました\nだ、だからこれからも…\nよろしくお願いしましゅ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1055017, /*unit_id*/105561, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"怖いことや危ないことがあっても\nずっと先輩のそばにいます\nきっとそれだけが、私に\nできることですから…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056001, /*unit_id*/105601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"お節介がすぎるのか、よく\n面倒ごとに巻き込まれるんだ\nお前さんも困ったことがあれば\n遠慮なく声かけておくれ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056002, /*unit_id*/105601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.4, /*change_face*/1, /*description*/"ひと仕事終えたし\n釣りにでも行くとするか…\nお前さんもどうだい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056003, /*unit_id*/105601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/8.3, /*change_face*/5, /*description*/"お前さん、いい所に来たね！\n悪さしてたヤツらをちょいと\n懲らしめたら謝礼がもらえてね\nちょいと一杯付き合っとくれよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056004, /*unit_id*/105601, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/2.7, /*change_face*/1, /*description*/"抱き締めるのはよせって？\n分かっちゃいるんだけどね\nお前さんがいい男なのが\n悪いんだよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056005, /*unit_id*/105601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"かわいい見た目してるのに\n意外と肝が据わってるね\nまた厄介事があった時は\n頼らせておくれよ、お前さん", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056006, /*unit_id*/105631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前さんのためなら\nコイツを抜くのも悪くない…\n全力でいくとしようか！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056007, /*unit_id*/105631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシにもう一本使わせたこと\nたっぷり後悔させてやるよ\nその身でとくと味わいな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056008, /*unit_id*/105631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何人相手にすりゃいいんだ？\nこっちは早いとこ終わらせて\nのんびりしたいってのに…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056009, /*unit_id*/105631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"手伝ってくれるのかい？\nお前さんがいると心強いね！\nとっとと片付けちまおう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056010, /*unit_id*/105631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誰かに背中を預けるなんて\nお前さんくらいなもんさ\nこれからも、頼りにしてるよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056011, /*unit_id*/105601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.3, /*change_face*/2, /*description*/"お前さん、今日が誕生日なのか？\nそうか…めでたい日にはパーッと\n祝うに限る。そうと決まれば\n今日の飯はアタシのおごりだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1056012, /*unit_id*/105631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとさん！\nいい釣竿が手に入ったんだ\nお前さんにあげるよ。今度二人で\n一緒にのんびり釣りに行こう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1057001, /*unit_id*/105701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.2, /*change_face*/2, /*description*/"同じ騎空士として\n一緒に頑張ろうね！\n何か困ったことがあったら\nいつでも相談して！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1057002, /*unit_id*/105701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"良かったら一緒に修行しない？\n一人よりも二人でやったほうが\nもっと強くなれると思わない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1057003, /*unit_id*/105701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私たちの騎空艇ができたら\nまずはアストライア大陸を\n一周したいな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1057004, /*unit_id*/105701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/3.7, /*change_face*/5, /*description*/"あなたが一緒なら、\n何があっても怖くないよ！\n…本当だよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
