INSERT INTO `unit_comments` VALUES (/*id*/1060011, /*unit_id*/106001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.9, /*change_face*/5, /*description*/"へ～、あんた、誕生日なんだ～\nあ、たまたまケーキ持ってるから\n一緒に食べましょ？\n…たまたまよ！　本当だからね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
