INSERT INTO `unit_comments` VALUES (/*id*/1061007, /*unit_id*/106131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"新しい天楼覇断剣か…どんな\nヤツが相手でも、コイツがあれば\nアタシは無敵だ！　いくぞぉぉぉ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
