INSERT INTO `unit_comments` VALUES (/*id*/1063009, /*unit_id*/106331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドキドキするのに、落ち着くし…\n安心するのに、たまに苦しくて…\nあなたの側にいると、\n不思議な気持ちになっちゃうな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063010, /*unit_id*/106331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森にいた頃よりも、\n今の方が楽しく思えちゃうのは…\nきっと、あなたのおかげかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063011, /*unit_id*/106301, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/4.9, /*change_face*/2, /*description*/"ちょっと自信ないけど…\nこれ！　作ったんだ！\n今日は特別な日だから…\nあーんってしてあげよっか…？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063012, /*unit_id*/106331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このままどんどん成長したら\nあなたはどうなっちゃうんだろ？\n…なんか、ずっと楽しみだね\nえへへ…お誕生日おめでと！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063013, /*unit_id*/106361, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力と、想い…大切なこと\n少しだけ、分かった気がするよ\n私、ちょっとは成長できたかな？\nえへへ…あなたのおかげだね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063014, /*unit_id*/106361, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ランドソルに来てよかったなぁ\nいろんな思い出が出来て、\nいろんな人と話せて…\n何より、あなたと出会えたから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063015, /*unit_id*/106361, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森番の試練…大変だったな\nだけどおかげで、一歩ずつでも\n前に進めてる気がするんだ\n大人になるってこういう事かな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063016, /*unit_id*/106361, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ロザリアみたいに、じゃない。\n私は私らしく、頑張るんだ\nこの想いも、いつか私らしく…\n…ひゃあ！？　き、聞いてた！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063017, /*unit_id*/106361, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"最初に会った時から、\nあなたはずっと優しいね\nそういうところが…す、す…\nす、素敵だと思うな！　うん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064001, /*unit_id*/106401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.2, /*change_face*/2, /*description*/"自分のなくした記憶もだけど…\nあなたが忘れた過去も気になるわ\n二人で一緒に\n見つけられたらいいわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064002, /*unit_id*/106401, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.6, /*change_face*/2, /*description*/"アイスクリームって、どうして\nこうもおいしく感じるのかしら…\n『前の私』もひょっとしたら\nアイスが好きだったりしてね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064003, /*unit_id*/106401, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.7, /*change_face*/1, /*description*/"そんなに真面目っぽい性格に、\n見えるかしら？\n冗談とか言うのは、確かに苦手ね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064004, /*unit_id*/106401, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.1, /*change_face*/5, /*description*/"…ふとした弾みで、思い出すの\nコッコロさんに甘えてたこととか\nキャルのしっぽの味とか…\nもう…顔から火が出そうよ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064005, /*unit_id*/106401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/6.7, /*change_face*/1, /*description*/"【美食殿】での生活は\n毎日が充実していて、楽しいわ\nこの時間が、少しでも長く続く\nように…精一杯努力するつもりよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064006, /*unit_id*/106431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"必ず、みんなを守ってみせる！\nだって、みんなの隣が\n『今の私』の居場所だから！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064007, /*unit_id*/106431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"氷を滑る、この動き―\n身体が覚えてる？\nううん、今はそんなこと\n考えてる場合じゃない…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064008, /*unit_id*/106431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"っくしゅっ…！\n氷の魔法は使えても、そこまで\n寒さに強いわけじゃないみたい\n後で、温かいスープが欲しいわね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064009, /*unit_id*/106431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私を迎えてくれた\n【美食殿】のみんな、\nそれにあなたも…\n絶対に、守ってみせるわ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064010, /*unit_id*/106431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…不思議ね\nあなたから力をもらうと、\n胸が熱くなって…\nどこまでも跳べそうな気がするわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064011, /*unit_id*/106401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/6.5, /*change_face*/4, /*description*/"お誕生日、おめでとう\nケーキ、一緒に食べましょう\nでも…魔物とか昆虫とか\nケーキに入ってないわよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064012, /*unit_id*/106431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、自分の誕生日が分からなくて\n…だから、お祝いとかできないの\nその分、あなたの誕生日を\nお祝いさせて貰っていいかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064013, /*unit_id*/106461, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"願いと引き換えに\n手に入れたこの『力』…\n私の愛する人たち…\n家族やあなたは私が守るわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064014, /*unit_id*/106461, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は【美食殿】のシェフィ…\n今なら胸を張ってそう言える\nだからあなたのそばにいたいって\n気持ちも、もう隠したりしない", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064015, /*unit_id*/106461, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"記憶がなかった頃\nあなたは大丈夫だよって笑って\n迷子のような私を支えてくれた…\n今度は私があなたを助ける番よ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064016, /*unit_id*/106461, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"みんなと過ごす時間は、\n私を幸せな気持ちにしてくれた\nでも、あなたと二人で過ごす\nひと時も…キライじゃなかったわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1064017, /*unit_id*/106461, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"過去を知った時は辛かった…\nでも、知らないままだったら\nなにも気づけなかったと思うの\nきっと、あなたへの想いにも…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065001, /*unit_id*/106501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"キミと一緒にいれば\nなんか面白いことが起きるだろ？\nだから期待させてもらってるよ\nさ、どっか遊びにいこうぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065002, /*unit_id*/106501, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/0.0, /*change_face*/3, /*description*/"オレはこの世界に\nなにが起きたのか知りたい…\nそのためにもオレは\nもっと強くなる必要があるんだよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065003, /*unit_id*/106501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/8.9, /*change_face*/1, /*description*/"なあ、仕事の依頼はないのか？\nだからケンカだって。ケンカの\n仕事。オレがたぎるようなやつを\n持ってきてくれよな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065004, /*unit_id*/106501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.6, /*change_face*/2, /*description*/"そう心配すんなって\nそこらのヤツにやられるほど\nオレは弱くはないさ\nなんなら、試してみるか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065005, /*unit_id*/106501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.8, /*change_face*/1, /*description*/"ドラゴン族以外のやつと\nここまで仲良くなるなんて\n思わなかったぜ\nキミ、一体何者なんだ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065006, /*unit_id*/106531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"喧嘩屋の本領、見せてやるよ！\nダチが一緒じゃ負けられねぇ！\n誰だろうとぶっ倒して、オレ様が\n最強だって証明してやるぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065007, /*unit_id*/106531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"すげえぜ、この力！\n体の奥から\nどんどん溢れてきやがる！\nオレたちに壊せないものはねえ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065008, /*unit_id*/106531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これがドラゴン族の力…！\nオレ様が使いこなしてやるよ！\nダチが見てるんだ。情けねえ\nカッコは見せらんねぇよな！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065009, /*unit_id*/106531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ダチと一緒に戦うのが\nこんなに楽しいなんてな…！\n知らないこの世界で知った\n最高にたぎる感情だ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065010, /*unit_id*/106531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いいぜいいぜ、たぎってきた！\nキミが一緒なら、これっぽっちも\n負ける気がしねえ！\n派手に暴れてやろうぜ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065011, /*unit_id*/106501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"よっ！　おめでとう！\n今日はキミの誕生日だろ？\nパーッとなんか食べに行こうぜ\n今日はオレがおごってやるよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065012, /*unit_id*/106531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！\nダチにとって良い日ってのは\nこっちまでうれしくなるんだな\nこんな気持ち、初めてだ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066001, /*unit_id*/106601, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/4.2, /*change_face*/3, /*description*/"ぴゃっ！　って、お兄さんですか\n何じろじろ見てるです、えっち！\n煩悩まみれ！　不審者だったら\n叩き潰してるところですよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066002, /*unit_id*/106601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.4, /*change_face*/4, /*description*/"あたしがサボってここにいるのは\nボスには内緒にするですよ？\nもしあのババアに見つかったら\n何されるか分からないです…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066003, /*unit_id*/106601, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.9, /*change_face*/1, /*description*/"は～…ほんとうに\nあたしたち以外にも\nドラゴン族っているんですかね？\n…そこらへんに歩いてないです？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066004, /*unit_id*/106601, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.0, /*change_face*/6, /*description*/"今日の仕事はここまでです！\n縄張りを見回ってたですよ！\nべ、別にサボってブラブラしてた\nわけじゃないですからね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066005, /*unit_id*/106601, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/6.5, /*change_face*/3, /*description*/"あ～あ、どこかにぽろっと\n大金が落ちてないですかね？\n早く自由の身になって\nボスを見返してやりたいですよ…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066006, /*unit_id*/106631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですかお兄さん！\nこれがあたしの…\nドラゴン族の力ですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066007, /*unit_id*/106631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふっふっふ…心技体ともに\n覚醒した今のあたしには\n死角はないです…ってぴゃあ！\nあ、足場が崩れたですよぉ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066008, /*unit_id*/106631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力がわきあがってくるです！\n知性派のあたしですけど\nたまには本能のおもむくまま\nってのも、悪くないです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066009, /*unit_id*/106631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この力があれば…\nお兄さんがどれだけ\nへっぽこナイトでも\n助けてあげられるですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066010, /*unit_id*/106631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もう守ってもらうばかりの\nあたしじゃないですよ！\nこの力でなりあがってやるです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066011, /*unit_id*/106601, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/6.3, /*change_face*/2, /*description*/"え？　なんです？　今日は\nお兄さんの誕生日ですか？\nじゃあ特別に、あたしに\n何かおごる権利をやるですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066012, /*unit_id*/106631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうです！\nこのあたしに祝われるなんて\nそうそうない名誉ですからね！\n感謝して何かおごりやがれです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066013, /*unit_id*/106661, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力が溢れてくるです！\nこれが完全な覚醒…\nこうなったあたしは、\n誰にも止められねーですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066014, /*unit_id*/106661, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どうですか！　これこそが\nあたしの真のパワーですよ！\nこの姿、その目にしっかりと\n焼き付けるがいいです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066015, /*unit_id*/106661, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたし一人だけだったら、きっと\n手に入れられなかった力です…\nお兄さんだけには、特別に\n手を貸してやるですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066016, /*unit_id*/106661, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしが覚醒するには\nお兄さんが必要ですからね\nふふーん。頼まれたって\n離れてやんねーですよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1066017, /*unit_id*/106661, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そんな顔してどうしたです？\n覚醒してもあたしはあたしです\nどっかいったりしねーですから\n安心しやがれです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067001, /*unit_id*/106701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/9.3, /*change_face*/2, /*description*/"揉め事に巻き込まれたら\n私の名前、出しちゃっていいよ\n…もっと悪いことが起きる？\nだって、そのほうが楽しいし♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067002, /*unit_id*/106701, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.6, /*change_face*/1, /*description*/"ギャングの心得その一はね…\nファミリーに入れる気はないよ～\n知ってるだけでいいから♪\n…何かあった時のために、ね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067003, /*unit_id*/106701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.0, /*change_face*/2, /*description*/"カヤちゃんとイノリちゃんと\nこれからも仲良くしてあげてね\n…私？　…君とはずっ～と前から\n仲良しだし、大丈夫♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067004, /*unit_id*/106701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.5, /*change_face*/2, /*description*/"君の頑張りが世界を\nより楽しく変えてくれるなら\nなんだって協力するよ\n遠慮せずに頼ってきてね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067005, /*unit_id*/106701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.8, /*change_face*/5, /*description*/"君がどんな色に変わっていくのか\n私はそれを最後まで見ていたいの\n…本当は私の選んだ色を\n使ってほしいけど、まだ早いかな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067006, /*unit_id*/106731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふふっ…あははっ♪\n戦うのって、本っ当に面白い…\n命を、魂を懸けた勝負…\nとってもワクワクするよね☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067007, /*unit_id*/106731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の目に狙われてる意味も\n理解できない相手なんて\n敵としての存在価値もないし…\nすぐに消してあげないと♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067008, /*unit_id*/106731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私たちの生きていく未来は\n誰かに決められるものじゃない…\nものすご～く大変だったとしても\nみんなで一緒に奪い返そうね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067009, /*unit_id*/106731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねえ…君の目に、今の私は\nどんな風に見えてるのかな？\n怖い？　かっこいい？\n私はね…心の底から楽しいよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067010, /*unit_id*/106731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねえ、君を苦しませるものを\n世界ごと焼き尽くしちゃおうか…\n実はそれだけのリスクがあるって\nじっくりと教えてあげたいし…♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067011, /*unit_id*/106701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"誕生日おめでとう～☆\nプレゼントは、一日私を\n好きにできちゃう券で～す\nいつ使いたい？　今でもいいよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1067012, /*unit_id*/106731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー☆\n君もドラゴン族になれる\nアイテムを作ってきました～☆\nドラゴン族…なってみたくない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068001, /*unit_id*/106801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/8.1, /*change_face*/1, /*description*/"やあ少年。元気にしてる？\nたまには店に寄っていってよ\n美味しいクレープ作るからさ\n味は普通？　…そうかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068002, /*unit_id*/106801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/8.5, /*change_face*/6, /*description*/"おや、今日は人助けはお休み？\n一人きりでのんびりしたいなら\n迷宮を作ってあげようか\nあれ？　なんで逃げるの？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068003, /*unit_id*/106801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/8.5, /*change_face*/6, /*description*/"実はちょっと困ってるんだよね…\n予約してた新作ゲームが\nこっちの世界だと遊べない！\nこれは由々しき問題だよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068004, /*unit_id*/106801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/"少年と一緒にいると\n世界も自分も、いい方向に\n向かう予感がするんだ\nキミはずっと、そのままでいてね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068005, /*unit_id*/106801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/8.4, /*change_face*/2, /*description*/"…記憶がなくても、キミの本質は\n変わらない。それを知った時は\n本当にうれしかったよ\nこれからもよろしくね、少年", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068006, /*unit_id*/106831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"迷宮女王とプリンセスナイトが\n力を合わせれば、どんな敵が\n相手でも勝てるさ\nさあ少年、一緒に迎え撃とう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068007, /*unit_id*/106831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ああ…やっとキミのそばで戦える\nうれしいよ、少年。七冠として、\n一人の人間としてキミを\n守り抜くと、今ここに誓うよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068008, /*unit_id*/106831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"おいで、憐れな挑戦者たち\nアタシの迷宮から、果たして\n生きて帰れるかな…？\nその足掻き、見せてもらうよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068009, /*unit_id*/106831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"近頃少年を近くに感じていると\n力が溢れて止まらないんだ\nこれってもしかして…恋？\n…いや、愛だ！　だよね、少年！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068010, /*unit_id*/106831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"アタシは人間が大好きだ\nでもそれ以上に、少年が好きだよ\n…どうかな、ドキッとした？\n真剣だよ？　それがどうかした？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068011, /*unit_id*/106801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"少年、誕生日おめでとう～！\n今日くらいはクレープじゃなくて\nケーキをプレゼントしちゃおう！\nでもクレープもあげちゃう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068012, /*unit_id*/106831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少年、誕生日おめでとう\nキミがこの世に生を受けた日は\n盛大に祝うべきだ。今から一緒に\nプレゼントを選びに行こう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069001, /*unit_id*/106901, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.0, /*change_face*/1, /*description*/"あなた、私と一緒に戦うなんて\nおかしな未来を選んだのね\n後悔しても、もう遅いわよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069002, /*unit_id*/106901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"剣の練習に付き合ってあげるわ\n一刻も早く成長して\n私にそれを突き立てなさい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069003, /*unit_id*/106901, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.9, /*change_face*/4, /*description*/"私に勉強を教わりたい？\n何を言うかと思えば…そもそも\n私の言葉を正しく理解できて？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069004, /*unit_id*/106901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.1, /*change_face*/3, /*description*/"食事を持ってきた…？\nなによこれ…魔物じゃないの\n見た目のなってない料理は嫌いよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069005, /*unit_id*/106901, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/4.5, /*change_face*/6, /*description*/"今日もくだらない一日を\n過ごしてきたみたいね…\nどうして満足気なのかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069006, /*unit_id*/106931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私を否定する世界はいらないの\n全て作り変えるわ、全てね\nだから何もかも終わらせるの", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069007, /*unit_id*/106931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私に全てを奪われたあなたには\n私の全てを奪う資格があるわ\n互いの全てをかけて戦いましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069008, /*unit_id*/106931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"仲間に頼らないと何もできない…\nそんなあなたには負けられないの\n私の強さを証明させてもらうわよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069009, /*unit_id*/106931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"守る価値のない未来のために\nどうして必死になれるのかしら\n本当にあなたは…理解できないわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069010, /*unit_id*/106931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私を倒しても、その先には\n今以上の苦しみがあるのに…\n理解してもなおあなたは進むのね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069011, /*unit_id*/106901, /*use_type*/2, /*voice_id*/6, /*face_id*/3, /*change_time*/3.9, /*change_face*/1, /*description*/"誕生日？　どうでもいいじゃない\nいつ世界に生まれたかじゃなくて\n何を成すかが大事なのだから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1069012, /*unit_id*/106931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなた、今日が誕生日らしいけど\nいつまで祝えるかしらね…？\n来年まで生き延びてたら\nその時は祝福してあげるわ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070001, /*unit_id*/107001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"敵の敵は味方…\n今回は貴方に協力しましょう\nよろしく頼みます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070002, /*unit_id*/107001, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.6, /*change_face*/1, /*description*/"プリンセスナイトとしてではなく\n貴方だけが持つ可能性\nそれに賭けさせてもらいます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070003, /*unit_id*/107001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.0, /*change_face*/2, /*description*/"今の私？　ええ、私本人ですよ\n今の貴方に自分を偽っても\n意味がありませんから", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070004, /*unit_id*/107001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.3, /*change_face*/6, /*description*/"貴方といると、犬を\nもう一匹飼い始めた気分です\n飼ったこと？　ありませんよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070005, /*unit_id*/107001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.3, /*change_face*/2, /*description*/"貴方の好みのタイプを\n教えなさい。再現してあげます\n妹系？　姉系？　幼馴染？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070006, /*unit_id*/107031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"自分の目に映った私が\n本当の私だと思いますか…？\n私は変貌大妃\n千変万化の女ですよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070007, /*unit_id*/107031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の『冠』が偽物ではないと\nその身に刻み込んであげます\nもっとも全ての敵は私に模倣され\n消えて無くなりますが", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070008, /*unit_id*/107031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"鏡よ鏡…世界で最も\n賢く強いのは誰ですか？\n…ええその通り。当然私です", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070009, /*unit_id*/107031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私に仕えたくなりましたか？\n私の中にデータさえあれば\n本人は不要なのです\n…彼以外は", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070010, /*unit_id*/107031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こそこそ隠れるのは終わりです\nこれから先は打って出ます\n貴方とその仲間たち…\n戦力として当てにしていますよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070011, /*unit_id*/107001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.8, /*change_face*/6, /*description*/"今日が誕生日ですか\nおめでとうございます\n…これ以上何か必要ですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1070012, /*unit_id*/107031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとうございます\n贈り物は何にしましょう…\n計算問題集ではどうですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071001, /*unit_id*/107101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"ややこしい話し合いは面倒だ！\n物事はシンプルに解決しよう\nそう、生きるか死ぬかの\n本気の殺り合いでな☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071002, /*unit_id*/107101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/2.5, /*change_face*/1, /*description*/"息苦しい世の中だ\n自分の欲求に従って\n好き勝手に生きた者の勝ちだとは\n思わんか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071003, /*unit_id*/107101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.1, /*change_face*/4, /*description*/"強すぎるというのも考えものだ\n歯ごたえのない連中ばかりで\n命の遣り取りをする愉しい戦いに\nそうそう出会えないからな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071004, /*unit_id*/107101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/8.5, /*change_face*/2, /*description*/"ワタシの前に散っていった\n男たちは、この胸を触ったオマエを\n心底憎く思うだろうなあ\n安心しろ、全員墓の中だ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071005, /*unit_id*/107101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.8, /*change_face*/5, /*description*/"オマエが幸せな結婚生活を\n送れるように、ワタシだって\n努力に手を抜かない\n意外と可愛いところもあるだろ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071006, /*unit_id*/107131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"イかれてなどいないさ\n人間の命は儚く脆い…\nその刹那の時もワタシは\n楽しみ切ってやるのさ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071007, /*unit_id*/107131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦場を満たす血だまりの上で\n一緒に陽気なステップを\n踏もうじゃないか\nさあ、お手をどうぞ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071008, /*unit_id*/107131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誓約で仕方なく…ではないぞ？\n少なくともワタシは納得している\nこのワタシに触れた男だ\n結婚相手として認めているよ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071009, /*unit_id*/107131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この胸の高ぶるような\n愉しい出来事はないか？\nないならばワタシが\n火種をつけてやろう☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071010, /*unit_id*/107131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ワタシの幼女時代の写真が\nもっと見たいだと？　もしや\n第二次性徴期前の幼子にしか\n興奮しない性癖だったのか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071011, /*unit_id*/107101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.1, /*change_face*/2, /*description*/"オマエ、今日が誕生日らしいな？\nほら、一つ歳をとった哀れな子羊に\nプレゼントだ\n今年もワタシを楽しませてくれよ？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071012, /*unit_id*/107131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう♪\nオマエの記念すべき日に\n立ち会えてうれしいよ！\n記念の誓約は…まだ早いかな？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071013, /*unit_id*/107161, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"気をつけろ坊や\nこの「結婚」という伏魔殿…\nあらゆる所に落とし穴がある\nどんな牙城よりも手ごわいぞ…！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071014, /*unit_id*/107161, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一人で千万の敵に挑むよりも\n二人で普通に生きることの方が\n難しいとはな…\nふふ、結婚…実に面白い！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071015, /*unit_id*/107161, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このワタシが共同作業とはな…\nしかし、共に乗り越えた先でしか\n見られない景色があるらしい\n坊や…見に行くぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071016, /*unit_id*/107161, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ワタシと共に生きるのは\n想像以上にハードだろう？\nだがまだこんなものじゃあないぞ\n坊や─死ぬなよっ☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1071017, /*unit_id*/107161, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"上り坂下り坂まさかの連続とは…\nふふっ、結婚もとんだ戦場だな\n何があっても手を離すなよ？\n病める時も健やかなる時もな☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075001, /*unit_id*/107501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.2, /*change_face*/2, /*description*/"水着買っちゃいました～！\nどうですどうです？\n似合ってますか～？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075002, /*unit_id*/107501, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この水着、結構動きやすいので\n素潜りとかして\n海の幸をゲットできそうです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075003, /*unit_id*/107501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"マリンスポーツしましょう！\nビーチボールとか、\nビーチフラッグとか！\n負けませんよ～", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075004, /*unit_id*/107501, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/4.3, /*change_face*/2, /*description*/"私の水着姿で、\nあなたを誘惑しちゃいます♪\nなんちゃってー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075005, /*unit_id*/107501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"せっかくの水着ですし、\nたくさん泳いで、\nたくさんはしゃいで…\n思い出いっぱい作りましょうね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075006, /*unit_id*/107531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わぁっ、やりましたね～！\n塩水しょっぱいぃ～\nこうなったらわたしも本気です！\n覚悟してくださいよぉ～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075007, /*unit_id*/107531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"青い海、白い砂浜、輝く太陽！\nそしておいしいお魚！\nここは天国ですね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075008, /*unit_id*/107531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水が気持ちいいですね～\nこんなに充実した夏、\n生まれて初めてです！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075009, /*unit_id*/107531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ♪\nあなたと海で遊ぶなんて、\nとっても新鮮な感じです！\n青春ですね♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075010, /*unit_id*/107531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はしゃぎすぎて水着が\n流されちゃったりしたら、\nあなたに抱きついて\n隠しますね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075011, /*unit_id*/107501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.6, /*change_face*/5, /*description*/"お誕生日おめでとうございます！\nお魚、貝、タコ、エビ、カニ…\n海の幸満載のバースデー\nパーティーにしましょうね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075012, /*unit_id*/107531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"水着で過ごす、あなたの誕生日！\n開放的でわくわくしちゃいます！\nさぁ、今日は二人で\nおもいっきりはしゃぎましょう！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075013, /*unit_id*/107561, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の海に二人きり…\nなんだかドキドキしちゃいます！\nせっかくなので、雰囲気を出して\nダーリンって呼んでいいですか☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075014, /*unit_id*/107561, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"不思議ですね…\n毎日見ているはずなのに\nあなたと眺める夕日は、なんだか\nロマンチックな感じがします♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075015, /*unit_id*/107561, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"周りはカップルだらけ…\n郷に入っては郷に従え！\nということで、その…\n二人で手を繋いでみませんか♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075016, /*unit_id*/107561, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海、楽しかったですね！\nなので、今日が終わることが\nすごくさみしいです。あの…\nまたあなたを誘っていいですか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1075017, /*unit_id*/107561, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたと過ごした夏は\nわたしにとってキラキラした\n青春で、大切な宝物です！\n…ずっと、忘れませんから☆", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076001, /*unit_id*/107601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.8, /*change_face*/5, /*description*/"救護院の皆さまに、水着を\n選んでいただきました…\n似合っていますでしょうか？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076002, /*unit_id*/107601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さま、よろしければ\n一緒に泳ぎの\n練習をしましょう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076003, /*unit_id*/107601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"主さま。わたくし、\n砂のお城というものを\n作ってみたいのですが、\nお手伝いいただけますでしょうか", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076004, /*unit_id*/107601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/0.0, /*change_face*/5, /*description*/"このような\n過度な肌の露出は、\nやはり恥ずかしいです…\n主さまにしかお見せできません", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076005, /*unit_id*/107601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"日焼け止めを塗っていただいても\nよいでしょうか？\n主さまにでしたら、\n肌を許すことができます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076006, /*unit_id*/107631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さま。どんな場所でも、\nわたくしがついております\nこの手を離さないでくださいね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076007, /*unit_id*/107631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見てください、主さま\nおさかながこんなに近くに…\nとても神秘的でございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076008, /*unit_id*/107631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"このまま流れに身を任せ、\n二人でどこまでも進む\n…それも悪くありませんね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076009, /*unit_id*/107631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これからもずっと、\n主さまと同じ景色を見て、\n主さまと同じ感動を抱き、\nともに歩んでいきたいです", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076010, /*unit_id*/107631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この広い海の中で、\n主さまと二人っきり…\nいまだけは、主さまを\n独り占めです♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076011, /*unit_id*/107601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"海で、主さまのお誕生日を\nお祝いするなどと、故郷にいた\n時には考えもしませんでした\nわたくし、幸せでございます", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076012, /*unit_id*/107631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"空も海も輝いて…この世の\n全てが、主さまのお誕生日を\n祝福しているようです\nわたくしも心よりお祝いします♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076013, /*unit_id*/107661, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"山奥の里で育ったわたくしですが\nもう井の中の蛙ではございません\n主さまのおかげで、このように\n大海を知ることができました♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076014, /*unit_id*/107661, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さま、ご覧くださいまし…\nわたくしたちの冒険の行く先を\n星々の光ように、お魚さんたちが\n照らしておりますよ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076015, /*unit_id*/107661, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまと過ごす夏は\nなんと刺激的なのでしょう…\nこれからも、どうか夏のガイドを\n務めさせてくださいまし！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076016, /*unit_id*/107661, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"主さまのお手手を引いて\nこれからもまだ見ぬ神秘の地へと\nガイドを続けてまいります\nどうぞお付き合いくださいまし♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1076017, /*unit_id*/107661, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『星々の海』…この素晴らしい\n光景を、主さまとともに\n見て、感じて、味わうことができ\nたいへんうれしく思います♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1077001, /*unit_id*/107701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.8, /*change_face*/5, /*description*/"働くのって楽しいですね！\nあなたと一緒だと、もっと\n楽しく感じちゃいます♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
