INSERT INTO `unit_comments` VALUES (/*id*/1078011, /*unit_id*/107801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/"誕生日、おめでと！\nこんな日くらい、パーッと\n羽目を外して楽しみましょ☆\nさ～海、海！　遊ぶわよー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
