INSERT INTO `unit_comments` VALUES (/*id*/1078013, /*unit_id*/107861, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな楽園みたいな所に\n来れるなんて夢みたいだわ！\nあーもう最っ高！\nさあ、楽しみつくすわよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1078014, /*unit_id*/107861, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せっかくの穏やかな時間なのよ？\nあくせくしないで波に身を任せて\nのんびり過ごすの。こういうのも\nたまにはいいでしょ♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1078015, /*unit_id*/107861, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キレイな海もまばゆい太陽も\n別に初めてじゃないのに\n何倍も心地よく感じるわ\nこれもあんたのおかげかしら？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
