INSERT INTO `unit_comments` VALUES (/*id*/1086010, /*unit_id*/108631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あったかくなったら、\n雪だるまはなくなっちゃうけど…\n私たちはずーっと一緒だよね、\nお兄ちゃん♪", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1086011, /*unit_id*/108601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.8, /*change_face*/6, /*description*/"お誕生日おめでとう！\nバースデーケーキは、\nぷうきちの形で作ってみたよ！\nダメっ、そっちは本物だよー！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1086012, /*unit_id*/108631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お兄ちゃん、誕生日おめでとー！\n大切な日を家族みたいに\n一緒に過ごせてうれしいな！\nぷうきちも喜んでるよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087001, /*unit_id*/108701, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/3.6, /*change_face*/1, /*description*/"年末年始はみんな忙しそう！\n騎士くん。あたしの手なら\nいくらでも貸しちゃうよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087002, /*unit_id*/108701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"動きやすい着物だから\n力仕事もオッケーだよ！\nねぇねぇ、なにすればいい？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087003, /*unit_id*/108701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"お正月の空気って好きだなー\n新しい年を迎えるのって\n気分がピーンってなるよね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087004, /*unit_id*/108701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.4, /*change_face*/5, /*description*/"騎士くんだー！\nおーい、ここだよー！\n騎士くんなら、どこにいても\nすぐに分かるよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087005, /*unit_id*/108701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.8, /*change_face*/5, /*description*/"初詣のおみくじ？　大吉だよ！\nいいことばっかり書いてたなー\n恋愛運！？　…内緒っ！　", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087006, /*unit_id*/108731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"次はあっちの屋台に行こうよ！\n持ちきれない分は早く食べちゃお！\n騎士くんもほら、食べて食べて！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087007, /*unit_id*/108731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ユイちゃんのおせちに\n屋台の食べ物…！\n最高のお正月だよね！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087008, /*unit_id*/108731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大晦日にたくさん頑張ったし\n今日くらいは、もうちょっと\n食べちゃってもいいよね？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087009, /*unit_id*/108731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あっ…そのお菓子\nあたしが口付けちゃった…\n気にしない？　そ、そっか！\nならオッケー！　うん！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087010, /*unit_id*/108731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士くん…手、つないでもいい？\nあっ、これ食べちゃわないとだね！\nはい騎士くん！　あーんっ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087011, /*unit_id*/108701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/4.4, /*change_face*/1, /*description*/"騎士くん、誕生日おめでとー！\n騎士くんにとって\n新しい一年のはじまりだね！\n一緒にお祝いしよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1087012, /*unit_id*/108731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n騎士くんがこれからの一年間も\n幸せでありますように！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088001, /*unit_id*/108801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.7, /*change_face*/1, /*description*/"あけましておめでとう！\n騎士クン。今年もよろしくね\nおみくじはもう引いた？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088002, /*unit_id*/108801, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/5.1, /*change_face*/1, /*description*/"お正月でも気を抜かずに\n魔法の修行をしないと…\n今年はもっと上達したいな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088003, /*unit_id*/108801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/"騎士クン、寒くない？\n防寒の魔法が必要なら\nいつでも言ってね", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088004, /*unit_id*/108801, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/7.4, /*change_face*/8, /*description*/"いいな…恋人同士でお参り\nわたしもいつか…\nな、なんでもないよ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088005, /*unit_id*/108801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.8, /*change_face*/5, /*description*/"今年の恋愛運は…\n「相手の胃袋を掴むべし」…\n…騎士クン、お腹空いてない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088006, /*unit_id*/108831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、去年はありがとう！\n今年もよろしくね！\n来年も…その先も！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088007, /*unit_id*/108831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…こっちに来て欲しいな\nか、風が強くて寒いから！\n…そばにいてくれる？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088008, /*unit_id*/108831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"初日の出、キレイだね…\n…騎士クン？　どうしたの？\n太陽、まぶしくない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088009, /*unit_id*/108831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あのね？　今日みたいに\n毎年騎士クンと…\n…太陽が見たいの！\n…なに言ってるのわたし～！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088010, /*unit_id*/108831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン。わたしね…\n騎士クンといるどんな時間も\n幸せに思ってるから…！\n…今、言えた…！？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088011, /*unit_id*/108801, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/11.3, /*change_face*/2, /*description*/"あ、あけましておめでとう！\nああっ、間違えちゃった…\nえっと…その…\nお誕生日おめでとう、騎士クン！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1088012, /*unit_id*/108831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士クン、お誕生日おめでとう！\nこれからも騎士クンが\n幸せな日々を過ごせますように…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089001, /*unit_id*/108901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"着物を着ると、いつもより\n歩き方や所作に注意して\n動くようになるんだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089002, /*unit_id*/108901, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/7.1, /*change_face*/2, /*description*/"正月といえど、羽目を外して\n暴れる輩は、この刀で成敗！\n…冗談だよ。本気にした？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089003, /*unit_id*/108901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.9, /*change_face*/2, /*description*/"キミは正月をどう過ごすの？\n参考にしたいから、良ければ\n教えてくれない？", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089004, /*unit_id*/108901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.8, /*change_face*/5, /*description*/"いつもと雰囲気が違う？\nまぁ、着物を着ているし、\nキミの隣にいるから…かな", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089005, /*unit_id*/108901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"おみくじを引いたら\n恋愛運が良かったんだけど…\n…さっそく運が向いたみたいだ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089006, /*unit_id*/108931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"新年早々なんて邪な…！\n人の幸せを邪魔する者は\n私が全力で斬る！　いくぞ！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089007, /*unit_id*/108931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"やっぱり今年も、私にはキミが\n必要だ。…お願い。これからも、\nキミの力を貸して欲しい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089008, /*unit_id*/108931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"冷気を帯びたこの剣技…\nキミのおかげで、更に磨きが\nかかったよ。ありがとう", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089009, /*unit_id*/108931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…私は、自分の進む道は\n自らの手で切り開きたいんだ\nそしてその道は、大切な仲間と\nキミと一緒に歩んでいきたい", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089010, /*unit_id*/108931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…煩悩は去年に置いてきたはず\n気にしないで！　なんでもない！\nなんでもない、はずだから…", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1089011, /*unit_id*/108901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.1, /*change_face*/2, /*description*/"誕生日おめでとう！\nキミと一緒に過ごす日々は\nこれからも私にとって\n大切な宝物だよ", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
