INSERT INTO `unit_comments` VALUES (/*id*/1094006, /*unit_id*/109431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"皆を護るためなら、\nいくらでも力が出せるよ…！\n竜の炎を見せてあげる！", /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
